import React, { useState, useEffect } from 'react';
import Header from './header';
import Navbar from './navbar';
import Footer from './footer';
import './feedback.css';

const Feedback = () => {
  const [activeTab, setActiveTab] = useState('feedback');
  const [rating, setRating] = useState(0);
  const [feedbackText, setFeedbackText] = useState('');

  // Sample recent reviews data with approval status
  const [allReviews, setAllReviews] = useState([
    {
      id: 1,
      name: 'Sophia Bennett',
      time: '2 weeks ago',
      rating: 5,
      text: 'The platform is incredibly useful for finding commercial properties. The search filters are very effective, and I\'ve found several promising leads.',
      likes: 12,
      dislikes: 2,
      image: 'https://images.unsplash.com/photo-1494790108755-2616b612b786?w=40&h=40&fit=crop&crop=face',
      status: 'approved', // approved, pending, rejected
      approvedBy: 'Admin User',
      approvedAt: '2 weeks ago'
    },
    {
      id: 2,
      name: 'Ethan Chen',
      time: '1 month ago',
      rating: 4,
      text: 'Overall, a good experience. The property listings are comprehensive, but the interface could be a bit more intuitive.',
      likes: 8,
      dislikes: 1,
      image: 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=40&h=40&fit=crop&crop=face',
      status: 'approved',
      approvedBy: 'Admin User',
      approvedAt: '1 month ago'
    },
    {
      id: 3,
      name: 'Olivia Zhang',
      time: '2 months ago',
      rating: 3,
      text: 'The platform is okay, but I\'ve encountered some outdated listings. It would be great if the information was more consistently updated.',
      likes: 5,
      dislikes: 3,
      image: 'https://images.unsplash.com/photo-1438761681033-6461ffad8d80?w=40&h=40&fit=crop&crop=face',
      status: 'approved',
      approvedBy: 'Admin User',
      approvedAt: '2 months ago'
    },
    {
      id: 4,
      name: 'Michael Johnson',
      time: '3 months ago',
      rating: 5,
      text: 'Excellent platform for commercial property search. The AI-powered recommendations are spot on!',
      likes: 15,
      dislikes: 0,
      image: 'https://images.unsplash.com/photo-1500648767791-00dcc994a43e?w=40&h=40&fit=crop&crop=face',
      status: 'pending', // This review is pending admin approval
      approvedBy: null,
      approvedAt: null
    },
    {
      id: 5,
      name: 'Sarah Wilson',
      time: '4 months ago',
      rating: 4,
      text: 'Great user interface and comprehensive property database. Highly recommend!',
      likes: 9,
      dislikes: 1,
      image: 'https://images.unsplash.com/photo-1534528741775-53994a69daeb?w=40&h=40&fit=crop&crop=face',
      status: 'rejected', // This review was rejected by admin
      approvedBy: null,
      approvedAt: null,
      rejectionReason: 'Inappropriate content'
    }
  ]);

  // Filter to show only approved reviews in recent reviews section
  const [recentReviews, setRecentReviews] = useState([]);

  // Filter approved reviews whenever allReviews changes
  useEffect(() => {
    const approvedReviews = allReviews.filter(review => review.status === 'approved');
    setRecentReviews(approvedReviews);
  }, [allReviews]);

  // Track user interactions with reviews
  const [userInteractions, setUserInteractions] = useState(() => {
    // Load saved interactions from localStorage
    const saved = localStorage.getItem('feedbackUserInteractions');
    return saved ? JSON.parse(saved) : {};
  });

  // Track user interactions with admin replies
  const [adminReplyInteractions, setAdminReplyInteractions] = useState(() => {
    // Load saved admin reply interactions from localStorage
    const saved = localStorage.getItem('feedbackAdminReplyInteractions');
    return saved ? JSON.parse(saved) : {};
  });

  // Save user interactions to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('feedbackUserInteractions', JSON.stringify(userInteractions));
  }, [userInteractions]);

  // Save admin reply interactions to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('feedbackAdminReplyInteractions', JSON.stringify(adminReplyInteractions));
  }, [adminReplyInteractions]);

  // Helper function to format time
  const formatTime = (timestamp) => {
    const now = Date.now();
    const diff = now - timestamp;
    const minutes = Math.floor(diff / 60000);
    const hours = Math.floor(diff / 3600000);
    const days = Math.floor(diff / 86400000);
    
    if (minutes < 1) return 'Just now';
    if (minutes < 60) return `${minutes} minute${minutes > 1 ? 's' : ''} ago`;
    if (hours < 24) return `${hours} hour${hours > 1 ? 's' : ''} ago`;
    if (days < 7) return `${days} day${days > 1 ? 's' : ''} ago`;
    return new Date(timestamp).toLocaleDateString();
  };

  // Sample your reviews data with state management
  const [yourReviews, setYourReviews] = useState([
    {
      id: 1,
      time: '3 months ago',
      rating: 4,
      text: 'I appreciate the detailed property reports available on the platform. They provide valuable insights that help in making informed decisions.',
      likes: 7,
      dislikes: 0,
      hasAdminReply: true,
      adminReply: {
        text: 'Thank you for your feedback! We\'re glad you found the property reports helpful. We are continuously working to improve the depth and accuracy of our data.',
        time: '3 months ago',
        image: 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=40&h=40&fit=crop&crop=face',
        likes: 3,
        dislikes: 0
      }
    },
    {
      id: 2,
      time: '6 months ago',
      rating: 5,
      text: 'The customer support team was very responsive and helpful when I had questions about a specific property. Great service!',
      likes: 10,
      dislikes: 1,
      hasAdminReply: true,
      adminReply: {
        text: 'We\'re thrilled to hear about your positive experience with our customer support. We strive to provide prompt and effective assistance to all our users.',
        time: '6 months ago',
        image: 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=40&h=40&fit=crop&crop=face',
        likes: 5,
        dislikes: 1
      }
    }
  ]);



  const handleRatingClick = (selectedRating) => {
    setRating(selectedRating);
  };

  const handleSubmitFeedback = () => {
    if (rating === 0) {
      alert('Please select a rating before submitting feedback.');
      return;
    }
    if (feedbackText.trim() === '') {
      alert('Please enter your feedback before submitting.');
      return;
    }
    
    // Create new review object
    const newReview = {
      id: Date.now(), // Use timestamp as unique ID
      time: 'Just now',
      rating: rating,
      text: feedbackText,
      likes: 0,
      dislikes: 0,
      hasAdminReply: false
    };
    
    // Add new review to yourReviews
    setYourReviews(prevReviews => [newReview, ...prevReviews]);
    
    // Here you would typically send the feedback to the backend
    console.log('Submitting feedback:', { rating, feedbackText });
    alert('Thank you for your feedback! Your review has been added to "Your Reviews".');
    
    // Reset form
    setRating(0);
    setFeedbackText('');
  };

  // Handle like button click for recent reviews
  const handleLikeClick = (reviewId) => {
    setRecentReviews(prevReviews => 
      prevReviews.map(review => {
        if (review.id === reviewId) {
          const currentInteraction = userInteractions[reviewId];
          
          // If user already liked this review, remove the like
          if (currentInteraction === 'like') {
            setUserInteractions(prev => ({ ...prev, [reviewId]: null }));
            return { ...review, likes: review.likes - 1 };
          }
          
          // If user already disliked this review, switch to like
          if (currentInteraction === 'dislike') {
            setUserInteractions(prev => ({ ...prev, [reviewId]: 'like' }));
            return { ...review, likes: review.likes + 1, dislikes: review.dislikes - 1 };
          }
          
          // If user hasn't interacted, add like
          setUserInteractions(prev => ({ ...prev, [reviewId]: 'like' }));
          return { ...review, likes: review.likes + 1 };
        }
        return review;
      })
    );
  };

  // Handle dislike button click for recent reviews
  const handleDislikeClick = (reviewId) => {
    setRecentReviews(prevReviews => 
      prevReviews.map(review => {
        if (review.id === reviewId) {
          const currentInteraction = userInteractions[reviewId];
          
          // If user already disliked this review, remove the dislike
          if (currentInteraction === 'dislike') {
            setUserInteractions(prev => ({ ...prev, [reviewId]: null }));
            return { ...review, dislikes: review.dislikes - 1 };
          }
          
          // If user already liked this review, switch to dislike
          if (currentInteraction === 'like') {
            setUserInteractions(prev => ({ ...prev, [reviewId]: 'dislike' }));
            return { ...review, likes: review.likes - 1, dislikes: review.dislikes + 1 };
          }
          
          // If user hasn't interacted, add dislike
          setUserInteractions(prev => ({ ...prev, [reviewId]: 'dislike' }));
          return { ...review, dislikes: review.dislikes + 1 };
        }
        return review;
      })
    );
  };

  // Handle like button click for your reviews
  const handleYourReviewLikeClick = (reviewId) => {
    setYourReviews(prevReviews => 
      prevReviews.map(review => {
        if (review.id === reviewId) {
          const currentInteraction = userInteractions[reviewId];
          
          // If user already liked this review, remove the like
          if (currentInteraction === 'like') {
            setUserInteractions(prev => ({ ...prev, [reviewId]: null }));
            return { ...review, likes: review.likes - 1 };
          }
          
          // If user already disliked this review, switch to like
          if (currentInteraction === 'dislike') {
            setUserInteractions(prev => ({ ...prev, [reviewId]: 'like' }));
            return { ...review, likes: review.likes + 1, dislikes: review.dislikes - 1 };
          }
          
          // If user hasn't interacted, add like
          setUserInteractions(prev => ({ ...prev, [reviewId]: 'like' }));
            return { ...review, likes: review.likes + 1 };
        }
        return review;
      })
    );
  };

    // Handle dislike button click for your reviews
  const handleYourReviewDislikeClick = (reviewId) => {
    setYourReviews(prevReviews => 
      prevReviews.map(review => {
        if (review.id === reviewId) {
          const currentInteraction = userInteractions[reviewId];
          
          // If user already disliked this review, remove the dislike
          if (currentInteraction === 'dislike') {
            setUserInteractions(prev => ({ ...prev, [reviewId]: null }));
            return { ...review, dislikes: review.dislikes - 1 };
          }
          
          // If user already liked this review, switch to dislike
          if (currentInteraction === 'like') {
            setUserInteractions(prev => ({ ...prev, [reviewId]: 'dislike' }));
            return { ...review, likes: review.likes - 1, dislikes: review.dislikes + 1 };
          }
          
          // If user hasn't interacted, add dislike
          setUserInteractions(prev => ({ ...prev, [reviewId]: 'dislike' }));
          return { ...review, dislikes: review.dislikes + 1 };
        }
        return review;
      })
    );
  };

  // Handle like button click for admin replies
  const handleAdminReplyLikeClick = (reviewId) => {
    setYourReviews(prevReviews => 
      prevReviews.map(review => {
        if (review.id === reviewId && review.hasAdminReply) {
          const currentInteraction = adminReplyInteractions[reviewId];
          
          // If user already liked this admin reply, remove the like
          if (currentInteraction === 'like') {
            setAdminReplyInteractions(prev => ({ ...prev, [reviewId]: null }));
            return { 
              ...review, 
              adminReply: { 
                ...review.adminReply, 
                likes: (review.adminReply.likes || 0) - 1 
              } 
            };
          }
          
          // If user already disliked this admin reply, switch to like
          if (currentInteraction === 'dislike') {
            setAdminReplyInteractions(prev => ({ ...prev, [reviewId]: 'like' }));
            return { 
              ...review, 
              adminReply: { 
                ...review.adminReply, 
                likes: (review.adminReply.likes || 0) + 1,
                dislikes: (review.adminReply.dislikes || 0) - 1
              } 
            };
          }
          
          // If user hasn't interacted, add like
          setAdminReplyInteractions(prev => ({ ...prev, [reviewId]: 'like' }));
          return { 
            ...review, 
            adminReply: { 
              ...review.adminReply, 
              likes: (review.adminReply.likes || 0) + 1 
            } 
          };
        }
        return review;
      })
    );
  };

  // Handle dislike button click for admin replies
  const handleAdminReplyDislikeClick = (reviewId) => {
    setYourReviews(prevReviews => 
      prevReviews.map(review => {
        if (review.id === reviewId && review.hasAdminReply) {
          const currentInteraction = adminReplyInteractions[reviewId];
          
          // If user already disliked this admin reply, remove the dislike
          if (currentInteraction === 'dislike') {
            setAdminReplyInteractions(prev => ({ ...prev, [reviewId]: null }));
            return { 
              ...review, 
              adminReply: { 
                ...review.adminReply, 
                dislikes: (review.adminReply.dislikes || 0) - 1 
              } 
            };
          }
          
          // If user already liked this admin reply, switch to dislike
          if (currentInteraction === 'like') {
            setAdminReplyInteractions(prev => ({ ...prev, [reviewId]: 'dislike' }));
            return { 
              ...review, 
              adminReply: { 
                ...review.adminReply, 
                likes: (review.adminReply.likes || 0) - 1,
                dislikes: (review.adminReply.dislikes || 0) + 1
              } 
            };
          }
          
          // If user hasn't interacted, add dislike
          setAdminReplyInteractions(prev => ({ ...prev, [reviewId]: 'dislike' }));
          return { 
            ...review, 
            adminReply: { 
              ...review.adminReply, 
              dislikes: (review.adminReply.dislikes || 0) + 1 
            } 
          };
        }
        return review;
      })
    );
  };

  const renderStars = (rating, interactive = false) => {
    return (
      <div className="feedback-stars">
        {[1, 2, 3, 4, 5].map((star) => (
          <button
            key={star}
            className={`feedback-star-btn ${star <= rating ? 'filled' : ''} ${interactive ? 'interactive' : ''}`}
            onClick={interactive ? () => handleRatingClick(star) : undefined}
            disabled={!interactive}
          >
            
          </button>
        ))}
      </div>
    );
  };



  return (
    <div className="user-dashboard">
      {/* Top Header */}
      <Header />

      <div className="user-dashboard-container">
        {/* Left Sidebar */}
        <Navbar activeTab={activeTab} setActiveTab={setActiveTab} />

        {/* Main Content */}
        <main className="user-main-content">
          {/* Feedback Section */}
          <section className="feedback-section">
            <h1 className="feedback-section-title">Feedback</h1>
            <h2 className="feedback-subtitle">Rate Your Experience</h2>
            
            <div className="feedback-rating-section">
              {renderStars(rating, true)}
            </div>
            
            <div className="feedback-form">
              <textarea
                className="feedback-textarea"
                placeholder="Share your thoughts, suggestions, or issues here... (Max. 500 characters)"
                value={feedbackText}
                onChange={(e) => setFeedbackText(e.target.value)}
                maxLength={500}
                rows={4}
              />
              <div className="feedback-char-count">{feedbackText.length}/500</div>
              
              <button 
                className="feedback-submit-btn"
                onClick={handleSubmitFeedback}
              >
                Submit Feedback
              </button>
            </div>
          </section>

          {/* Recent Reviews Section - Only Approved Reviews */}
          <section className="feedback-reviews-section">
            <h2 className="feedback-section-title">Recent Reviews</h2>
            <p className="feedback-section-subtitle">Only approved and published reviews are shown here</p>
            <div className="feedback-reviews-list">
              {recentReviews.length > 0 ? (
                recentReviews.map((review) => (
                  <div key={review.id} className="feedback-review-card approved-review">
                    <div className="feedback-review-header">
                      <img src={review.image} alt={review.name} className="feedback-reviewer-image" />
                      <div className="feedback-reviewer-info">
                        <h3 className="feedback-reviewer-name">{review.name}</h3>
                        <span className="feedback-review-time">{review.time}</span>
                        <span className="feedback-approval-badge"> Approved by {review.approvedBy}</span>
                      </div>
                    </div>
                    <div className="feedback-review-rating">
                      {renderStars(review.rating)}
                    </div>
                    <p className="feedback-review-text">{review.text}</p>
                    <div className="feedback-review-engagement">
                      <button 
                        className={`feedback-engagement-btn ${userInteractions[review.id] === 'like' ? 'active' : ''}`}
                        onClick={() => handleLikeClick(review.id)}
                        title={userInteractions[review.id] === 'like' ? 'Remove like' : 'Like this review'}
                      >
                        <span className="feedback-engagement-icon"></span>
                        <span className="feedback-engagement-count">{review.likes}</span>
                      </button>
                      <button 
                        className={`feedback-engagement-btn ${userInteractions[review.id] === 'dislike' ? 'active' : ''}`}
                        onClick={() => handleDislikeClick(review.id)}
                        title={userInteractions[review.id] === 'dislike' ? 'Remove dislike' : 'Dislike this review'}
                      >
                        <span className="feedback-engagement-icon"></span>
                        <span className="feedback-engagement-count">{review.dislikes}</span>
                      </button>
                    </div>
                  </div>
                ))
              ) : (
                <div className="feedback-no-reviews">
                  <p>No approved reviews available yet.</p>
                </div>
              )}
            </div>
          </section>

          {/* Pending Reviews Section - For Admin Users */}
          <section className="feedback-pending-section">
            <h2 className="feedback-section-title">Pending Reviews</h2>
            <p className="feedback-section-subtitle">Reviews awaiting admin approval</p>
            <div className="feedback-reviews-list">
              {allReviews.filter(review => review.status === 'pending').map((review) => (
                <div key={review.id} className="feedback-review-card pending-review">
                  <div className="feedback-review-header">
                    <img src={review.image} alt={review.name} className="feedback-reviewer-image" />
                    <div className="feedback-reviewer-info">
                      <h3 className="feedback-reviewer-name">{review.name}</h3>
                      <span className="feedback-review-time">{review.time}</span>
                      <span className="feedback-status-badge pending"> Pending Approval</span>
                    </div>
                  </div>
                  <div className="feedback-review-rating">
                    {renderStars(review.rating)}
                  </div>
                  <p className="feedback-review-text">{review.text}</p>
                  <div className="feedback-admin-actions">
                    <button 
                      className="feedback-approve-btn"
                      onClick={() => handleApproveReview(review.id)}
                      title="Approve this review"
                    >
                       Approve
                    </button>
                    <button 
                      className="feedback-reject-btn"
                      onClick={() => handleRejectReview(review.id)}
                      title="Reject this review"
                    >
                       Reject
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </section>

          {/* Your Reviews Section */}
          <section className="feedback-your-reviews-section">
            <h2 className="feedback-section-title">Your Reviews</h2>
            <div className="feedback-reviews-list">
              {yourReviews.map((review) => (
                <div key={review.id} className="feedback-review-card your-review">
                  <div className="feedback-review-header">
                    <div className="feedback-reviewer-image you">You</div>
                    <div className="feedback-reviewer-info">
                      <h3 className="feedback-reviewer-name">You</h3>
                      <span className="feedback-review-time">{review.time}</span>
                    </div>
                  </div>
                  <div className="feedback-review-rating">
                    {renderStars(review.rating)}
                  </div>
                  <p className="feedback-review-text">{review.text}</p>
                                     <div className="feedback-review-engagement">
                     <button 
                       className={`feedback-engagement-btn ${userInteractions[review.id] === 'like' ? 'active' : ''}`}
                       onClick={() => handleYourReviewLikeClick(review.id)}
                       title={userInteractions[review.id] === 'like' ? 'Remove like' : 'Like this review'}
                     >
                       <span className="feedback-engagement-icon"></span>
                       <span className="feedback-engagement-count">{review.likes}</span>
                     </button>
                     <button 
                       className={`feedback-engagement-btn ${userInteractions[review.id] === 'dislike' ? 'active' : ''}`}
                       onClick={() => handleYourReviewDislikeClick(review.id)}
                       title={userInteractions[review.id] === 'dislike' ? 'Remove dislike' : 'Dislike this review'}
                     >
                       <span className="feedback-engagement-icon"></span>
                       <span className="feedback-engagement-count">{review.dislikes}</span>
                     </button>
                   </div>
                  
                                     {review.hasAdminReply && (
                     <div className="feedback-admin-reply">
                       <div className="feedback-review-header">
                         <img src={review.adminReply.image} alt="Admin" className="feedback-reviewer-image" />
                         <div className="feedback-reviewer-info">
                           <h3 className="feedback-reviewer-name">Admin</h3>
                           <span className="feedback-review-time">{review.adminReply.time}</span>
                         </div>
                       </div>
                       <p className="feedback-review-text">{review.adminReply.text}</p>
                       <div className="feedback-review-engagement">
                         <button 
                           className={`feedback-engagement-btn ${adminReplyInteractions[review.id] === 'like' ? 'active' : ''}`}
                           onClick={() => handleAdminReplyLikeClick(review.id)}
                           title={adminReplyInteractions[review.id] === 'like' ? 'Remove like' : 'Like this admin reply'}
                         >
                           <span className="feedback-engagement-icon"></span>
                           <span className="feedback-engagement-count">{review.adminReply.likes || 0}</span>
                         </button>
                         <button 
                           className={`feedback-engagement-btn ${adminReplyInteractions[review.id] === 'dislike' ? 'active' : ''}`}
                           onClick={() => handleAdminReplyDislikeClick(review.id)}
                           title={adminReplyInteractions[review.id] === 'dislike' ? 'Remove dislike' : 'Dislike this admin reply'}
                         >
                           <span className="feedback-engagement-icon"></span>
                           <span className="feedback-engagement-count">{review.adminReply.dislikes || 0}</span>
                         </button>
                       </div>
                     </div>
                   )}
                </div>
              ))}
            </div>
          </section>
        </main>
      </div>

      {/* Footer */}
      <Footer />
    </div>
  );
};

export default Feedback;
