import React, { useState } from 'react';
import './HowItWorks.css';
import Navbar from './Navbar';
import Footer from './Footer';

const HowItWorks = () => {
  const [selectedCard, setSelectedCard] = useState(null);

  const cards = [
    {
      id: 1,
      title: 'Marina Bay Financial Centre',
      address: '10 Marina Boulevard, Singapore',
      type: 'Commercial',
      floors: 50,
      sqft: 3000000,
      image: "http://localhost:3845/assets/18a9ad6ae180db6222a9dcede882f38c4f7381ca.png"
    },
    {
      id: 2,
      title: 'Jurong Industrial Estate',
      address: 'Jurong Island, Singapore',
      type: 'Industrial',
      floors: 8,
      sqft: 500000,
      image: "http://localhost:3845/assets/06856c49ec8fb21c90f1c5314db6486439c2d337.png"
    },
    {
      id: 3,
      title: 'Orchard Gateway',
      address: '277 Orchard Road, Singapore',
      type: 'Commercial',
      floors: 20,
      sqft: 800000,
      image: "http://localhost:3845/assets/a5567c835d14932dd61d89cbe49bba188ddf51a4.png"
    },
    {
      id: 4,
      title: 'Tuas Industrial Park',
      address: 'Tuas South, Singapore',
      type: 'Industrial',
      floors: 12,
      sqft: 1200000,
      image: "http://localhost:3845/assets/e0b84b558653fc6e03589419db65a3819146775f.png"
    }
  ];

  return (
    <div className="howitworks-page">
      <Navbar />

      <section className="how-hero">
        <h1 className="section-title" style={{ color: '#fff', marginTop: '4rem' }}>See How it Works</h1>
      </section>

      {/* Card Grid */}
      <section className="how-grid">
        {cards.map(card => (
          <article className="how-card" key={card.id}>
            <div className="how-card-image" style={{ backgroundImage: `url('${card.image}')` }} />
            <div className="how-card-body">
              <h3>{card.title}</h3>
              <p className="muted">{card.address}</p>
              <div className="how-meta">
                <span>{card.beds} Beds</span>
                <span>{card.baths} Baths</span>
                <span>{card.sqft} sqft</span>
              </div>
              <button
                className="btn btn-secondary"
                style={{ width: 164 }}
                onClick={() => setSelectedCard(card.id)}
              >
                Check
              </button>
            </div>
          </article>
        ))}
      </section>

      {/* Detail Container Underneath */}
      {selectedCard && (
        <section className="how-details">
          {(() => {
            const card = cards.find(c => c.id === selectedCard);
            return (
              <div className="details-card">
                <img src={card.image} alt={card.title} className="details-img" />
                <div className="details-info">
                  <h2>{card.title}</h2>
                  <p>{card.address}</p>
                  <p><strong>{card.beds}</strong> Beds</p>
                  <p><strong>{card.baths}</strong> Baths</p>
                  <p><strong>{card.sqft}</strong> sqft</p>
                </div>
              </div>
            );
          })()}
        </section>
      )}

      <Footer />
    </div>
  );
};

export default HowItWorks;
