import React, { useEffect, useState, useRef } from 'react';
import './Explore.css';
import { GOOGLE_MAPS_API_KEY } from '../../config/maps';

const GOOGLE_MAPS_LIBRARIES = ['places', 'geometry'];

function loadGoogleMaps(apiKey) {
  if (!apiKey) {
    return Promise.reject(new Error('Missing Google Maps API key (set in src/config/maps.js or .env)'));
  }
  if (window.google && window.google.maps) {
    return Promise.resolve(window.google);
  }
  if (window._googleMapsPromise) {
    return window._googleMapsPromise;
  }
  const script = document.createElement('script');
  const libs = GOOGLE_MAPS_LIBRARIES.join(',');
  script.src = `https://maps.googleapis.com/maps/api/js?key=${apiKey}&libraries=${libs}`;
  script.async = true;
  script.defer = true;
  window._googleMapsPromise = new Promise((resolve, reject) => {
    script.onload = () => resolve(window.google);
    script.onerror = reject;
  });
  document.head.appendChild(script);
  return window._googleMapsPromise;
}

const DEFAULT_CENTER = { lat: 1.2868108, lng: 103.8545349 }; // SG center-ish
const SEARCH_RADIUS_METERS = 1000;

const AMENITY_OPTIONS = [
  { type: 'school', label: 'Schools' },
  { type: 'hospital', label: 'Hospitals' },
  { type: 'supermarket', label: 'Supermarkets' },
  { type: 'restaurant', label: 'Restaurants' },
  { type: 'cafe', label: 'Cafes' },
  { type: 'bank', label: 'Banks' },
  { type: 'atm', label: 'ATMs' },
  { type: 'shopping_mall', label: 'Shopping Malls' },
  { type: 'bus_station', label: 'Bus Stations' },
  { type: 'subway_station', label: 'MRT' },
  { type: 'gym', label: 'Gyms' },
  { type: 'pharmacy', label: 'Pharmacies' },
];

const Explore = () => {
  const [searchAddress, setSearchAddress] = useState('');
  const [clickedAddress, setClickedAddress] = useState('');
  const [isSearching, setIsSearching] = useState(false);
  const [searchError, setSearchError] = useState('');
  const [propertyType, setPropertyType] = useState('residential');
  const [propertyStage, setPropertyStage] = useState('all');

  const [selectedAmenityTypes, setSelectedAmenityTypes] = useState(
    ['school', 'supermarket', 'restaurant', 'bus_station', 'subway_station']
  );
  const [amenities, setAmenities] = useState([]);
  const [isLoadingAmenities, setIsLoadingAmenities] = useState(false);
  const [amenitiesError, setAmenitiesError] = useState('');

  const markerRef = useRef(null);
  const mapInstanceRef = useRef(null);
  const geocoderRef = useRef(null);
  const placesServiceRef = useRef(null);
  const autocompleteRef = useRef(null);
  const inputRef = useRef(null);
  const lastLocationRef = useRef(null);

  // Initialize Google Maps
  useEffect(() => {
    loadGoogleMaps(GOOGLE_MAPS_API_KEY)
      .then((google) => {
    const mapDiv = document.getElementById('mapdiv');
    if (!mapDiv) {
      console.error('Map div not found');
      return;
    }
        const map = new google.maps.Map(mapDiv, {
          center: DEFAULT_CENTER,
          zoom: 16,
          mapTypeControl: false,
          streetViewControl: false,
          fullscreenControl: true,
        });
        mapInstanceRef.current = map;
        geocoderRef.current = new google.maps.Geocoder();
        placesServiceRef.current = new google.maps.places.PlacesService(map);

        // Map click -> reverse geocode + marker + amenities
        map.addListener('click', (e) => {
          const latLng = e.latLng;
          placeMarker(latLng);
          reverseGeocode(latLng);
          fetchAmenities(latLng, selectedAmenityTypes);
        });

        // Autocomplete on input
        if (inputRef.current) {
          const autocomplete = new google.maps.places.Autocomplete(inputRef.current, {
            componentRestrictions: { country: 'sg' },
            types: ['address', 'establishment', 'geocode']
          });
          autocompleteRef.current = autocomplete;

          autocomplete.addListener('place_changed', () => {
            const place = autocomplete.getPlace();
            if (place.geometry) {
              const latLng = place.geometry.location;
              placeMarker(latLng);
              reverseGeocode(latLng);
              fetchAmenities(latLng, selectedAmenityTypes);
              map.setCenter(latLng);
              map.setZoom(16);
            }
          });
        }
      })
      .catch((error) => {
        console.error('Error loading Google Maps:', error);
      });
  }, []);

  const placeMarker = (latLng) => {
    if (markerRef.current) {
      markerRef.current.setMap(null);
    }

    const marker = new window.google.maps.Marker({
      position: latLng,
      map: mapInstanceRef.current,
      animation: window.google.maps.Animation.DROP,
    });
    markerRef.current = marker;
    lastLocationRef.current = latLng;
  };

  const reverseGeocode = (latLng) => {
    geocoderRef.current.geocode({ location: latLng }, (results, status) => {
      if (status === 'OK' && results[0]) {
        setClickedAddress(results[0].formatted_address);
      }
    });
  };

  const fetchAmenities = (latLng, types) => {
    setIsLoadingAmenities(true);
    setAmenitiesError('');

    const promises = types.map(type => {
      return new Promise((resolve) => {
        const request = {
          location: latLng,
          radius: SEARCH_RADIUS_METERS,
          type: type
        };

        placesServiceRef.current.nearbySearch(request, (results, status) => {
          if (status === window.google.maps.places.PlacesServiceStatus.OK) {
            resolve(results.slice(0, 5).map(place => ({
              ...place,
              amenityType: type
            })));
          } else {
            resolve([]);
          }
        });
      });
    });

    Promise.all(promises)
      .then((results) => {
        const allAmenities = results.flat();
        setAmenities(allAmenities);
        setIsLoadingAmenities(false);
      })
      .catch((error) => {
        console.error('Error fetching amenities:', error);
        setAmenitiesError('Failed to load nearby amenities');
        setIsLoadingAmenities(false);
      });
  };

  const handleSearch = async () => {
    if (!searchAddress.trim()) {
      setSearchError('Please enter an address');
      return;
    }

    setIsSearching(true);
    setSearchError('');

    try {
      const results = await new Promise((resolve, reject) => {
        geocoderRef.current.geocode({ address: searchAddress }, (results, status) => {
          if (status === 'OK') {
            resolve(results);
          } else {
            reject(new Error('Geocoding failed'));
          }
        });
      });

      if (results && results[0]) {
        const latLng = results[0].geometry.location;
        placeMarker(latLng);
        reverseGeocode(latLng);
        fetchAmenities(latLng, selectedAmenityTypes);
        mapInstanceRef.current.setCenter(latLng);
        mapInstanceRef.current.setZoom(16);
      }
    } catch (error) {
      setSearchError('Address not found. Please try a different address.');
    } finally {
      setIsSearching(false);
    }
  };

  const handleClearAll = () => {
    setSearchAddress('');
    setClickedAddress('');
    setSearchError('');
    setAmenities([]);
    if (markerRef.current) {
      markerRef.current.setMap(null);
      markerRef.current = null;
    }
    mapInstanceRef.current.setCenter(DEFAULT_CENTER);
    mapInstanceRef.current.setZoom(16);
  };

  const handlePropertyTypeChange = (type) => {
    setPropertyType(type);
  };

  return (
    <section id="explore" className="explore-section">
      <div className="explore-container">
        <div className="explore-header">
          <h2 className="explore-title">Explore Uninterrupted Insights</h2>
          <p className="explore-subtitle">Find your next property with ease.</p>
        </div>

        {/* Property Type Filters */}
        <div className="property-type-filters">
          <button
            className={`property-type-btn ${propertyType === 'residential' ? 'active' : ''}`}
            onClick={() => handlePropertyTypeChange('residential')}
          >
            Residential
          </button>
          <button
            className={`property-type-btn ${propertyType === 'commercial' ? 'active' : ''}`}
            onClick={() => handlePropertyTypeChange('commercial')}
          >
            Commercial
          </button>
        </div>

        {/* Search Interface Card */}
        <div className="search-interface-card">
          {/* Left Panel - Search Form */}
          <div className="search-form-panel">
            <div className="search-input-group">
              <input
                ref={inputRef}
                type="text"
                value={searchAddress}
                onChange={(e) => setSearchAddress(e.target.value)}
                placeholder="Enter Address, City, or Zip Code"
                className="search-input"
              />
              <div className="search-icon">
                <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
                  <path d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                </svg>
              </div>
            </div>

            <div className="search-dropdowns">
              <select
                value={propertyType}
                onChange={(e) => setPropertyType(e.target.value)}
                className="search-dropdown"
              >
                <option value="residential">Residential</option>
                <option value="commercial">Commercial</option>
                <option value="industrial">Industrial</option>
              </select>

              <select
                value={propertyStage}
                onChange={(e) => setPropertyStage(e.target.value)}
                className="search-dropdown"
              >
                <option value="all">All Stages</option>
                <option value="new">New</option>
                <option value="resale">Resale</option>
                <option value="under-construction">Under Construction</option>
              </select>
            </div>

            {searchError && <div className="search-error">{searchError}</div>}

            <div className="search-actions">
              <button onClick={handleClearAll} className="btn btn-secondary">
                Clear All
              </button>
              <button 
                onClick={handleSearch} 
                className="btn btn-primary"
                disabled={isSearching}
              >
                {isSearching ? 'Searching...' : 'Search'}
              </button>
            </div>
          </div>

          {/* Right Panel - Map View */}
          <div className="map-panel">
            <div id="mapdiv" className="map-container"></div>
          </div>
        </div>

        {/* Amenities Section */}
        {amenities.length > 0 && (
          <div className="amenities-section">
            <h3 className="amenities-title">Nearby Amenities</h3>
            <div className="amenities-grid">
              {amenities.map((amenity, index) => (
                <div key={index} className="amenity-item">
                  <div className="amenity-icon">
                    <svg width="16" height="16" viewBox="0 0 24 24" fill="none">
                      <path d="M21 10c0 7-9 13-9 13s-9-6-9-13a9 9 0 0118 0z" stroke="currentColor" strokeWidth="2"/>
                      <circle cx="12" cy="10" r="3" stroke="currentColor" strokeWidth="2"/>
                    </svg>
                  </div>
                  <div className="amenity-info">
                    <div className="amenity-name">{amenity.name}</div>
                    <div className="amenity-type">{amenity.amenityType}</div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </section>
  );
};

export default Explore;
