import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import FreeUser from './free/FreeUser';
import PremiumUser from './premium/PremiumUser';
import AgentUser from './agent/AgentUser';
import SysAdmin from './system/SysAdmin';

const Dashboard = () => {
  const navigate = useNavigate();

  useEffect(() => {
    // Check if user is logged in
    const isLoggedIn = localStorage.getItem('isLoggedIn');
    if (!isLoggedIn) {
      navigate('/login');
      return;
    }

    // Check if user has completed first-time setup
    const userPreferences = localStorage.getItem('userPreferences');
    const isFirstTimeUser = !userPreferences || !JSON.parse(userPreferences).completed;
    
    if (isFirstTimeUser) {
      const userType = localStorage.getItem('userType');
      if (userType === 'free' || userType === 'premium') {
        navigate('/first-time');
        return;
      }
    }
  }, [navigate]);

  // Get user type from localStorage
  const userType = localStorage.getItem('userType') || 'free';

  // Render appropriate dashboard based on user type
  switch (userType) {
    case 'agent':
      return <AgentUser />;
    case 'premium':
      return <PremiumUser />;
    case 'sysadmin':
      return <SysAdmin />;
    case 'free':
    default:
      return <FreeUser />;
  }
};

export default Dashboard;
