import React, { useState } from 'react';
import './RecoverEmail.css';

const RecoverEmail = () => {
  const [formData, setFormData] = useState({
    email: '',
    newPassword: '',
    confirmPassword: ''
  });
  const [errors, setErrors] = useState({});
  const [isEmailVerified, setIsEmailVerified] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateEmail = () => {
    const newErrors = {};

    if (!formData.email) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return false;
    }

    return true;
  };

  const validatePassword = () => {
    const newErrors = {};

    if (!formData.newPassword) {
      newErrors.newPassword = 'New password is required';
    } else if (formData.newPassword.length < 6) {
      newErrors.newPassword = 'Password must be at least 6 characters';
    }

    if (!formData.confirmPassword) {
      newErrors.confirmPassword = 'Please confirm your password';
    } else if (formData.newPassword !== formData.confirmPassword) {
      newErrors.confirmPassword = 'Passwords do not match';
    }

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return false;
    }

    return true;
  };

  const handleEmailSubmit = (e) => {
    e.preventDefault();
    if (validateEmail()) {
      // Simulate email verification
      console.log('Sending recovery email to:', formData.email);
      setIsEmailVerified(true);
      setErrors({});
    }
  };

  const handlePasswordSubmit = (e) => {
    e.preventDefault();
    if (validatePassword()) {
      // Handle password reset
      console.log('Password reset for:', formData.email);
      console.log('New password:', formData.newPassword);
      // You can add your password reset logic here
      alert('Password has been reset successfully!');
    }
  };

  const handleBackToEmail = () => {
    setIsEmailVerified(false);
    setFormData(prev => ({
      ...prev,
      newPassword: '',
      confirmPassword: ''
    }));
    setErrors({});
  };

  return (
    <div className="recover-page">
      <div className="recover-container">
        {/* Form Panel */}
        <div className="form-panel">
          <div className="page-title-section">
            <h1 className="page-title">
              {isEmailVerified ? 'Reset Password' : 'Recover Account'}
            </h1>
            <p className="section-subtitle">
              {isEmailVerified 
                ? 'Create a new password for your account'
                : 'Enter your registered email address to recover your account'
              }
            </p>
          </div>

          {!isEmailVerified ? (
            // Email Verification Step
            <form className="recover-form" onSubmit={handleEmailSubmit}>
              <div className="input-container">
                <div className="input-icon">üìß</div>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  className={errors.email ? 'error' : ''}
                  placeholder="Enter your registered email address"
                />
              </div>
              {errors.email && <span className="error-message">{errors.email}</span>}

              <button type="submit" className="recover-btn">
                Send Recovery Email
              </button>
            </form>
          ) : (
            // Password Reset Step
            <form className="recover-form" onSubmit={handlePasswordSubmit}>
              <div className="email-display">
                <span className="email-label">Email:</span>
                <span className="email-value">{formData.email}</span>
                <button 
                  type="button" 
                  className="change-email-btn"
                  onClick={handleBackToEmail}
                >
                  Change
                </button>
              </div>

              <div className="input-container">
                <div className="input-icon">üîí</div>
                <input
                  type="password"
                  name="newPassword"
                  value={formData.newPassword}
                  onChange={handleChange}
                  className={errors.newPassword ? 'error' : ''}
                  placeholder="Enter new password"
                />
              </div>
              {errors.newPassword && <span className="error-message">{errors.newPassword}</span>}

              <div className="input-container">
                <div className="input-icon">üîí</div>
                <input
                  type="password"
                  name="confirmPassword"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                  className={errors.confirmPassword ? 'error' : ''}
                  placeholder="Confirm new password"
                />
              </div>
              {errors.confirmPassword && <span className="error-message">{errors.confirmPassword}</span>}

              <button type="submit" className="recover-btn">
                Reset Password
              </button>
            </form>
          )}

          <div className="recover-footer">
            <p>
              Remember your password?{' '}
              <a href="/login" className="login-link">Sign in</a>
            </p>
          </div>

          <div className="back-to-home">
            <a href="/" className="back-link">
              ‚Üê Back to Home
            </a>
          </div>
        </div>

        {/* Visual Panel */}
        <div className="visual-panel">
          <div className="cityscape-photo"></div>
        </div>
      </div>
    </div>
  );
};

export default RecoverEmail;
