import React, { useState, useEffect } from 'react';
import Header from './header';
import Navbar from './navbar';
import Footer from './footer';
import './profile.css';

const Profile = () => {
  const [activeTab, setActiveTab] = useState('profile');
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phoneNumber: ''
  });
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });
  const [isLoading, setIsLoading] = useState(false);
  const [showSubscriptionModal, setShowSubscriptionModal] = useState(false);
  const [currentPlan, setCurrentPlan] = useState('Premium'); // Can be 'Free', 'Basic', 'Premium'
  const [subscriptionPeriod, setSubscriptionPeriod] = useState('monthly'); // 'monthly' or 'yearly'
  const [subscriptionStartDate, setSubscriptionStartDate] = useState(new Date('2024-01-01')); // When they started premium
  const [accountCreatedDate, setAccountCreatedDate] = useState('March 22, 2022');

  // Load user data from localStorage (from signup) when component mounts
  useEffect(() => {
    const loadUserData = () => {
      try {
        // Load user profile data from localStorage (typically set during signup)
        const savedUserData = localStorage.getItem('userProfileData');
        if (savedUserData) {
          const userData = JSON.parse(savedUserData);
          setFormData({
            fullName: userData.fullName || '',
            email: userData.email || '',
            phoneNumber: userData.phoneNumber || ''
          });
          setAccountCreatedDate(userData.accountCreatedDate || 'March 22, 2022');
        } else {
          // If no saved data, try to load from other localStorage keys
          const userEmail = localStorage.getItem('userEmail');
          const userName = localStorage.getItem('userName');

          if (userEmail || userName) {
            setFormData(prev => ({
              ...prev,
              email: userEmail || '',
              fullName: userName || ''
            }));
          }
        }

        // Load subscription data if available
        const savedSubscriptionData = localStorage.getItem('userSubscriptionData');
        if (savedSubscriptionData) {
          const subscriptionData = JSON.parse(savedSubscriptionData);
          setCurrentPlan(subscriptionData.plan || 'Free');
          setSubscriptionPeriod(subscriptionData.period || 'monthly');
          if (subscriptionData.startDate) {
            setSubscriptionStartDate(new Date(subscriptionData.startDate));
          }
        }
      } catch (error) {
        console.error('Error loading user data:', error);
        // Fallback to default data if there's an error
      }
    };

    loadUserData();
  }, []);

  // Check subscription status and automatically downgrade when period expires
  useEffect(() => {
    const checkSubscriptionStatus = () => {
      if (currentPlan === 'Premium') {
        const now = new Date();
        let accessEndDate;

        if (subscriptionPeriod === 'monthly') {
          // For monthly plans, check if we're past the end of the month
          accessEndDate = new Date(now.getFullYear(), now.getMonth() + 1, 0);
        } else {
          // For yearly plans, check if we're past the end of the year
          accessEndDate = new Date(now.getFullYear(), 11, 31);
        }

        // If current date is past the access end date, automatically downgrade
        if (now > accessEndDate) {
          console.log('Subscription period expired, automatically downgrading to Free plan');
          setCurrentPlan('Free');
          alert('Your Premium subscription period has expired. You have been moved to the Free plan.');
        }
      }
    };

    // Check immediately when component mounts
    checkSubscriptionStatus();

    // Check every day to see if subscription has expired
    const interval = setInterval(checkSubscriptionStatus, 24 * 60 * 60 * 1000);

    return () => clearInterval(interval);
  }, [currentPlan, subscriptionPeriod]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handlePasswordChange = (e) => {
    const { name, value } = e.target;
    setPasswordData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleChangePassword = () => {
    // Check if current password is provided
    if (!passwordData.currentPassword.trim()) {
      alert('Please enter your current password!');
      return;
    }

    // Check if new password is provided
    if (!passwordData.newPassword.trim()) {
      alert('Please enter a new password!');
      return;
    }

    // Check if confirm password is provided
    if (!passwordData.confirmPassword.trim()) {
      alert('Please confirm your new password!');
      return;
    }

    // Check if new password matches confirm password
    if (passwordData.newPassword !== passwordData.confirmPassword) {
      alert('New passwords do not match!');
      return;
    }

    // Check if new password is different from current password
    if (passwordData.currentPassword === passwordData.newPassword) {
      alert('New password must be different from your current password!');
      return;
    }

    // Check password length requirement
    if (passwordData.newPassword.length < 6) {
      alert('New password must be at least 6 characters long!');
      return;
    }

    // Here you would typically verify the current password against the stored password
    // For demo purposes, we'll simulate a password check
    const storedPassword = localStorage.getItem('userPassword'); // This would come from your backend

    if (storedPassword && passwordData.currentPassword !== storedPassword) {
      alert('Current password is incorrect! Please try again.');
      return;
    }

    // If all validations pass, proceed with password change
    console.log('Changing password:', passwordData);

    // Simulate API call
    setIsLoading(true);

    setTimeout(() => {
      try {
        // Save new password to localStorage (in production, this would go to backend)
        localStorage.setItem('userPassword', passwordData.newPassword);

        alert('Password changed successfully!');

        // Reset password form
        setPasswordData({
          currentPassword: '',
          newPassword: '',
          confirmPassword: ''
        });

        setIsLoading(false);
      } catch (error) {
        console.error('Error changing password:', error);
        alert('Error changing password. Please try again.');
        setIsLoading(false);
      }
    }, 1000);
  };

  const handleSaveChanges = () => {
    setIsLoading(true);

    // Simulate API call
    setTimeout(() => {
      try {
        // Save updated profile data to localStorage
        const userProfileData = {
          fullName: formData.fullName,
          email: formData.email,
          phoneNumber: formData.phoneNumber,
          accountCreatedDate: accountCreatedDate,
          lastUpdated: new Date().toISOString()
        };

        localStorage.setItem('userProfileData', JSON.stringify(userProfileData));

        // Here you would typically send the form data to the backend
        console.log('Saving profile changes:', formData);
        alert('Profile updated successfully! Your changes have been saved.');
        setIsLoading(false);
      } catch (error) {
        console.error('Error saving profile data:', error);
        alert('Error saving profile data. Please try again.');
        setIsLoading(false);
      }
    }, 1000);
  };

  const handleCancel = () => {
    // Reset form to original values
    setFormData({
      fullName: '',
      email: '',
      phoneNumber: ''
    });
  };

  const handleManageSubscription = () => {
    setShowSubscriptionModal(true);
  };

  const handleCloseSubscriptionModal = () => {
    setShowSubscriptionModal(false);
  };

  const handleUpgradePlan = () => {
    // Here you would typically handle the upgrade logic
    console.log('Upgrading plan...');
    alert('Redirecting to upgrade page...');
    setShowSubscriptionModal(false);
  };

  const handleDowngradePlan = () => {
    const confirmed = window.confirm(
      'Are you sure you want to cancel your Premium subscription? You will lose access to premium features and be moved to the Free plan.'
    );

    if (confirmed) {
      // Calculate when access should end based on subscription period
      const now = new Date();
      let accessEndDate;

      if (subscriptionPeriod === 'monthly') {
        // For monthly plans, access ends at the end of the current month
        accessEndDate = new Date(now.getFullYear(), now.getMonth() + 1, 0);
      } else {
        // For yearly plans, access ends at the end of the current year
        accessEndDate = new Date(now.getFullYear(), 11, 31);
      }

      // Format the date for display
      const formattedEndDate = accessEndDate.toLocaleDateString('en-US', {
        year: 'numeric',
        month: 'long',
        day: 'numeric'
      });

      // Here you would typically handle the subscription cancellation logic
      console.log('Cancelling Premium subscription...');
      console.log('Access will end on:', formattedEndDate);

      // Show user when their access will end
      alert(`Premium subscription cancelled successfully! You will have access to premium features until ${formattedEndDate}. After that, you will be moved to the Free plan.`);

      // Don't immediately change the plan - they keep premium access until the period ends
      setShowSubscriptionModal(false);
    }
  };

  const handleCancelSubscription = () => {
    const confirmed = window.confirm(
      'Are you sure you want to cancel your subscription? You will lose access to all premium features.'
    );

    if (confirmed) {
      // Here you would typically handle the cancellation logic
      console.log('Cancelling subscription...');
      setCurrentPlan('Free');
      alert('Subscription cancelled successfully!');
      setShowSubscriptionModal(false);
    }
  };

  const handleDeactivateAccount = () => {
    const confirmed = window.confirm(
      'Are you sure you want to deactivate your account? This action cannot be undone and you will lose access to all features immediately.'
    );

    if (confirmed) {
      // Here you would typically send the deactivation request to the backend
      console.log('Deactivating current user account...');

      // Simulate account deactivation
      setIsLoading(true);

      setTimeout(() => {
        // Reset all user data and state
        setFormData({
          fullName: '',
          email: '',
          phoneNumber: ''
        });
        setPasswordData({
          currentPassword: '',
          newPassword: '',
          confirmPassword: ''
        });
        setCurrentPlan('Free');
        setSubscriptionPeriod('monthly');
        setSubscriptionStartDate(new Date());

        setIsLoading(false);

        // Show deactivation confirmation
        alert('Your account has been deactivated successfully. You will be logged out and redirected to the home page.');

        // Here you would typically:
        // 1. Clear user session/tokens
        // 2. Redirect to home page
        // 3. Clear localStorage
        // 4. Update backend user status

        // For demo purposes, redirect to home page
        window.location.href = '/';
      }, 1500);
    }
  };

  // Function to create sample user data for demonstration
  const createSampleUserData = () => {
    const sampleUserData = {
      fullName: 'John Doe',
      email: 'john.doe@example.com',
      phoneNumber: '+65 9123 4567',
      accountCreatedDate: 'January 15, 2024'
    };

    const sampleSubscriptionData = {
      plan: 'Premium',
      period: 'monthly',
      startDate: '2024-01-15'
    };

    // Sample password for testing password change functionality
    const samplePassword = 'password123';

    localStorage.setItem('userProfileData', JSON.stringify(sampleUserData));
    localStorage.setItem('userSubscriptionData', JSON.stringify(sampleSubscriptionData));
    localStorage.setItem('userPassword', samplePassword);

    // Reload the data
    window.location.reload();
  };



  return (
    <div className="user-dashboard">
      {/* Top Header */}
      <Header />

      <div className="user-dashboard-container">
        {/* Left Sidebar */}
        <Navbar activeTab={activeTab} setActiveTab={setActiveTab} />

        {/* Main Content */}
        <main className="user-main-content">
          <h1 className="profile-section-title">Profile</h1>

          {/* Personal Information */}
          <section className="profile-section">
            <h2 className="profile-subsection-title">Personal Information</h2>
            <div className="profile-form-group">
              <label htmlFor="fullName">Full Name</label>
              <input
                type="text"
                id="fullName"
                name="fullName"
                value={formData.fullName}
                onChange={handleInputChange}
                placeholder="Enter your full name"
              />
            </div>
            <div className="profile-form-group">
              <label htmlFor="email">Email Address</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                placeholder="Enter your email address"
              />
            </div>
            <div className="profile-form-group">
              <label htmlFor="phoneNumber">Phone Number</label>
              <input
                type="tel"
                id="phoneNumber"
                name="phoneNumber"
                value={formData.phoneNumber}
                onChange={handleInputChange}
                placeholder="Enter your phone number"
              />
            </div>

            {/* Demo button - remove in production */}
            {!formData.fullName && (
              <button 
                className="profile-demo-btn"
                onClick={createSampleUserData}
                style={{
                  background: '#10b981',
                  color: 'white',
                  border: 'none',
                  borderRadius: '8px',
                  padding: '0.5rem 1rem',
                  fontSize: '0.875rem',
                  cursor: 'pointer',
                  marginTop: '1rem'
                }}
              >
                Load Sample User Data (Demo)
              </button>
            )}
          </section>

          {/* Change Password */}
          <section className="profile-section">
            <h2 className="profile-subsection-title">Change Password</h2>
            <div className="profile-form-group">
              <label htmlFor="currentPassword">Current Password</label>
              <input
                type="password"
                id="currentPassword"
                name="currentPassword"
                value={passwordData.currentPassword}
                onChange={handlePasswordChange}
                placeholder="Enter current password"
              />
            </div>
            <div className="profile-form-group">
              <label htmlFor="newPassword">New Password</label>
              <input
                type="password"
                id="newPassword"
                name="newPassword"
                value={passwordData.newPassword}
                onChange={handlePasswordChange}
                placeholder="Enter new password"
              />
            </div>
            <div className="profile-form-group">
              <label htmlFor="confirmPassword">Confirm New Password</label>
              <input
                type="password"
                id="confirmPassword"
                name="confirmPassword"
                value={passwordData.confirmPassword}
                onChange={handlePasswordChange}
                placeholder="Confirm new password"
              />
            </div>
            <button 
              className="profile-change-password-btn"
              onClick={handleChangePassword}
            >
              Change Password
            </button>
          </section>

          {/* Account Information */}
          <section className="profile-section">
            <h2 className="profile-subsection-title">Account Information</h2>
            <div className="profile-account-info">
              <div className="profile-info-item">
                <span className="profile-info-label">Account Created:</span>
                <span className="profile-info-value">{accountCreatedDate}</span>
              </div>
            </div>
          </section>

          {/* Subscription */}
          <section className="profile-section">
            <h2 className="profile-subsection-title">Subscription</h2>
            <div className="profile-subscription-info">
              <div className="profile-info-item">
                <span className="profile-info-label">Current Plan:</span>
                <span className={`profile-info-value ${currentPlan.toLowerCase()}`}>{currentPlan}</span>
              </div>
              <p className="profile-subscription-description">
                {currentPlan === 'Premium' && 'Enjoy exclusive access to advanced search filters, detailed property reports, and personalized alerts with your Premium plan.'}
                {currentPlan === 'Free' && 'Basic access to property listings. Upgrade to unlock advanced features and detailed reports.'}
              </p>
              <button 
                className="profile-manage-subscription-btn"
                onClick={handleManageSubscription}
              >
                Manage Subscription
              </button>
            </div>
          </section>

          {/* Account Settings */}
          <section className="profile-section">
            <h2 className="profile-subsection-title">Account Settings</h2>
            <button 
              className="profile-deactivate-account-btn"
              onClick={handleDeactivateAccount}
              disabled={isLoading}
            >
              {isLoading ? 'Deactivating...' : 'Deactivate My Account'}
            </button>
          </section>

          {/* Action Buttons */}
          <div className="profile-action-buttons">
            <button 
              className="profile-cancel-btn"
              onClick={handleCancel}
            >
              Cancel
            </button>
            <button 
              className="profile-save-btn"
              onClick={handleSaveChanges}
              disabled={isLoading}
            >
              {isLoading ? 'Saving...' : 'Save Changes'}
            </button>
          </div>
        </main>
      </div>

      {/* Subscription Management Modal */}
      {showSubscriptionModal && (
        <div className="subscription-modal-overlay">
          <div className="subscription-modal">
            <div className="subscription-modal-header">
              <h2>Manage Subscription</h2>
              <button 
                className="subscription-modal-close"
                onClick={handleCloseSubscriptionModal}
              >

              </button>
            </div>

            <div className="subscription-modal-content">
              <div className="current-plan-display">
                <h3>Current Plan: <span className={currentPlan.toLowerCase()}>{currentPlan}</span></h3>
                <p>
                  {currentPlan === 'Premium' && `You currently have access to all premium features including advanced search filters, detailed property reports, and personalized alerts. You are on a ${subscriptionPeriod} plan. Canceling will move you to Free plan at the end of your current ${subscriptionPeriod} period.`}
                  {currentPlan === 'Free' && 'You currently have basic access to property listings. Upgrade to unlock advanced features and detailed reports.'}
                </p>
                {currentPlan === 'Premium' && (
                  <div className="subscription-period-info">
                    <p><strong>Subscription Period:</strong> {subscriptionPeriod === 'monthly' ? 'Monthly' : 'Yearly'}</p>
                    <p><strong>Started:</strong> {subscriptionStartDate.toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' })}</p>
                  </div>
                )}
              </div>

              <div className="subscription-actions">
                {currentPlan === 'Premium' && (
                  <button 
                    className="subscription-action-btn downgrade"
                    onClick={handleDowngradePlan}
                  >
                    Cancel Premium Subscription
                  </button>
                )}

                {currentPlan === 'Free' && (
                  <button 
                    className="subscription-action-btn upgrade"
                    onClick={handleUpgradePlan}
                  >
                    Upgrade to Premium
                  </button>
                )}
              </div>


            </div>
          </div>
        </div>
      )}

      {/* Footer */}
      <Footer />
    </div>
  );
};

export default Profile;
