import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { GOOGLE_MAPS_API_KEY } from '../../config/maps';
import './prediction.css';

const GOOGLE_MAPS_LIBRARIES = ['places', 'geometry'];

function loadGoogleMaps(apiKey) {
  if (!apiKey) {
    return Promise.reject(new Error('Missing Google Maps API key (set in src/config/maps.js or .env)'));
  }
  if (window.google && window.google.maps) {
    return Promise.resolve(window.google);
  }
  if (window._googleMapsPromise) {
    return window._googleMapsPromise;
  }
  const script = document.createElement('script');
  const libs = GOOGLE_MAPS_LIBRARIES.join(',');
  script.src = `https://maps.googleapis.com/maps/api/js?key=${apiKey}&libraries=${libs}`;
  script.async = true;
  script.defer = true;
  window._googleMapsPromise = new Promise((resolve, reject) => {
    script.onload = () => resolve(window.google);
    script.onerror = reject;
  });
  document.head.appendChild(script);
  return window._googleMapsPromise;
}

const DEFAULT_CENTER = { lat: 1.2868108, lng: 103.8545349 }; // SG center-ish
const SEARCH_RADIUS_METERS = 1000;

const AMENITY_OPTIONS = [
  { type: 'school', label: 'Schools' },
  { type: 'hospital', label: 'Hospitals' },
  { type: 'supermarket', label: 'Supermarkets' },
  { type: 'restaurant', label: 'Restaurants' },
  { type: 'cafe', label: 'Cafes' },
  { type: 'bank', label: 'Banks' },
  { type: 'atm', label: 'ATMs' },
  { type: 'shopping_mall', label: 'Shopping Malls' },
  { type: 'bus_station', label: 'Bus Stations' },
  { type: 'subway_station', label: 'MRT' },
  { type: 'gym', label: 'Gyms' },
  { type: 'pharmacy', label: 'Pharmacies' },
];

const Prediction = () => {
  const [activeTab, setActiveTab] = useState('price-prediction');
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);
  const [activeMapTab, setActiveMapTab] = useState('street');
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedAmenityTypes, setSelectedAmenityTypes] = useState(
    ['school', 'supermarket', 'restaurant', 'bus_station', 'subway_station']
  );
  const [amenities, setAmenities] = useState([]);
  const [isLoadingAmenities, setIsLoadingAmenities] = useState(false);
  const [amenitiesError, setAmenitiesError] = useState('');
  const [isAddressBookmarked, setIsAddressBookmarked] = useState(false);
  const [isPredictionBookmarked, setIsPredictionBookmarked] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();
  
  // Get search data from navigation state or use default
  const searchData = location.state?.searchData || {
    address: '123 Main Street, Singapore',
    propertyType: 'Industrial Property',
    floorArea: '1,500',
    level: 'Ground Floor',
    unit: 'A1'
  };

  // Check if the address was originally a postal code
  const isPostalCodeSearch = /^\d{6}$/.test(searchData.address.split(',')[0].trim());

  const mapInstanceRef = useRef(null);
  const markerRef = useRef(null);
  const placesServiceRef = useRef(null);
  const lastLocationRef = useRef(null);

  const navigationItems = [
    { id: 'dashboard', label: 'Dashboard', icon: 'üè†' },
    { id: 'price-prediction', label: 'Price Prediction', icon: 'üìà' },
    { id: 'bookmarks', label: 'Bookmarks', icon: 'üîñ' },
    { id: 'support', label: 'Support', icon: '‚ùì' },
    { id: 'feedback', label: 'Feedback', icon: 'üí¨' },
    { id: 'profile', label: 'Profile', icon: 'üë§' }
  ];

  // Generate dynamic similar properties based on search data
  const generateSimilarProperties = () => {
    const baseAddress = searchData.address.split(',')[0]; // Get street name
    const propertyType = searchData.propertyType;
    const baseSize = parseInt(searchData.floorArea.replace(/,/g, ''));
    
    return [
      {
        id: 1,
        address: `${baseAddress.replace(/\d+/, '456')} Oak Avenue, Singapore`,
        propertyType: propertyType,
        size: `${baseSize + 200} sq ft`,
        transactionDate: '2023-10-01',
        transactionPrice: '$2.7M'
      },
      {
        id: 2,
        address: `${baseAddress.replace(/\d+/, '789')} Pine Lane, Singapore`,
        propertyType: propertyType,
        size: `${baseSize - 300} sq ft`,
        transactionDate: '2023-09-15',
        transactionPrice: '$2.3M'
      },
      {
        id: 3,
        address: `${baseAddress.replace(/\d+/, '321')} Elm Street, Singapore`,
        propertyType: propertyType,
        size: `${baseSize + 300} sq ft`,
        transactionDate: '2023-08-20',
        transactionPrice: '$3.1M'
      },
      {
        id: 4,
        address: `${baseAddress.replace(/\d+/, '654')} Maple Road, Singapore`,
        propertyType: propertyType,
        size: `${baseSize - 200} sq ft`,
        transactionDate: '2023-07-10',
        transactionPrice: '$2.5M'
      },
      {
        id: 5,
        address: `${baseAddress.replace(/\d+/, '987')} Cedar Way, Singapore`,
        propertyType: propertyType,
        size: `${baseSize + 100} sq ft`,
        transactionDate: '2023-06-05',
        transactionPrice: '$2.9M'
      }
    ];
  };

  const similarProperties = generateSimilarProperties();

  // Generate dynamic property listings based on search data
  const generatePropertyListings = () => {
    const baseAddress = searchData.address.split(',')[0];
    const propertyType = searchData.propertyType;
    const baseSize = parseInt(searchData.floorArea.replace(/,/g, ''));
    
    return [
      {
        id: 1,
        address: baseAddress,
        propertyType: propertyType,
        size: `${searchData.floorArea} sq ft`,
        price: '$2.5M - $2.8M',
        image: 'https://images.unsplash.com/photo-1586528116311-ad8dd3c8310d?w=100&h=80&fit=crop'
      },
      {
        id: 2,
        address: `${baseAddress.replace(/\d+/, '456')} Industrial Park`,
        propertyType: propertyType,
        size: `${baseSize + 500} sq ft`,
        price: '$3.2M - $3.5M',
        image: 'https://images.unsplash.com/photo-1560472354-b33ff0c44a43?w=100&h=80&fit=crop'
      },
      {
        id: 3,
        address: `${baseAddress.replace(/\d+/, '789')} Business Center`,
        propertyType: propertyType,
        size: `${baseSize + 300} sq ft`,
        price: '$2.8M - $3.1M',
        image: 'https://images.unsplash.com/photo-1497366216548-37526070297c?w=100&h=80&fit=crop'
      }
    ];
  };

  const propertyListings = generatePropertyListings();

  const agents = [
    {
      id: 1,
      name: 'Ethan Tan',
      role: 'Senior Specialist',
      image: 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=60&h=60&fit=crop&crop=face'
    },
    {
      id: 2,
      name: 'Darrel Lee',
      role: 'Associate Partner',
      image: 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=60&h=60&fit=crop&crop=face'
    }
  ];

  // Initialize Google Maps
  useEffect(() => {
    loadGoogleMaps(GOOGLE_MAPS_API_KEY)
      .then((google) => {
        const mapDiv = document.getElementById('prediction-map');
        if (!mapDiv) {
          console.error('Map div not found');
          return;
        }
        
        // Try to geocode the searched address to get coordinates
        const geocoder = new google.maps.Geocoder();
        geocoder.geocode({ address: searchData.address }, (results, status) => {
          let center = DEFAULT_CENTER;
          if (status === 'OK' && results[0]) {
            center = results[0].geometry.location;
          }
          
          const map = new google.maps.Map(mapDiv, {
            center: center,
            zoom: 16,
            mapTypeControl: false,
            streetViewControl: false,
            fullscreenControl: true,
          });
          mapInstanceRef.current = map;
          placesServiceRef.current = new google.maps.places.PlacesService(map);

          // Add marker for the property
          const marker = new google.maps.Marker({
            position: center,
            map: map,
            title: searchData.address
          });
          markerRef.current = marker;
          lastLocationRef.current = center;

          // Load amenities for the property location
          fetchAmenities(center, selectedAmenityTypes);
        });
      })
      .catch((err) => {
        console.error('Error loading Google Maps:', err);
      });
  }, [activeMapTab, selectedAmenityTypes, searchData.address]);

  const handleProfileClick = () => {
    setShowProfileDropdown(!showProfileDropdown);
  };

  const handleProfilePage = () => {
    setShowProfileDropdown(false);
    navigate('/profile');
  };

  const handleLogout = () => {
    setShowProfileDropdown(false);
    localStorage.removeItem('isLoggedIn');
    localStorage.removeItem('userType');
    localStorage.removeItem('userPreferences');
    navigate('/login');
  };

  const handleClickOutside = (e) => {
    if (!e.target.closest('.user-profile')) {
      setShowProfileDropdown(false);
    }
  };

  const handleNavigation = (itemId) => {
    setActiveTab(itemId);
    
    switch (itemId) {
      case 'dashboard':
        navigate('/dashboard/free');
        break;
      case 'price-prediction':
        navigate('/dashboard/priceprediction');
        break;
      case 'bookmarks':
        navigate('/bookmarks');
        break;
      case 'support':
        navigate('/support');
        break;
      case 'feedback':
        navigate('/feedback');
        break;
      case 'profile':
        navigate('/profile');
        break;
      default:
        break;
    }
  };

  const nearbySearchOnce = (location, type) => {
    return new Promise((resolve) => {
      if (!placesServiceRef.current) {
        console.error('‚ùå PlacesService not initialized');
        resolve([]);
        return;
      }
      
      placesServiceRef.current.nearbySearch(
        { location, radius: SEARCH_RADIUS_METERS, type },
        (results, status) => {
          if (status === window.google.maps.places.PlacesServiceStatus.OK && results) {
            console.log(`‚úÖ Found ${results.length} ${type} places`);
            resolve(results.slice(0, 10));
          } else {
            console.warn(`‚ö†Ô∏è No ${type} places found or error:`, status);
            resolve([]);
          }
        }
      );
    });
  };

  const fetchAmenities = async (latLng, typesOverride) => {
    const activeTypes = Array.isArray(typesOverride) ? typesOverride : selectedAmenityTypes;
    try {
      setIsLoadingAmenities(true);
      setAmenitiesError('');
      
      console.log('üîç Fetching amenities for:', latLng.lat(), latLng.lng());
      console.log('üìç Search types:', activeTypes);
      
      const promises = activeTypes.map((t) => nearbySearchOnce(latLng, t));
      const results = await Promise.all(promises);
      const flat = results.flat();
      
      console.log('üìä Raw results:', flat.length, 'amenities found');
      
      // Deduplicate by place_id
      const uniqueMap = new Map();
      for (const r of flat) {
        if (!uniqueMap.has(r.place_id)) uniqueMap.set(r.place_id, r);
      }
      
      console.log('üîÑ After deduplication:', uniqueMap.size, 'unique amenities');
      
      // Enrich with distance and primary type label
      const google = window.google;
      const enriched = Array.from(uniqueMap.values()).map((p) => {
        const distance = google && google.maps.geometry
          ? google.maps.geometry.spherical.computeDistanceBetween(latLng, p.geometry.location)
          : null;
        return {
          id: p.place_id,
          name: p.name,
          vicinity: p.vicinity,
          rating: p.rating,
          userRatingsTotal: p.user_ratings_total,
          types: p.types,
          location: p.geometry && p.geometry.location,
          distance,
        };
      });
      
      // Sort by distance
      enriched.sort((a, b) => (a.distance || Infinity) - (b.distance || Infinity));
      
      console.log('‚úÖ Final amenities:', enriched.length, 'amenities ready');
      console.log('üìç Sample amenities:', enriched.slice(0, 3));
      
      setAmenities(enriched);
    } catch (e) {
      console.error('‚ùå Error fetching amenities:', e);
      setAmenitiesError('Failed to load nearby amenities.');
    } finally {
      setIsLoadingAmenities(false);
    }
  };

  const toggleAmenity = (type) => {
    setSelectedAmenityTypes((prev) => {
      const next = prev.includes(type) ? prev.filter((t) => t !== type) : [...prev, type];
      if (lastLocationRef.current) {
        // Use the new selection immediately
        fetchAmenities(lastLocationRef.current, next);
      }
      return next;
    });
  };

  const handleMapTabChange = (tab) => {
    setActiveMapTab(tab);
    const mapDiv = document.getElementById('prediction-map');
    
    if (tab === 'onemap') {
      // Open OneMap in new tab with address search and pinpoint
      const encodedAddress = encodeURIComponent(searchData.address);
      const oneMapUrl = `https://www.onemap.gov.sg/amm/amm.html?mapStyle=Default&zoomLevel=16&searchVal=${encodedAddress}&returnVal=true&pin=true&marker=true&showMarker=true&centerOnSearch=true&searchType=address&showSearchResults=true&highlight=true`;
      console.log('üîç Opening OneMap in new tab:', oneMapUrl);
      window.open(oneMapUrl, '_blank');
      
      // Show a message in the map container
      mapDiv.innerHTML = `
        <div style="display: flex; flex-direction: column; align-items: center; justify-content: center; height: 100%; background: #f8f9fa; border-radius: 8px; padding: 2rem;">
          <div style="font-size: 3rem; margin-bottom: 1rem;">üó∫Ô∏è</div>
          <h3 style="margin-bottom: 1rem; color: #333;">OneMap Opened in New Tab</h3>
          <p style="text-align: center; color: #666; margin-bottom: 1.5rem;">
            OneMap has been opened in a new tab with the address: <strong>${searchData.address}</strong>
          </p>
          <button 
            onclick="window.open('${oneMapUrl}', '_blank')" 
            style="background: #3b82f6; color: white; border: none; padding: 0.75rem 1.5rem; border-radius: 6px; cursor: pointer; font-weight: 500;"
          >
            Open OneMap Again
          </button>
        </div>
      `;
    } else if (tab === 'ura') {
      // Open URA Masterplan Map in new tab with coordinates
      const lat = lastLocationRef.current?.lat() || 1.2868108;
      const lng = lastLocationRef.current?.lng() || 103.8545349;
      const uraUrl = `https://www.ura.gov.sg/maps/?service=mp&lat=${lat}&lng=${lng}&zoom=16&marker=true&popup=true&showMarker=true&highlight=true`;
      console.log('üîç Opening URA Masterplan Map in new tab:', uraUrl);
      window.open(uraUrl, '_blank');
      
      // Show a message in the map container
      mapDiv.innerHTML = `
        <div style="display: flex; flex-direction: column; align-items: center; justify-content: center; height: 100%; background: #f8f9fa; border-radius: 8px; padding: 2rem;">
          <div style="font-size: 3rem; margin-bottom: 1rem;">üèóÔ∏è</div>
          <h3 style="margin-bottom: 1rem; color: #333;">URA Masterplan Map Opened in New Tab</h3>
          <p style="text-align: center; color: #666; margin-bottom: 1.5rem;">
            URA Masterplan Map has been opened in a new tab for the location: <strong>${searchData.address}</strong>
          </p>
          <button 
            onclick="window.open('${uraUrl}', '_blank')" 
            style="background: #3b82f6; color: white; border: none; padding: 0.75rem 1.5rem; border-radius: 6px; cursor: pointer; font-weight: 500;"
          >
            Open URA Map Again
          </button>
        </div>
      `;
    } else {
      // Load Google Maps
      if (mapInstanceRef.current && window.google) {
        const mapTypeId = tab === 'satellite' ? 'satellite' : tab === 'hybrid' ? 'hybrid' : 'roadmap';
        mapInstanceRef.current.setMapTypeId(window.google.maps.MapTypeId[mapTypeId.toUpperCase()]);
      }
    }
  };

  const handleDownloadReport = () => {
    // Create a comprehensive report with all prediction data
    const reportData = {
      propertyAddress: searchData.address,
      propertyType: searchData.propertyType,
      floorArea: searchData.floorArea,
      prediction: {
        estimatedValue: '$2.5M - $2.8M',
        conservative: '$2.5M - $2.8M',
        aggressive: '$2.5K - $2.8K'
      },
      propertyDetails: {
        landArea: '1,000 sqft',
        builtUpArea: '1,500 sqft',
        yearOfCompletion: '2000',
        tenure: '99 years',
        zoning: 'B2',
        district: '22',
        storeys: '3',
        units: '10',
        occupancyRate: '90%'
      },
      marketTrends: {
        priceChange: '+5%',
        period: 'Last 12 months (YoY)'
      },
      similarProperties: similarProperties,
      amenities: amenities,
      generatedAt: new Date().toISOString()
    };

    // Create and download the report
    const reportBlob = new Blob([JSON.stringify(reportData, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(reportBlob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `prediction-report-${searchData.address.replace(/[^a-zA-Z0-9]/g, '-')}-${new Date().toISOString().split('T')[0]}.json`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  const handleBookmarkAddress = () => {
    setIsAddressBookmarked(!isAddressBookmarked);
    // Here you would typically save to localStorage or send to backend
    const bookmarks = JSON.parse(localStorage.getItem('addressBookmarks') || '[]');
    if (!isAddressBookmarked) {
      bookmarks.push({
        address: searchData.address,
        propertyType: searchData.propertyType,
        bookmarkedAt: new Date().toISOString()
      });
    } else {
      const index = bookmarks.findIndex(b => b.address === searchData.address);
      if (index > -1) bookmarks.splice(index, 1);
    }
    localStorage.setItem('addressBookmarks', JSON.stringify(bookmarks));
  };

  const handleBookmarkPrediction = () => {
    setIsPredictionBookmarked(!isPredictionBookmarked);
    // Here you would typically save to localStorage or send to backend
    const bookmarks = JSON.parse(localStorage.getItem('predictionBookmarks') || '[]');
    if (!isPredictionBookmarked) {
      bookmarks.push({
        address: searchData.address,
        propertyType: searchData.propertyType,
        prediction: '$2.5M - $2.8M',
        bookmarkedAt: new Date().toISOString()
      });
    } else {
      const index = bookmarks.findIndex(b => b.address === searchData.address);
      if (index > -1) bookmarks.splice(index, 1);
    }
    localStorage.setItem('predictionBookmarks', JSON.stringify(bookmarks));
  };

  // Add click listener to close dropdown when clicking outside
  useEffect(() => {
    document.addEventListener('click', handleClickOutside);
    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, []);

  return (
    <div className="prediction-dashboard">
      {/* Top Header */}
      <header className="dashboard-header">
        <div className="header-center">
          <div className="logo">
            <span className="logo-icon">üè†</span>
            <span className="logo-text">Valuez</span>
          </div>
        </div>
        <div className="header-right">
          <div className="notification-icon">üîî</div>
          <div className="user-profile">
            <img 
              src="https://images.unsplash.com/photo-1494790108755-2616b612b786?w=40&h=40&fit=crop&crop=face" 
              alt="User Profile" 
              className="profile-image"
              onClick={handleProfileClick}
            />
            {showProfileDropdown && (
              <div className="profile-dropdown">
                <div className="dropdown-item" onClick={handleProfilePage}>
                  <span className="dropdown-icon">üë§</span>
                  <span>Profile</span>
                </div>
                <div className="dropdown-divider"></div>
                <div className="dropdown-item" onClick={handleLogout}>
                  <span className="dropdown-icon">üö™</span>
                  <span>Logout</span>
                </div>
              </div>
            )}
          </div>
        </div>
      </header>

      <div className="dashboard-container">
        {/* Left Sidebar */}
        <aside className="sidebar">
          <nav className="sidebar-nav">
            {navigationItems.map((item) => (
              <button
                key={item.id}
                className={`nav-item ${activeTab === item.id ? 'active' : ''}`}
                onClick={() => handleNavigation(item.id)}
              >
                <span className="nav-icon">{item.icon}</span>
                <span className="nav-label">{item.label}</span>
              </button>
            ))}
            <div className="nav-divider"></div>
            <button className="nav-item logout-item" onClick={handleLogout}>
              <span className="nav-icon">üö™</span>
              <span className="nav-label">Logout</span>
            </button>
          </nav>
        </aside>

        {/* Main Content */}
        <main className="main-content">
          <div className="content-header">
            <div className="header-top">
              <h1 className="main-title">Prediction for {searchData.address}</h1>
              <div className="header-actions">
                <button 
                  className={`action-btn bookmark-btn ${isAddressBookmarked ? 'active' : ''}`}
                  onClick={handleBookmarkAddress}
                  title={isAddressBookmarked ? 'Remove from bookmarks' : 'Bookmark address'}
                >
                  {isAddressBookmarked ? 'üîñ' : 'üîñ'}
                  <span>{isAddressBookmarked ? 'Bookmarked' : 'Bookmark Address'}</span>
                </button>
                <button 
                  className={`action-btn bookmark-btn ${isPredictionBookmarked ? 'active' : ''}`}
                  onClick={handleBookmarkPrediction}
                  title={isPredictionBookmarked ? 'Remove prediction from bookmarks' : 'Bookmark prediction'}
                >
                  {isPredictionBookmarked ? 'üìå' : 'üìå'}
                  <span>{isPredictionBookmarked ? 'Prediction Saved' : 'Bookmark Prediction'}</span>
                </button>
                <button 
                  className="action-btn download-btn"
                  onClick={handleDownloadReport}
                  title="Download prediction report"
                >
                  üìÑ
                  <span>Download Report</span>
                </button>
              </div>
            </div>
            {isPostalCodeSearch && (
              <div className="postal-code-info">
                <small>üìç Address found from postal code search</small>
              </div>
            )}
          </div>

          {/* Prediction Summary */}
          <section className="prediction-summary">
            <div className="prediction-cards">
              <div className="prediction-card primary">
                <h3>Estimated Sales Prediction</h3>
                <div className="prediction-value">$2.5M - $2.8M</div>
              </div>
              <div className="prediction-card">
                <h3>Estimated Rental Prediction</h3>
                <div className="prediction-value">$8K - $12K/month</div>
              </div>
            </div>
          </section>

          {/* Key Factors */}
          <section className="key-factors">
            <h2>Key Factors</h2>
            <p>
              The prediction is influenced by factors such as competitive pricing, location, property age, and classification, which contribute to the estimated market value.
            </p>
          </section>

          {/* Property Details Summary */}
          <section className="property-details">
            <h2>Property Details Summary</h2>
            <div className="details-grid">
              <div className="detail-item">
                <span className="detail-label">Property Type:</span>
                <span className="detail-value">{searchData.propertyType}</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">Land Area:</span>
                <span className="detail-value">1,000 sqft</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">Built-up Area:</span>
                <span className="detail-value">1,500 sqft</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">Year of Completion:</span>
                <span className="detail-value">2000</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">Tenure:</span>
                <span className="detail-value">99 years</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">Zoning:</span>
                <span className="detail-value">B2</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">District:</span>
                <span className="detail-value">22</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">No. of Storeys:</span>
                <span className="detail-value">3</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">No. of Units:</span>
                <span className="detail-value">10</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">Occupancy Rate:</span>
                <span className="detail-value">90%</span>
              </div>
            </div>
          </section>

          {/* Market Trends */}
          <section className="market-trends">
            <h2>Market Trends</h2>
            <h3>Property Sales Price Trends</h3>
            <div className="trend-metric">
              <div className="trend-value">+5%</div>
              <div className="trend-period">Last 12 months (YoY)</div>
            </div>
            <div className="trend-chart">
              <div className="chart-placeholder">
                <div className="chart-line"></div>
                <div className="chart-labels">
                  <span>Jan</span>
                  <span>Mar</span>
                  <span>May</span>
                  <span>Jul</span>
                  <span>Sep</span>
                </div>
              </div>
            </div>
          </section>

          {/* Similar Properties Transactions */}
          <section className="similar-properties">
            <h2>Similar Properties Transactions</h2>
            <div className="transactions-table">
              <table>
                <thead>
                  <tr>
                    <th>Address</th>
                    <th>Property Type</th>
                    <th>Size</th>
                    <th>Transaction Date</th>
                    <th>Transaction Price</th>
                  </tr>
                </thead>
                <tbody>
                  {similarProperties.map((property) => (
                    <tr key={property.id}>
                      <td>{property.address}</td>
                      <td>{property.propertyType}</td>
                      <td>{property.size}</td>
                      <td>{property.transactionDate}</td>
                      <td>{property.transactionPrice}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </section>

          {/* Median Sale Price */}
          <section className="median-sale-price">
            <h2>Median Sale Price</h2>
            <p>The median sale price for similar properties around {searchData.address} is $2.8M.</p>
          </section>

          {/* Highest Sold Price */}
          <section className="highest-sold-price">
            <h2>Highest Sold Price</h2>
            <p>The highest recorded sale for a {searchData.propertyType.toLowerCase()} is $3.1M at {searchData.address.split(',')[0].replace(/\d+/, '987')} Redhill Way, a {parseInt(searchData.floorArea.replace(/,/g, '')) + 300} sq ft unit with similar features, sold on 2023-09-10.</p>
          </section>

          {/* Location Map */}
          <section className="location-map">
            <h2>Location Map</h2>
            <div className="map-tabs">
              <button 
                className={`map-tab ${activeMapTab === 'street' ? 'active' : ''}`}
                onClick={() => handleMapTabChange('street')}
              >
                Street Map
              </button>
              <button 
                className={`map-tab ${activeMapTab === 'satellite' ? 'active' : ''}`}
                onClick={() => handleMapTabChange('satellite')}
              >
                Satellite Map
              </button>
              <button 
                className={`map-tab ${activeMapTab === 'hybrid' ? 'active' : ''}`}
                onClick={() => handleMapTabChange('hybrid')}
              >
                Hybrid Map
              </button>
            </div>
            <div id="prediction-map" className="prediction-map-container"></div>
            <div className="map-instructions">
              <p>üß≠ <strong>Nearby Amenities</strong></p>
              <p>The map shows the property location and nearby amenities.</p>
              <p><small>Use the filters below to customize which amenities to display.</small></p>
            </div>

            <div className="amenities-panel">
              <div className="amenities-header">
                <h3>Nearby Amenities</h3>
                <div className="amenity-filters">
                  {AMENITY_OPTIONS.map((opt) => (
                    <button 
                      key={opt.type}
                      type="button" 
                      className={`amenity-chip ${selectedAmenityTypes.includes(opt.type) ? 'active' : ''}`}
                      onClick={() => toggleAmenity(opt.type)}
                    >
                      {opt.label}
                    </button>
                  ))}
                </div>
              </div>

              {isLoadingAmenities && (
                <div className="amenities-loading">Loading amenities‚Ä¶</div>
              )}
              {amenitiesError && (
                <div className="amenities-error">{amenitiesError}</div>
              )}

              {!isLoadingAmenities && !amenitiesError && amenities.length === 0 && (
                <div className="amenities-empty">No amenities found for the selected filters.</div>
              )}

              {!isLoadingAmenities && amenities.length > 0 && (
                <ul className="amenities-list">
                  {amenities.map((a) => (
                    <li className="amenity-item" key={a.id}>
                      <div className="amenity-main">
                        <div className="amenity-name">{a.name}</div>
                        {a.vicinity && <div className="amenity-address">{a.vicinity}</div>}
                      </div>
                      <div className="amenity-meta">
                        {typeof a.rating === 'number' && (
                          <span className="amenity-rating">‚òÖ {a.rating.toFixed(1)}</span>
                        )}
                        {typeof a.distance === 'number' && (
                          <span className="amenity-distance">{Math.round(a.distance)} m</span>
                        )}
                        <a
                          className="amenity-link"
                          href={`https://www.google.com/maps/place/?q=place_id:${a.id}`}
                          target="_blank"
                          rel="noopener noreferrer"
                        >
                          Open in Maps
                        </a>
                      </div>
                    </li>
                  ))}
                </ul>
              )}
            </div>
          </section>

          {/* Property Listings */}
          <section className="property-listings">
            <h2>Property Listings</h2>
            <div className="listings-container">
              {propertyListings.map((listing) => (
                <div key={listing.id} className="listing-item">
                  <div className="listing-image">
                    <img src={listing.image} alt={listing.address} />
                  </div>
                  <div className="listing-content">
                    <h3 className="listing-address">{listing.address}</h3>
                    <div className="listing-details">
                      <span className="listing-type">{listing.propertyType}</span>
                      <span className="listing-size">{listing.size}</span>
                      <span className="listing-price">{listing.price}</span>
                    </div>
                    <button className="view-details-btn">View Details</button>
                  </div>
                </div>
              ))}
            </div>
            <div className="pagination">
              <button className="pagination-btn">&lt;</button>
              <button className="pagination-btn active">1</button>
              <button className="pagination-btn">2</button>
              <button className="pagination-btn">3</button>
              <button className="pagination-btn">&gt;</button>
            </div>
          </section>

          {/* Contact Agents */}
          <section className="contact-agents">
            <h2>Contact Agents</h2>
            <div className="agents-grid">
              {agents.map((agent) => (
                <div key={agent.id} className="agent-card">
                  <div className="agent-info">
                    <img src={agent.image} alt={agent.name} className="agent-image" />
                    <div className="agent-details">
                      <h3>{agent.name}</h3>
                      <p>{agent.role}</p>
                    </div>
                  </div>
                  <button className="contact-btn">Contact</button>
                </div>
              ))}
            </div>
            <p className="contact-note">You have 5 free contacts available for the month.</p>
          </section>
        </main>
      </div>

      {/* Footer */}
      <footer className="dashboard-footer">
        <div className="footer-left">
          <a href="/terms" className="footer-link">Terms</a>
          <a href="/privacy" className="footer-link">Privacy</a>
        </div>
        <div className="footer-center">
          <span className="copyright">¬© 2024 Valuez. All rights reserved.</span>
        </div>
        <div className="footer-right">
          <a href="/contact" className="footer-link">Contact</a>
          <a href="/disclaimer" className="footer-link">Disclaimer</a>
        </div>
      </footer>
    </div>
  );
};

export default Prediction;
