import React, { useEffect, useState, useRef } from 'react';
import './Explore.css';
import { GOOGLE_MAPS_API_KEY } from '../../config/maps';

const GOOGLE_MAPS_LIBRARIES = ['places', 'geometry'];

function loadGoogleMaps(apiKey) {
  if (!apiKey) {
    return Promise.reject(new Error('Missing Google Maps API key (set in src/config/maps.js or .env)'));
  }
  if (window.google && window.google.maps) {
    return Promise.resolve(window.google);
  }
  if (window._googleMapsPromise) {
    return window._googleMapsPromise;
  }
  const script = document.createElement('script');
  const libs = GOOGLE_MAPS_LIBRARIES.join(',');
  script.src = `https://maps.googleapis.com/maps/api/js?key=${apiKey}&libraries=${libs}`;
  script.async = true;
  script.defer = true;
  window._googleMapsPromise = new Promise((resolve, reject) => {
    script.onload = () => resolve(window.google);
    script.onerror = reject;
  });
  document.head.appendChild(script);
  return window._googleMapsPromise;
}

const DEFAULT_CENTER = { lat: 1.2868108, lng: 103.8545349 }; // SG center-ish
const SEARCH_RADIUS_METERS = 1000;

const AMENITY_OPTIONS = [
  { type: 'school', label: 'Schools' },
  { type: 'hospital', label: 'Hospitals' },
  { type: 'supermarket', label: 'Supermarkets' },
  { type: 'restaurant', label: 'Restaurants' },
  { type: 'cafe', label: 'Cafes' },
  { type: 'bank', label: 'Banks' },
  { type: 'atm', label: 'ATMs' },
  { type: 'shopping_mall', label: 'Shopping Malls' },
  { type: 'bus_station', label: 'Bus Stations' },
  { type: 'subway_station', label: 'MRT' },
  { type: 'train_station', label: 'Train Stations' },
  { type: 'park', label: 'Parks' },
  { type: 'gym', label: 'Gyms' },
  { type: 'pharmacy', label: 'Pharmacies' },
];

const Explore = () => {
  const [searchAddress, setSearchAddress] = useState('');
  const [clickedAddress, setClickedAddress] = useState('');
  const [isSearching, setIsSearching] = useState(false);
  const [searchError, setSearchError] = useState('');

  const [selectedAmenityTypes, setSelectedAmenityTypes] = useState(
    ['school', 'supermarket', 'restaurant', 'bus_station', 'subway_station']
  );
  const [amenities, setAmenities] = useState([]);
  const [isLoadingAmenities, setIsLoadingAmenities] = useState(false);
  const [amenitiesError, setAmenitiesError] = useState('');

  const markerRef = useRef(null);
  const mapInstanceRef = useRef(null);
  const geocoderRef = useRef(null);
  const placesServiceRef = useRef(null);
  const autocompleteRef = useRef(null);
  const inputRef = useRef(null);
  const lastLocationRef = useRef(null);

  // Initialize Google Maps
  useEffect(() => {
    loadGoogleMaps(GOOGLE_MAPS_API_KEY)
      .then((google) => {
        const mapDiv = document.getElementById('mapdiv');
        if (!mapDiv) {
          console.error('Map div not found');
          return;
        }
        const map = new google.maps.Map(mapDiv, {
          center: DEFAULT_CENTER,
          zoom: 16,
          mapTypeControl: false,
          streetViewControl: false,
          fullscreenControl: true,
        });
        mapInstanceRef.current = map;
        geocoderRef.current = new google.maps.Geocoder();
        placesServiceRef.current = new google.maps.places.PlacesService(map);

        // Map click -> reverse geocode + marker + amenities
        map.addListener('click', (e) => {
          const latLng = e.latLng;
          placeMarker(latLng);
          reverseGeocode(latLng);
          fetchAmenities(latLng, selectedAmenityTypes);
        });

        // Autocomplete on input
        if (inputRef.current) {
          const autocomplete = new google.maps.places.Autocomplete(inputRef.current, {
            componentRestrictions: { country: 'sg' },
            fields: ['geometry', 'formatted_address', 'name'],
          });
          autocompleteRef.current = autocomplete;
          autocomplete.addListener('place_changed', () => {
            const place = autocomplete.getPlace();
            if (!place || !place.geometry || !place.geometry.location) return;
            const latLng = place.geometry.location;
            map.panTo(latLng);
            map.setZoom(18);
            placeMarker(latLng);
            const address = place.formatted_address || place.name || '';
            setClickedAddress(address);
            setSearchAddress(address);
            fetchAmenities(latLng, selectedAmenityTypes);
          });
        }
      })
      .catch((err) => {
        console.error('Error loading Google Maps:', err);
        setSearchError('Map failed to load. Please configure your Google Maps API key in src/config/maps.js or .env');
      });
  }, [selectedAmenityTypes]);

  const placeMarker = (latLng) => {
    const google = window.google;
    if (!google || !mapInstanceRef.current) return;
    if (markerRef.current) {
      markerRef.current.setMap(null);
      markerRef.current = null;
    }
    const marker = new google.maps.Marker({
      position: latLng,
      map: mapInstanceRef.current,
      animation: google.maps.Animation.DROP,
    });
    markerRef.current = marker;
    lastLocationRef.current = latLng;
  };

  const reverseGeocode = (latLng) => {
    const geocoder = geocoderRef.current;
    if (!geocoder) return;
    geocoder.geocode({ location: latLng }, (results, status) => {
      if (status === 'OK' && results && results.length) {
        const address = results[0].formatted_address;
        setClickedAddress(address);
        setSearchAddress(address);
        if (markerRef.current) {
          const infowindow = new window.google.maps.InfoWindow({
            content: `<div style=\"font-weight:600\">${address}</div>`,
          });
          infowindow.open({ anchor: markerRef.current, map: mapInstanceRef.current });
        }
      }
    });
  };

  const nearbySearchOnce = (location, type) => {
    return new Promise((resolve) => {
      placesServiceRef.current.nearbySearch(
        { location, radius: SEARCH_RADIUS_METERS, type },
        (results, status) => {
          if (status === window.google.maps.places.PlacesServiceStatus.OK && results) {
            resolve(results.slice(0, 10));
          } else {
            resolve([]);
          }
        }
      );
    });
  };

  // typesOverride allows using the latest types immediately (avoids stale state)
  const fetchAmenities = async (latLng, typesOverride) => {
    const activeTypes = Array.isArray(typesOverride) ? typesOverride : selectedAmenityTypes;
    try {
      setIsLoadingAmenities(true);
      setAmenitiesError('');
      const promises = activeTypes.map((t) => nearbySearchOnce(latLng, t));
      const results = await Promise.all(promises);
      const flat = results.flat();
      // Deduplicate by place_id
      const uniqueMap = new Map();
      for (const r of flat) {
        if (!uniqueMap.has(r.place_id)) uniqueMap.set(r.place_id, r);
      }
      // Enrich with distance and primary type label
      const google = window.google;
      const enriched = Array.from(uniqueMap.values()).map((p) => {
        const distance = google && google.maps.geometry
          ? google.maps.geometry.spherical.computeDistanceBetween(latLng, p.geometry.location)
          : null;
        return {
          id: p.place_id,
          name: p.name,
          vicinity: p.vicinity,
          rating: p.rating,
          userRatingsTotal: p.user_ratings_total,
          types: p.types,
          location: p.geometry && p.geometry.location,
          distance,
        };
      });
      // Sort by distance
      enriched.sort((a, b) => (a.distance || Infinity) - (b.distance || Infinity));
      setAmenities(enriched);
    } catch (e) {
      setAmenitiesError('Failed to load nearby amenities.');
    } finally {
      setIsLoadingAmenities(false);
    }
  };

  const handleSearchAddress = async () => {
    setSearchError('');
    const google = window.google;
    if (!google || !mapInstanceRef.current) {
      setSearchError('Map not ready. Please wait a moment and try again.');
      return;
    }
    if (!searchAddress.trim()) return;
    setIsSearching(true);
    try {
      const geocoder = geocoderRef.current || new google.maps.Geocoder();
      geocoderRef.current = geocoder;
      geocoder.geocode({ address: `${searchAddress}, Singapore` }, (results, status) => {
        if (status === 'OK' && results && results.length) {
          const result = results[0];
          const latLng = result.geometry.location;
          mapInstanceRef.current.panTo(latLng);
          mapInstanceRef.current.setZoom(18);
          placeMarker(latLng);
          const address = result.formatted_address;
          setClickedAddress(address);
          setSearchAddress(address);
          fetchAmenities(latLng, selectedAmenityTypes);
        } else {
          setSearchError('No results found for this address.');
        }
        setIsSearching(false);
      });
    } catch (err) {
      setSearchError('Error searching address.');
      setIsSearching(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearchAddress();
    }
  };

  const toggleAmenity = (type) => {
    setSelectedAmenityTypes((prev) => {
      const next = prev.includes(type) ? prev.filter((t) => t !== type) : [...prev, type];
      if (lastLocationRef.current) {
        // Use the new selection immediately
        fetchAmenities(lastLocationRef.current, next);
      }
      return next;
    });
  };

  return (
    <section className="explore" id="intro">
      <h2 className="section-title">Explore Automated Valuations</h2>
      <div className="explore-header">
        <p className="section-subtitle">Estimate the value of your property now</p>
      </div>
      <div className="explore-types">
        <div className="type-pill">
          <img src="http://localhost:3845/assets/1f2c5cefac5c3b843f4e0ea23b4d6916bfbd2857.svg" alt="Commercial" />
          <span>Commercial</span>
        </div>
        <div className="type-pill">
          <img src="http://localhost:3845/assets/a6a567e6434a0a9b58b53865a5cf0064ee43d31b.svg" alt="Industrial" />
          <span>Industrial</span>
        </div>
      </div>
      <div className="explore-grid">
        <div className="explore-form">
          <div className="form-row">
            <label>Address Search</label>
            <div className="search-container">
              <input
                ref={inputRef}
                type="text"
                className="search-input"
                placeholder="Search an address in Singapore (Google Places Autocomplete)"
                value={searchAddress}
                onChange={(e) => {
                  setSearchAddress(e.target.value);
                  if (e.target.value === '') {
                    setSearchError('');
                    setClickedAddress('');
                    setAmenities([]);
                    if (markerRef.current) {
                      markerRef.current.setMap(null);
                      markerRef.current = null;
                    }
                  }
                }}
                onKeyPress={handleKeyPress}
                disabled={isSearching}
              />
              <button 
                className="search-btn" 
                onClick={handleSearchAddress}
                type="button"
                disabled={isSearching || !searchAddress.trim()}
              >
                {isSearching ? 'Searching...' : 'Search'}
              </button>
            </div>
            {searchError && (
              <div className="search-error">{searchError}</div>
            )}
          </div>
          {clickedAddress && (
            <div className="form-row">
              <label>Selected Location</label>
              <div className="selected-address">{clickedAddress}</div>
            </div>
          )}
          <div className="form-row two-cols">
            <div>
              <label>Property Type</label>
              <div className="input like-input">Select Property Type</div>
            </div>
            <div>
              <label>Floor Area</label>
              <div className="input like-input">Enter Floor Area</div>
            </div>
          </div>
          <div className="form-row">
            <button className="btn btn-primary">Predict Price</button>
            <div className="hint">Sign in to view full prediction and data insights.</div>
          </div>
        </div>
        <div className="explore-image">
          <div id="mapdiv" style={{ height: '400px' }}></div>
          <div className="map-instructions">
            <p>ðŸ§­ <strong>Nearby Amenities</strong></p>
            <p>Click the map or use search to pick a location.</p>
            <p><small>Weâ€™ll list nearby schools, transport, food, healthcare and more.</small></p>
          </div>

          <div className="amenities-panel">
            <div className="amenities-header">
              <h3>Nearby Amenities</h3>
              <div className="amenity-filters">
                {AMENITY_OPTIONS.map((opt) => (
                  <button
                    key={opt.type}
                    type="button"
                    className={`amenity-chip ${selectedAmenityTypes.includes(opt.type) ? 'active' : ''}`}
                    onClick={() => toggleAmenity(opt.type)}
                  >
                    {opt.label}
                  </button>
                ))}
              </div>
            </div>

            {isLoadingAmenities && (
              <div className="amenities-loading">Loading amenitiesâ€¦</div>
            )}
            {amenitiesError && (
              <div className="amenities-error">{amenitiesError}</div>
            )}

            {!isLoadingAmenities && !amenitiesError && amenities.length === 0 && (
              <div className="amenities-empty">Select a location to load nearby amenities.</div>
            )}

            {!isLoadingAmenities && amenities.length > 0 && (
              <ul className="amenities-list">
                {amenities.map((a) => (
                  <li className="amenity-item" key={a.id}>
                    <div className="amenity-main">
                      <div className="amenity-name">{a.name}</div>
                      {a.vicinity && <div className="amenity-address">{a.vicinity}</div>}
                    </div>
                    <div className="amenity-meta">
                      {typeof a.rating === 'number' && (
                        <span className="amenity-rating">â˜… {a.rating.toFixed(1)}</span>
                      )}
                      {typeof a.distance === 'number' && (
                        <span className="amenity-distance">{Math.round(a.distance)} m</span>
                      )}
                      <a
                        className="amenity-link"
                        href={`https://www.google.com/maps/place/?q=place_id:${a.id}`}
                        target="_blank"
                        rel="noopener noreferrer"
                      >
                        Open in Maps
                      </a>
                    </div>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Explore;
