              </div>
              <button
                className="btn btn-primary"
                onClick={() => setSelectedCard(card.id)}
              >
                View Details
              </button>
            </div>
          </article>
        ))}
      </section>

      {/* Property Card Details */}
      {selectedCard && (
        <section className="how-details">
          <div className="details-header">
            <button 
              className="close-btn"
              onClick={() => setSelectedCard(null)}
            >
              ‚úï Close
            </button>
          </div>
          {(() => {
            const card = cards.find(c => c.id === selectedCard);
            return (
              <PropertyCard
                property={card.property}
                comparisonData={card.comparisonData}
                activeMapTab="google"
                onMapTabChange={() => {}}
                mapId="howitworks-map"
                showVS={false}
                amenities={[]}
                selectedAmenityTypes={[]}
                onToggleAmenity={() => {}}
                isLoadingAmenities={false}
                amenitiesError=""
                amenityOptions={[]}
              />
            );
          })()}
        </section>
      )}

      <Footer />
    </div>
  );
};

export default HowItWorks;
