import React, { useState, useEffect } from 'react';
import { useApi } from '../../context/ApiContext';
import { authAPI } from '../../services/api';
import Header from '../sharedpages/header';
import Navbar from '../sharedpages/navbar';
import Footer from '../sharedpages/footer';
import './SysAdmin.css';

const SysAdmin = () => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const { getUserName } = useApi();

  // State for real data
  const [metricsData, setMetricsData] = useState(null);
  const [userAccounts, setUserAccounts] = useState([]);
  const [recentFeedback, setRecentFeedback] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch real data from backend
  useEffect(() => {
    const fetchAdminData = async () => {
      try {
        setLoading(true);
        setError(null);

        // Fetch metrics
        const metricsResponse = await authAPI.getAdminMetrics();
        setMetricsData(metricsResponse);

        // Fetch users
        const usersResponse = await authAPI.getAllUsers(1, 5); // First 5 users
        setUserAccounts(usersResponse.users || []);

        // Fetch feedback
        const feedbackResponse = await authAPI.getAllFeedback(1, 3); // First 3 feedback items
        setRecentFeedback(feedbackResponse.feedback || []);

      } catch (err) {
        console.error('Error fetching admin data:', err);
        setError(err.message || 'Failed to fetch admin data');
      } finally {
        setLoading(false);
      }
    };

    fetchAdminData();
  }, []);





  // Mock data for content management
  const contentItems = [
    {
      id: 1,
      title: 'Landing Page',
      icon: 'ðŸ ',
      description: 'Manage main landing page content'
    },
    {
      id: 2,
      title: 'Contact',
      icon: 'âœ‰ï¸',
      description: 'Update contact information'
    },
    {
      id: 3,
      title: 'FAQ',
      icon: 'â“',
      description: 'Manage frequently asked questions'
    },
    {
      id: 4,
      title: 'Disclaimer',
      icon: 'ðŸ“„',
      description: 'Update legal disclaimers'
    },
    {
      id: 5,
      title: 'Legal Terms',
      icon: 'ðŸ“‹',
      description: 'Manage terms of service and privacy policy'
    }
  ];

  const handleContentItemClick = (item) => {
    console.log('Content item clicked:', item.title);
    // Navigate to specific content management pages based on item
    switch (item.title) {
      case 'Landing Page':
        window.location.href = '/dashboard/edit-hero-section';
        break;
      case 'Contact':
        window.location.href = '/dashboard/edit-contact-section';
        break;
      case 'FAQ':
        window.location.href = '/dashboard/edit-faq-section';
        break;
      case 'Disclaimer':
        window.location.href = '/dashboard/edit-disclaimer';
        break;
      case 'Legal Terms':
        window.location.href = '/dashboard/edit-legal-terms';
        break;
      default:
        window.location.href = '/dashboard/content-management';
    }
  };

  const handleViewMoreAccounts = () => {
    window.location.href = '/dashboard/user-accounts';
  };

  const handleViewMoreFeedback = () => {
    window.location.href = '/dashboard/feedback-management';
  };

  const handleViewMoreContent = () => {
    window.location.href = '/dashboard/content-management';
  };

  const getStatusBadgeClass = (status) => {
    return status === 'Active' ? 'sys-admin-status-badge active' : 'sys-admin-status-badge inactive';
  };

  return (
    <div className="user-dashboard">
      <Header />
      <div className="user-dashboard-container">
        <Navbar activeTab={activeTab} setActiveTab={setActiveTab} />
        <main className="user-main-content">
          <div className="user-content-header">
            <h1 className="user-main-title">System Admin's Dashboard</h1>
            <p className="user-welcome-message">Welcome back, {getUserName()}</p>
          </div>

          <div className="sys-admin-dashboard-content">
            {/* Key Metrics Section */}
            <section className="sys-admin-metrics-section">
              <div className="sys-admin-metrics-grid">
                {metricsData.map((metric, index) => (
                  <div key={index} className="sys-admin-metric-card">
                    <h3 className="sys-admin-metric-title">{metric.title}</h3>
                    <div className="sys-admin-metric-value">{metric.value}</div>
                    <div className={`sys-admin-metric-change ${metric.changeType}`}>
                      {metric.change}
                    </div>
                  </div>
                ))}
              </div>
            </section>

            {/* User Account List Section */}
            <section className="sys-admin-user-accounts-section">
              <div className="sys-admin-section-header">
                <h2 className="sys-admin-section-title">User Account List</h2>
                <button 
                  className="sys-admin-view-more-btn"
                  onClick={handleViewMoreAccounts}
                >
                  View More Accounts
                </button>
              </div>
              <div className="sys-admin-table-container">
                <table className="sys-admin-data-table">
                  <thead>
                    <tr>
                      <th>Name</th>
                      <th>Email</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {userAccounts.map((user) => (
                      <tr key={user.id}>
                        <td className="sys-admin-user-name">{user.name}</td>
                        <td className="sys-admin-user-email">{user.email}</td>
                        <td className="sys-admin-user-status">
                          <span className={getStatusBadgeClass(user.status)}>
                            {user.status}
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </section>

            {/* Recent User Feedback Section */}
            <section className="sys-admin-feedback-section">
              <div className="sys-admin-section-header">
                <h2 className="sys-admin-section-title">Recent User Feedback</h2>
                <button 
                  className="sys-admin-view-more-btn"
                  onClick={handleViewMoreFeedback}
                >
                  View More Feedback
                </button>
              </div>
              <div className="sys-admin-feedback-list">
                {recentFeedback.map((feedback) => (
                  <div key={feedback.id} className="sys-admin-feedback-item">
                    <p className="sys-admin-feedback-text">"{feedback.feedback}"</p>
                    <span className="sys-admin-feedback-user">User: {feedback.user}</span>
                  </div>
                ))}
              </div>
            </section>

            {/* Content Management Section */}
            <section className="sys-admin-content-management-section">
              <div className="sys-admin-section-header">
                <h2 className="sys-admin-section-title">Content Management</h2>
                <button 
                  className="sys-admin-view-more-btn"
                  onClick={handleViewMoreContent}
                >
                  View More Content
                </button>
              </div>
              <div className="sys-admin-content-grid">
                {contentItems.map((item) => (
                  <div 
                    key={item.id} 
                    className="sys-admin-content-item"
                    onClick={() => handleContentItemClick(item)}
                  >
                    <div className="sys-admin-content-icon">{item.icon}</div>
                    <h3 className="sys-admin-content-title">{item.title}</h3>
                    <p className="sys-admin-content-description">{item.description}</p>
                  </div>
                ))}
              </div>
            </section>
          </div>
        </main>
      </div>
      <Footer />
    </div>
  );
};

export default SysAdmin;
