import React, { useState } from 'react';
import Header from './header';
import Navbar from './navbar';
import Footer from './footer';
import './profile.css';

const Profile = () => {
  const [activeTab, setActiveTab] = useState('profile');
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phoneNumber: ''
  });
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  // Get user type from localStorage
  const userType = localStorage.getItem('userType') || 'free';

  const navigationItems = [
    { id: 'dashboard', label: 'Dashboard', icon: 'üè†' },
    { id: 'price-prediction', label: 'Price Prediction', icon: 'üìà' },
    ...(userType === 'premium' ? [{ id: 'compare-predictions', label: 'Compare Predictions', icon: 'üîÑ' }] : []),
    { id: 'bookmarks', label: 'Bookmarks', icon: 'üîñ' },
    { id: 'support', label: 'Support', icon: '‚ùì' },
    { id: 'feedback', label: 'Feedback', icon: 'üí¨' },
    { id: 'profile', label: 'Profile', icon: 'üë§' }
  ];

  const handleProfileClick = () => {
    setShowProfileDropdown(!showProfileDropdown);
  };

  const handleProfilePage = () => {
    setShowProfileDropdown(false);
    navigate('/profile');
  };

  const handleLogout = () => {
    setShowProfileDropdown(false);
    localStorage.removeItem('isLoggedIn');
    localStorage.removeItem('userType');
    localStorage.removeItem('userPreferences');
    navigate('/login');
  };

  const handleClickOutside = (e) => {
    if (!e.target.closest('.user-profile')) {
      setShowProfileDropdown(false);
    }
  };

  const handleNavigation = (itemId) => {
    setActiveTab(itemId);

    switch (itemId) {
      case 'dashboard':
        navigate('/dashboard/free');
        break;
      case 'price-prediction':
        navigate('/dashboard/priceprediction');
        break;
      case 'compare-predictions':
        navigate('/compare-predictions');
        break;
      case 'bookmarks':
        navigate('/bookmarks');
        break;
      case 'support':
        navigate('/support');
        break;
      case 'feedback':
        navigate('/feedback');
        break;
      case 'profile':
        navigate('/profile');
        break;
      default:
        break;
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handlePasswordChange = (e) => {
    const { name, value } = e.target;
    setPasswordData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleChangePassword = () => {
    if (passwordData.newPassword !== passwordData.confirmPassword) {
      alert('New passwords do not match!');
      return;
    }
    if (passwordData.newPassword.length < 6) {
      alert('New password must be at least 6 characters long!');
      return;
    }

    // Here you would typically send the password change request to the backend
    console.log('Changing password:', passwordData);
    alert('Password changed successfully!');

    // Reset password form
    setPasswordData({
      currentPassword: '',
      newPassword: '',
      confirmPassword: ''
    });
  };

  const handleSaveChanges = () => {
    setIsLoading(true);

    // Simulate API call
    setTimeout(() => {
      // Here you would typically send the form data to the backend
      console.log('Saving profile changes:', formData);
      alert('Profile updated successfully!');
      setIsLoading(false);
    }, 1000);
  };

  const handleCancel = () => {
    // Reset form to original values
    setFormData({
      fullName: '',
      email: '',
      phoneNumber: ''
    });
  };

  const handleManageSubscription = () => {
    // Navigate to subscription management page
    alert('Redirecting to subscription management...');
  };

  const handleDeactivateAccount = () => {
    const confirmed = window.confirm(
      'Are you sure you want to deactivate your account? This action cannot be undone.'
    );

    if (confirmed) {
      // Here you would typically send the deactivation request to the backend
      console.log('Deactivating account...');
      alert('Account deactivation request submitted. You will receive a confirmation email.');
    }
  };

  // Add click listener to close dropdown when clicking outside
  useEffect(() => {
    document.addEventListener('click', handleClickOutside);
    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, []);

  return (
    <div className="profile-dashboard">
      {/* Top Header */}
      <header className="dashboard-header">
        <div className="header-center">
          <div className="logo">
            <span className="logo-icon">üè†</span>
            <span className="logo-text">Valuez</span>
          </div>
        </div>
        <div className="header-right">
          <div className="notification-icon">üîî</div>
          <div className="user-profile">
            <img 
              src="https://images.unsplash.com/photo-1494790108755-2616b612b786?w=40&h=40&fit=crop&crop=face" 
              alt="User Profile" 
              className="profile-image"
              onClick={handleProfileClick}
            />
            {showProfileDropdown && (
              <div className="profile-dropdown">
                <div className="dropdown-item" onClick={handleProfilePage}>
                  <span className="dropdown-icon">üë§</span>
                  <span>Profile</span>
                </div>
                <div className="dropdown-divider"></div>
                <div className="dropdown-item" onClick={handleLogout}>
                  <span className="dropdown-icon">üö™</span>
                  <span>Logout</span>
                </div>
              </div>
            )}
          </div>
        </div>
      </header>

      <div className="dashboard-container">
        {/* Left Sidebar */}
        <aside className="sidebar">
          <nav className="sidebar-nav">
            {navigationItems.map((item) => (
              <button
                key={item.id}
                className={`nav-item ${activeTab === item.id ? 'active' : ''}`}
                onClick={() => handleNavigation(item.id)}
              >
                <span className="nav-icon">{item.icon}</span>
                <span className="nav-label">{item.label}</span>
              </button>
            ))}
            <div className="nav-divider"></div>
            <button className="nav-item logout-item" onClick={handleLogout}>
              <span className="nav-icon">üö™</span>
              <span className="nav-label">Logout</span>
            </button>
          </nav>
        </aside>

        {/* Main Content */}
        <main className="main-content">
          <h1 className="section-title">Profile</h1>

          {/* Personal Information */}
          <section className="profile-section">
            <h2 className="subsection-title">Personal Information</h2>
            <div className="form-group">
              <label htmlFor="fullName">Full Name</label>
              <input
                type="text"
                id="fullName"
                name="fullName"
                value={formData.fullName}
                onChange={handleInputChange}
                placeholder="Enter your full name"
              />
            </div>
            <div className="form-group">
              <label htmlFor="email">Email Address</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                placeholder="Enter your email address"
              />
            </div>
            <div className="form-group">
              <label htmlFor="phoneNumber">Phone Number</label>
              <input
                type="tel"
                id="phoneNumber"
                name="phoneNumber"
                value={formData.phoneNumber}
                onChange={handleInputChange}
                placeholder="Enter your phone number"
              />
            </div>
          </section>

          {/* Change Password */}
          <section className="profile-section">
            <h2 className="subsection-title">Change Password</h2>
            <div className="form-group">
              <label htmlFor="currentPassword">Current Password</label>
              <input
                type="password"
                id="currentPassword"
                name="currentPassword"
                value={passwordData.currentPassword}
                onChange={handlePasswordChange}
                placeholder="Enter current password"
              />
            </div>
            <div className="form-group">
              <label htmlFor="newPassword">New Password</label>
              <input
                type="password"
                id="newPassword"
                name="newPassword"
                value={passwordData.newPassword}
                onChange={handlePasswordChange}
                placeholder="Enter new password"
              />
            </div>
            <div className="form-group">
              <label htmlFor="confirmPassword">Confirm New Password</label>
              <input
                type="password"
                id="confirmPassword"
                name="confirmPassword"
                value={passwordData.confirmPassword}
                onChange={handlePasswordChange}
                placeholder="Confirm new password"
              />
            </div>
            <button 
              className="change-password-btn"
              onClick={handleChangePassword}
            >
              Change Password
            </button>
          </section>

          {/* Account Information */}
          <section className="profile-section">
            <h2 className="subsection-title">Account Information</h2>
            <div className="account-info">
              <div className="info-item">
                <span className="info-label">Account Created:</span>
                <span className="info-value">March 22, 2022</span>
              </div>
            </div>
          </section>

          {/* Subscription */}
          <section className="profile-section">
            <h2 className="subsection-title">Subscription</h2>
            <div className="subscription-info">
              <div className="info-item">
                <span className="info-label">Current Plan:</span>
                <span className="info-value premium">Premium</span>
              </div>
              <p className="subscription-description">
                Enjoy exclusive access to advanced search filters, detailed property reports, and personalized alerts with your Premium plan.
              </p>
              <button 
                className="manage-subscription-btn"
                onClick={handleManageSubscription}
              >
                Manage Subscription
              </button>
            </div>
          </section>

          {/* Account Settings */}
          <section className="profile-section">
            <h2 className="subsection-title">Account Settings</h2>
            <button 
              className="deactivate-account-btn"
              onClick={handleDeactivateAccount}
            >
              Deactivate Account
            </button>
          </section>

          {/* Action Buttons */}
          <div className="action-buttons">
            <button 
              className="cancel-btn"
              onClick={handleCancel}
            >
              Cancel
            </button>
            <button 
              className="save-btn"
              onClick={handleSaveChanges}
              disabled={isLoading}
            >
              {isLoading ? 'Saving...' : 'Save Changes'}
            </button>
          </div>
        </main>
      </div>

      {/* Footer */}
      <footer className="dashboard-footer">
        <div className="footer-left">
          <a href="/terms" className="footer-link">Terms</a>
          <a href="/privacy" className="footer-link">Privacy</a>
          <a href="/contact" className="footer-link">Contact</a>
          <a href="/disclaimer" className="footer-link">Disclaimer</a>
        </div>
        <div className="footer-center">
          <span className="copyright">¬© 2024 Valuez. All rights reserved.</span>
        </div>
      </footer>
    </div>
  );
};

export default Profile;
