import React, { useState } from 'react';
import Header from '../sharedpages/header';
import Navbar from '../sharedpages/navbar';
import Footer from '../sharedpages/footer';
import './AddStep.css';

const AddStep = () => {
  const [activeTab, setActiveTab] = useState('content');
  const [formData, setFormData] = useState({
    stepNumber: '',
    title: '',
    description: '',
    image: null,
    imagePreview: null
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData(prev => ({
        ...prev,
        image: file
      }));

      const reader = new FileReader();
      reader.onload = (e) => {
        setFormData(prev => ({
          ...prev,
          imagePreview: e.target.result
        }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSaveStep = () => {
    console.log('Saving new step:', formData);
    // Add save logic here
    alert('Step added successfully!');
    // Navigate back to edit how it works
    window.location.href = '/dashboard/edit-how-it-works';
  };

  const handleCancel = () => {
    console.log('Canceling step addition');
    // Navigate back to edit how it works
    window.location.href = '/dashboard/edit-how-it-works';
  };

  const handleBack = () => {
    // Navigate back to edit how it works
    window.location.href = '/dashboard/edit-how-it-works';
  };

  return (
    <div className="add-step-page">
      <Header />
      <div className="dashboard-container">
        <Navbar activeTab={activeTab} setActiveTab={setActiveTab} />
        <main className="main-content">
          {/* Breadcrumb */}
          <div className="breadcrumb">
            Content Management / How It Works Section / Add Step
          </div>

          {/* Page Title and Back Button */}
          <div className="page-header">
            <h1 className="page-title">Add New Step</h1>
            <button 
              className="back-btn"
              onClick={handleBack}
            >
              Back
            </button>
          </div>

          {/* Form */}
          <div className="add-form">
            <div className="form-group">
              <label htmlFor="stepNumber" className="form-label">Step Number</label>
              <input
                type="number"
                id="stepNumber"
                name="stepNumber"
                value={formData.stepNumber}
                onChange={handleInputChange}
                className="form-input"
                placeholder="Enter step number"
                min="1"
              />
            </div>

            <div className="form-group">
              <label htmlFor="title" className="form-label">Step Title</label>
              <input
                type="text"
                id="title"
                name="title"
                value={formData.title}
                onChange={handleInputChange}
                className="form-input"
                placeholder="Enter step title"
              />
            </div>

            <div className="form-group">
              <label htmlFor="description" className="form-label">Step Description</label>
              <textarea
                id="description"
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                className="form-textarea"
                placeholder="Enter step description"
                rows="4"
              />
            </div>

            <div className="form-group">
              <label htmlFor="image" className="form-label">Step Image</label>
              <div className="image-upload-section">
                {formData.imagePreview && (
                  <div className="image-preview">
                    <img
                      src={formData.imagePreview}
                      alt="Step preview"
                      className="preview-image"
                    />
                  </div>
                )}
                <input
                  type="file"
                  id="image"
                  name="image"
                  onChange={handleImageUpload}
                  className="file-input"
                  accept="image/*"
                />
                <label htmlFor="image" className="upload-btn">
                  {formData.imagePreview ? 'Change Image' : 'Upload Image'}
                </label>
              </div>
            </div>

            {/* Action Buttons */}
            <div className="action-buttons">
              <button
                type="button"
                className="cancel-btn"
                onClick={handleCancel}
              >
                Cancel
              </button>
              <button
                type="button"
                className="save-btn"
                onClick={handleSaveStep}
              >
                Add Step
              </button>
            </div>
          </div>
        </main>
      </div>
      <Footer />
    </div>
  );
};

export default AddStep;
