import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../sharedpages/header.js';
import Navbar from '../sharedpages/navbar.js';
import Footer from '../sharedpages/footer.js';
import './UserAccount.css';

const UserAccount = () => {
  console.log('UserAccount component rendered');
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('user-accounts');
  const [searchQuery, setSearchQuery] = useState('');
  const [currentPage, setCurrentPage] = useState(1);

  // Mock data for user accounts
  const userAccounts = [
    {
      id: 1,
      name: 'Ethan Harper',
      email: 'ethan.harper@example.com',
      referralCode: 'REF123',
      subscriptionStatus: 'Premium',
      accountCreatedDate: '2023-01-15',
      accountStatus: 'Active'
    },
    {
      id: 2,
      name: 'Olivia Bennett',
      email: 'olivia.bennett@example.com',
      referralCode: 'REF456',
      subscriptionStatus: 'Free',
      accountCreatedDate: '2023-02-20',
      accountStatus: 'Active'
    },
    {
      id: 3,
      name: 'Liam Carter',
      email: 'liam.carter@example.com',
      referralCode: 'REF789',
      subscriptionStatus: 'Agent',
      accountCreatedDate: '2023-03-10',
      accountStatus: 'Active'
    },
    {
      id: 4,
      name: 'Sophia Davis',
      email: 'sophia.davis@example.com',
      referralCode: 'REF101',
      subscriptionStatus: 'Premium',
      accountCreatedDate: '2023-04-05',
      accountStatus: 'Active'
    },
    {
      id: 5,
      name: 'Noah Evans',
      email: 'noah.evans@example.com',
      referralCode: 'REF112',
      subscriptionStatus: 'Free',
      accountCreatedDate: '2023-05-12',
      accountStatus: 'Active'
    },
    {
      id: 6,
      name: 'Ava Foster',
      email: 'ava.foster@example.com',
      referralCode: 'REF131',
      subscriptionStatus: 'Agent',
      accountCreatedDate: '2023-06-18',
      accountStatus: 'Active'
    },
    {
      id: 7,
      name: 'Jackson Gray',
      email: 'jackson.gray@example.com',
      referralCode: 'REF141',
      subscriptionStatus: 'Premium',
      accountCreatedDate: '2023-07-22',
      accountStatus: 'Active'
    },
    {
      id: 8,
      name: 'Isabella Hayes',
      email: 'isabella.hayes@example.com',
      referralCode: 'REF159',
      subscriptionStatus: 'Free',
      accountCreatedDate: '2023-08-30',
      accountStatus: 'Active'
    },
    {
      id: 9,
      name: 'Lucas Ingram',
      email: 'lucas.ingram@example.com',
      referralCode: 'REF167',
      subscriptionStatus: 'Agent',
      accountCreatedDate: '2023-09-15',
      accountStatus: 'Active'
    },
    {
      id: 10,
      name: 'Mia Jenkins',
      email: 'mia.jenkins@example.com',
      referralCode: 'REF175',
      subscriptionStatus: 'Premium',
      accountCreatedDate: '2023-10-20',
      accountStatus: 'Active'
    }
  ];

  // Filter users based on search query
  const filteredUsers = userAccounts.filter(user =>
    user.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    user.email.toLowerCase().includes(searchQuery.toLowerCase()) ||
    user.referralCode.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const handleSearch = (e) => {
    setSearchQuery(e.target.value);
    setCurrentPage(1); // Reset to first page when searching
  };

  const handleUserClick = (userId) => {
    console.log('User clicked:', userId);
    navigate(`/dashboard/user-details/${userId}`);
  };

  const getSubscriptionBadgeClass = (status) => {
    switch (status.toLowerCase()) {
      case 'premium':
        return 'user-account-subscription-badge premium';
      case 'free':
        return 'user-account-subscription-badge free';
      case 'agent':
        return 'user-account-subscription-badge agent';
      default:
        return 'user-account-subscription-badge';
    }
  };

  const getAccountStatusBadgeClass = (status) => {
    return status === 'Active' ? 'user-account-account-status-badge active' : 'user-account-account-status-badge inactive';
  };

  return (
    <div className="user-dashboard">
      <Header />
      <div className="user-dashboard-container">
        <Navbar activeTab={activeTab} setActiveTab={setActiveTab} />
        <main className="user-main-content">
          <div className="user-content-header">
            <h1 className="user-main-title">User Accounts</h1>
            <p className="user-account-page-description">Manage user accounts and their subscription status.</p>
          </div>

          <div className="user-account-content">
            {/* Search Section */}
            <div className="user-account-search-section">
              <div className="user-account-search-container">
                <span className="user-account-search-icon">üîç</span>
                <input
                  type="text"
                  className="user-account-search-input"
                  placeholder="Search by name, email, or referral code"
                  value={searchQuery}
                  onChange={handleSearch}
                />
              </div>
            </div>

            {/* Users Table */}
            <div className="user-account-table-container">
              <table className="user-account-users-table">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Referral Code</th>
                    <th>Subscription Status</th>
                    <th>Account Created Date</th>
                    <th>Account Status</th>
                    <th></th>
                  </tr>
                </thead>
                <tbody>
                  {filteredUsers.map((user) => (
                    <tr key={user.id} className="user-account-user-row" onClick={() => handleUserClick(user.id)}>
                      <td className="user-account-user-name">{user.name}</td>
                      <td className="user-account-user-email">{user.email}</td>
                      <td className="user-account-referral-code">{user.referralCode}</td>
                      <td className="subscription-status">
                        <span className={getSubscriptionBadgeClass(user.subscriptionStatus)}>
                          {user.subscriptionStatus}
                        </span>
                      </td>
                      <td className="user-account-account-created-date">{user.accountCreatedDate}</td>
                      <td className="account-status">
                        <span className={getAccountStatusBadgeClass(user.accountStatus)}>
                          {user.accountStatus}
                        </span>
                      </td>
                      <td className="user-account-action-arrow">
                        <span className="user-account-arrow-icon">‚Üí</span>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            {/* Pagination */}
            <div className="user-account-pagination-container">
              <div className="user-account-pagination">
                <button className="user-account-pagination-arrow">‚Üê</button>
                <button className="user-account-pagination-page active">1</button>
                <button className="user-account-pagination-page">2</button>
                <button className="user-account-pagination-page">3</button>
                <button className="user-account-pagination-page">4</button>
                <button className="user-account-pagination-page">5</button>
                <button className="user-account-pagination-arrow">‚Üí</button>
              </div>
            </div>
          </div>
        </main>
      </div>
      <Footer />
    </div>
  );
};

export default UserAccount;
