import React, { useState } from 'react';
import Header from '../sharedpages/header';
import Navbar from '../sharedpages/navbar';
import Footer from '../sharedpages/footer';
import './ContentManagement.css';

const ContentManagement = () => {
  const [activeTab, setActiveTab] = useState('content');
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [currentPage, setCurrentPage] = useState(1);

  // Mock data for content items
  const contentItems = [
    {
      id: 1,
      title: 'How to Use Property Search',
      category: 'Help Guide',
      author: 'Admin Team',
      status: 'Published',
      lastModified: '2024-01-15',
      views: 1250,
      priority: 'High'
    },
    {
      id: 2,
      title: 'New Features Announcement',
      category: 'Announcement',
      author: 'Admin Team',
      status: 'Draft',
      lastModified: '2024-01-14',
      views: 0,
      priority: 'Medium'
    },
    {
      id: 3,
      title: 'Property Market Trends Q4 2023',
      category: 'Article',
      author: 'Admin Team',
      status: 'Published',
      lastModified: '2024-01-10',
      views: 890,
      priority: 'Medium'
    },
    {
      id: 4,
      title: 'User Guide: Price Prediction Tool',
      category: 'Help Guide',
      author: 'Admin Team',
      status: 'Published',
      lastModified: '2024-01-08',
      views: 2100,
      priority: 'High'
    },
    {
      id: 5,
      title: 'Platform Maintenance Notice',
      category: 'Announcement',
      author: 'Admin Team',
      status: 'Scheduled',
      lastModified: '2024-01-12',
      views: 0,
      priority: 'High'
    },
    {
      id: 6,
      title: 'Investment Tips for First-Time Buyers',
      category: 'Article',
      author: 'Admin Team',
      status: 'Draft',
      lastModified: '2024-01-13',
      views: 0,
      priority: 'Low'
    }
  ];

  // Filter content based on search query and category
  const filteredContent = contentItems.filter(item => {
    const matchesSearch = item.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         item.author.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesCategory = selectedCategory === 'all' || item.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  const handleSearch = (e) => {
    setSearchQuery(e.target.value);
    setCurrentPage(1);
  };

  const handleCategoryChange = (e) => {
    setSelectedCategory(e.target.value);
    setCurrentPage(1);
  };

  const handleAddNewContent = () => {
    console.log('Add new content');
    // Navigate to add content page
    window.location.href = '/dashboard/add-content';
  };

  const handleEditContent = (contentId) => {
    console.log('Edit content:', contentId);
    // Navigate to edit content page
    window.location.href = `/dashboard/edit-content/${contentId}`;
  };

  const handleDeleteContent = (contentId) => {
    console.log('Delete content:', contentId);
    // Add delete confirmation logic here
    if (window.confirm('Are you sure you want to delete this content item?')) {
      console.log('Content deleted:', contentId);
    }
  };

  const handleStatusChange = (contentId, newStatus) => {
    console.log('Status changed for content:', contentId, 'to:', newStatus);
    // Add status update logic here
  };

  const getStatusBadgeClass = (status) => {
    switch (status.toLowerCase()) {
      case 'published':
        return 'status-badge published';
      case 'draft':
        return 'status-badge draft';
      case 'scheduled':
        return 'status-badge scheduled';
      case 'archived':
        return 'status-badge archived';
      default:
        return 'status-badge';
    }
  };

  const getPriorityBadgeClass = (priority) => {
    switch (priority.toLowerCase()) {
      case 'high':
        return 'priority-badge high';
      case 'medium':
        return 'priority-badge medium';
      case 'low':
        return 'priority-badge low';
      default:
        return 'priority-badge';
    }
  };

  const getCategoryIcon = (category) => {
    switch (category) {
      case 'Help Guide':
        return 'üìö';
      case 'Announcement':
        return 'üì¢';
      case 'Article':
        return 'üìÑ';
      default:
        return 'üìù';
    }
  };

  return (
    <div className="content-management-page">
      <Header />
      <div className="dashboard-container">
        <Navbar activeTab={activeTab} setActiveTab={setActiveTab} />
        <main className="main-content">
          <div className="content-header">
            <h1 className="main-title">Content Management</h1>
            <p className="page-description">Manage platform content, articles, announcements, and help guides.</p>
          </div>
          
          <div className="content-management-content">
            {/* Content Statistics Section */}
            <section className="content-stats-section">
              <div className="stats-grid">
                <div className="stat-card">
                  <div className="stat-icon">üìö</div>
                  <div className="stat-value">{contentItems.length}</div>
                  <div className="stat-label">Total Content Items</div>
                </div>
                <div className="stat-card">
                  <div className="stat-icon">‚úÖ</div>
                  <div className="stat-value">{contentItems.filter(item => item.status === 'Published').length}</div>
                  <div className="stat-label">Published</div>
                </div>
                <div className="stat-card">
                  <div className="stat-icon">üìù</div>
                  <div className="stat-value">{contentItems.filter(item => item.status === 'Draft').length}</div>
                  <div className="stat-label">Drafts</div>
                </div>
                <div className="stat-card">
                  <div className="stat-icon">üëÅÔ∏è</div>
                  <div className="stat-value">{contentItems.reduce((total, item) => total + item.views, 0)}</div>
                  <div className="stat-label">Total Views</div>
                </div>
              </div>
            </section>

            {/* Controls Section */}
            <section className="controls-section">
              <div className="controls-left">
                <div className="search-container">
                  <span className="search-icon">üîç</span>
                  <input
                    type="text"
                    className="search-input"
                    placeholder="Search content by title or author"
                    value={searchQuery}
                    onChange={handleSearch}
                  />
                </div>
                <select
                  className="category-filter"
                  value={selectedCategory}
                  onChange={handleCategoryChange}
                >
                  <option value="all">All Categories</option>
                  <option value="Help Guide">Help Guide</option>
                  <option value="Announcement">Announcement</option>
                  <option value="Article">Article</option>
                </select>
              </div>
              <div className="controls-right">
                <button 
                  className="add-content-btn"
                  onClick={handleAddNewContent}
                >
                  + Add New Content
                </button>
              </div>
            </section>

            {/* Content List Section */}
            <section className="content-list-section">
              <div className="table-container">
                <table className="content-table">
                  <thead>
                    <tr>
                      <th>Content</th>
                      <th>Category</th>
                      <th>Author</th>
                      <th>Status</th>
                      <th>Priority</th>
                      <th>Last Modified</th>
                      <th>Views</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredContent.map((item) => (
                      <tr key={item.id} className="content-row">
                        <td className="content-title">
                          <div className="content-info">
                            <span className="category-icon">{getCategoryIcon(item.category)}</span>
                            <div>
                              <div className="title-text">{item.title}</div>
                              <div className="content-id">ID: {item.id}</div>
                            </div>
                          </div>
                        </td>
                        <td className="content-category">
                          <span className="category-badge">{item.category}</span>
                        </td>
                        <td className="content-author">{item.author}</td>
                        <td className="content-status">
                          <span className={getStatusBadgeClass(item.status)}>
                            {item.status}
                          </span>
                        </td>
                        <td className="content-priority">
                          <span className={getPriorityBadgeClass(item.priority)}>
                            {item.priority}
                          </span>
                        </td>
                        <td className="last-modified">{item.lastModified}</td>
                        <td className="content-views">{item.views.toLocaleString()}</td>
                        <td className="content-actions">
                          <button 
                            className="action-btn edit-btn"
                            onClick={() => handleEditContent(item.id)}
                          >
                            Edit
                          </button>
                          <button 
                            className="action-btn delete-btn"
                            onClick={() => handleDeleteContent(item.id)}
                          >
                            Delete
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>

              {/* Pagination */}
              <div className="pagination-container">
                <div className="pagination">
                  <button className="pagination-arrow">‚Üê</button>
                  <button className="pagination-page active">1</button>
                  <button className="pagination-page">2</button>
                  <button className="pagination-page">3</button>
                  <button className="pagination-arrow">‚Üí</button>
                </div>
              </div>
            </section>
          </div>
        </main>
      </div>
      <Footer />
    </div>
  );
};

export default ContentManagement;
