import React, { useState, useEffect } from 'react';
import './Navbar.css';

const Navbar = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  // Function to toggle mobile menu
  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  // Function to close mobile menu when clicking on a link
  const closeMobileMenu = () => {
    setIsMobileMenuOpen(false);
  };

  // Close mobile menu when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (isMobileMenuOpen && !event.target.closest('.navbar-container')) {
        setIsMobileMenuOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isMobileMenuOpen]);

  return (
    <nav className="navbar">
      <div className="navbar-container">
        {/* Logo */}
        <div className="navbar-logo">
          <img src="http://localhost:3845/assets/0b020b3c596ade443a0b22760a015f73ecdd10df.png" alt="valuez logo" className="logo-img" />
          <span className="logo-text">valuez</span>
        </div>

        {/* Mobile menu toggle */}
        <button 
          className="mobile-menu-toggle"
          onClick={toggleMobileMenu}
          aria-label="Toggle mobile menu"
        >
          <span className={`hamburger ${isMobileMenuOpen ? 'open' : ''}`}></span>
        </button>

        {/* Desktop navigation menu */}
        <ul className={`navbar-menu ${isMobileMenuOpen ? 'mobile-open' : ''}`}>
          <li><a href="#home" onClick={closeMobileMenu}>Home</a></li>
          <li><a href="#intro" onClick={closeMobileMenu}>Introduction</a></li>
          <li><a href="/how-it-works" onClick={closeMobileMenu}>See How it Works</a></li>
          <li><a href="#plans" onClick={closeMobileMenu}>Subscription Plans</a></li>
          <li><a href="#faq" onClick={closeMobileMenu}>FAQ</a></li>
          <li><a href="#reviews" onClick={closeMobileMenu}>Reviews</a></li>
        </ul>

        {/* Login/Register button */}
        <div className="navbar-actions">
          <a href="/login" className="btn btn-primary">Login / Register</a>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
