import React, { useState } from 'react';
import './FAQ.css';

const FAQ = () => {
  const [activeIndex, setActiveIndex] = useState(0);

  const faqData = [
    {
      question: "What is Valuez and how does it work?",
      answer: "Valuez is an AI-powered property insights platform that provides accurate property valuations and market analysis. Our advanced algorithms analyze market data, property characteristics, and location factors to deliver precise property value predictions. Simply enter a property address, and our system will provide you with comprehensive insights including predicted value, market trends, and comparable properties."
    },
    {
      question: "How accurate are your property predictions?",
      answer: "Our property predictions are highly accurate, with an average accuracy rate of 95% when compared to actual sale prices. We use machine learning algorithms trained on extensive market data, including recent sales, property characteristics, and market trends. Our models are continuously updated to reflect current market conditions and improve accuracy over time."
    },
    {
      question: "What types of properties can I analyze?",
      answer: "Valuez supports analysis of various property types including residential properties (HDB flats, condominiums, landed houses), commercial properties (office spaces, retail units, industrial facilities), and mixed-use developments. Our platform covers properties across Singapore with comprehensive data for different market segments."
    },
    {
      question: "Do you provide market analysis reports?",
      answer: "Yes, we offer comprehensive market analysis reports that include market trends, price movements, supply and demand analysis, and future projections. These reports are available for different property types and locations, helping you make informed investment decisions."
    },
    {
      question: "Can I export data for my own analysis?",
      answer: "Premium users can export data in various formats including CSV, Excel, and PDF. This includes property valuations, market reports, and comparative analysis data. The exported data can be used for further analysis, presentations, or integration with other tools."
    },
    {
      question: "Is my data secure and private?",
      answer: "Absolutely. We take data security and privacy very seriously. All data is encrypted, stored securely, and we comply with Singapore's data protection regulations. We never share your personal information or search history with third parties without your explicit consent."
    }
  ];

  const toggleFAQ = (index) => {
    setActiveIndex(activeIndex === index ? -1 : index);
  };

  return (
    <section id="faq" className="faq-section">
      <div className="faq-container">
        <div className="faq-header">
          <h2 className="faq-title">Frequently Asked Questions</h2>
          <p className="faq-subtitle">Find answers to common questions about our platform and services.</p>
        </div>

        <div className="faq-content">
          <div className="faq-accordion">
            {faqData.map((faq, index) => (
              <div 
                key={index} 
                className={`faq-item ${activeIndex === index ? 'active' : ''}`}
              >
                <button 
                  className="faq-question"
                  onClick={() => toggleFAQ(index)}
                  aria-expanded={activeIndex === index}
                >
                  <span className="question-text">{faq.question}</span>
                  <span className="faq-icon">
                    {activeIndex === index ? (
                      <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
                        <path d="M18 15l-6-6-6 6" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                      </svg>
                    ) : (
                      <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
                        <path d="M6 9l6 6 6-6" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                      </svg>
                    )}
                  </span>
                </button>
                <div className="faq-answer">
                  <div className="answer-content">
                    {faq.answer}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default FAQ;
