import React, { useState } from 'react';
import './Reviews.css';

const Reviews = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const testimonials = [
    {
      id: 1,
      quote: "Valuez has transformed how I approach property investments. The insights are incredibly accurate and the platform is so easy to use.",
      rating: 5,
      name: "Sarah Chen",
      title: "Property Investor",
      avatar: "https://images.unsplash.com/photo-1494790108755-2616b612b786?w=150&h=150&fit=crop&crop=face"
    },
    {
      id: 2,
      quote: "As a real estate agent, this tool has been invaluable for providing accurate valuations to my clients. Highly recommended!",
      rating: 5,
      name: "Michael Rodriguez",
      title: "Real Estate Agent",
      avatar: "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=150&h=150&fit=crop&crop=face"
    },
    {
      id: 3,
      quote: "The data-driven approach gives me confidence in my property decisions. The market analysis features are outstanding.",
      rating: 5,
      name: "Emily Watson",
      title: "Property Developer",
      avatar: "https://images.unsplash.com/photo-1438761681033-6461ffad8d80?w=150&h=150&fit=crop&crop=face"
    },
    {
      id: 4,
      quote: "Finally, a platform that combines AI accuracy with user-friendly design. It's become my go-to tool for property research.",
      rating: 5,
      name: "David Kim",
      title: "Property Consultant",
      avatar: "https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=150&h=150&fit=crop&crop=face"
    }
  ];

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % testimonials.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + testimonials.length) % testimonials.length);
  };

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  return (
    <section id="reviews" className="reviews-section">
      <div className="reviews-container">
        <div className="reviews-header">
          <h2 className="reviews-title">People Love Building With Valuez</h2>
          <p className="reviews-subtitle">Hear what our satisfied clients have to say about their experience.</p>
        </div>

        <div className="reviews-content">
          <div className="reviews-carousel">
            <div className="reviews-grid">
              {testimonials.map((testimonial, index) => (
                <div 
                  key={testimonial.id} 
                  className={`review-card ${index === currentSlide ? 'active' : ''}`}
                >
                  <div className="review-content">
                    <div className="review-quote">
                      "{testimonial.quote}"
                    </div>
                    <div className="review-rating">
                      {[...Array(testimonial.rating)].map((_, i) => (
                        <span key={i} className="star">â˜…</span>
                      ))}
                    </div>
                    <div className="review-author">
                      <img 
                        src={testimonial.avatar} 
                        alt={testimonial.name} 
                        className="review-avatar"
                      />
                      <div className="review-author-info">
                        <div className="review-author-name">{testimonial.name}</div>
                        <div className="review-author-title">{testimonial.title}</div>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Navigation Dots */}
          <div className="reviews-dots">
            {testimonials.map((_, index) => (
              <button
                key={index}
                className={`dot ${index === currentSlide ? 'active' : ''}`}
                onClick={() => goToSlide(index)}
                aria-label={`Go to slide ${index + 1}`}
              />
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Reviews;
