            </div>
            <div className="feature-item">
              <span className="checkmark"></span>
              <span>Unlimited Users Team</span>
            </div>
            <div className="feature-item">
              <span className="checkmark"></span>
              <span>Advanced Admin</span>
            </div>
            <div className="feature-item">
              <span className="checkmark"></span>
              <span>Custom Data Retention</span>
            </div>
          </div>

          <button className="choose-plan-btn">Choose plan</button>
        </div>

        <div 
          className={`plan-card ${formData.userType === 'agent' ? 'selected' : ''}`}
          onClick={() => setFormData(prev => ({ ...prev, userType: 'agent' }))}
        >
          <div className="plan-header">
            <h3 className="plan-title">${formData.yearlyBilling ? '90' : '99'}/month</h3>
            <p className="plan-subtitle">Agent</p>
            <p className="plan-description">Automation plus enterprise-grade features.</p>
          </div>

          <div className="plan-features">
            <div className="feature-item">
              <span className="checkmark"></span>
              <span>Multi-step</span>
            </div>
            <div className="feature-item">
              <span className="checkmark"></span>
              <span>Unlimited Premium Apps</span>
            </div>
            <div className="feature-item">
              <span className="checkmark"></span>
              <span>Unlimited Users Team</span>
            </div>
            <div className="feature-item">
              <span className="checkmark"></span>
              <span>Advanced Admin</span>
            </div>
            <div className="feature-item">
              <span className="checkmark"></span>
              <span>Custom Data Retention</span>
            </div>
          </div>

          <button className="choose-plan-btn">Choose plan</button>
        </div>
      </div>

      {errors.userType && <span className="error-message">{errors.userType}</span>}

      <div className="step-buttons">
        <button type="button" className="btn-secondary" onClick={prevStep}>
          Back
        </button>
        <button type="button" className="next-btn" onClick={nextStep}>
          Continue
        </button>
      </div>
    </div>
  );

  const renderStep6 = () => (
    <div className="step-content">
      <div className="form-group">
        <label htmlFor="ceaNumber">CEA Number *</label>
        <div className="input-container">
          <span className="input-icon"></span>
          <input
            type="text"
            id="ceaNumber"
            name="ceaNumber"
            value={formData.ceaNumber}
            onChange={handleChange}
            className={errors.ceaNumber ? 'error' : ''}
            placeholder="Enter your CEA license number"
          />
        </div>
        {errors.ceaNumber && <span className="error-message">{errors.ceaNumber}</span>}
      </div>

      <div className="form-group">
        <label htmlFor="company">Company Name *</label>
        <div className="input-container">
          <span className="input-icon"></span>
          <input
            type="text"
            id="company"
            name="company"
            value={formData.company}
            onChange={handleChange}
            className={errors.company ? 'error' : ''}
            placeholder="Enter your company name"
          />
        </div>
        {errors.company && <span className="error-message">{errors.company}</span>}
      </div>

      <div className="form-group">
        <label htmlFor="workNumber">Work Phone Number *</label>
        <div className="input-container">
          <span className="input-icon"></span>
          <input
            type="tel"
