                    <div className="summary-item">
                      <span>Subtotal:</span>
                      <span>${formData.userType === 'premium' ? '29' : '99'}</span>
                    </div>
                    <div className="summary-item">
                      <span>Tax:</span>
                      <span>$0</span>
                    </div>
                    <div className="summary-divider"></div>
                    <div className="summary-total">
                      <span>Total:</span>
                      <span>${formData.userType === 'premium' ? '29' : '99'}</span>
                    </div>
                  </div>
                </div>
              </div>
