import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useApi } from '../context/ApiContext';
import './Login.css';

const Login = () => {
  const navigate = useNavigate();
  const { login, loading, error, clearError } = useApi();
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newErrors = {};

    // Validation
    if (!formData.email) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (!formData.password) {
      newErrors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      newErrors.password = 'Password must be at least 6 characters';
    }

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    try {
      // Clear any previous errors
      clearError();

      // Attempt real login
      const result = await login(formData);
      console.log('Login successful:', result);

      // Redirect based on user type
      if (result.user_type === 'free' || result.user_type === 'premium') {
        navigate('/dashboard');
      } else if (result.user_type === 'agent') {
        navigate('/dashboard/agent');
      } else if (result.user_type === 'admin') {
        navigate('/dashboard/sysadmin');
      } else {
        navigate('/dashboard');
      }
    } catch (error) {
      console.error('Login failed:', error);
      // Error is already handled by the API context
    }
  };

  return (
    <div className="login-page">
      <div className="login-container">
        {/* Form Panel */}
        <div className="login-form-panel">
          <div className="login-page-title-section">
            <h1 className="login-page-title">Welcome Back</h1>
            <p className="login-section-subtitle">Sign in to your account to continue your journey</p>
          </div>

          <form className="login-form" onSubmit={handleSubmit}>
            <div className="login-input-container">
              <div className="login-input-icon">üìß</div>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                className={errors.email ? 'error' : ''}
                placeholder="Enter your email address"
              />
            </div>
            {errors.email && <span className="error-message">{errors.email}</span>}

            <div className="login-input-container">
              <div className="login-input-icon">üîí</div>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                className={errors.password ? 'error' : ''}
                placeholder="Enter your password"
              />
            </div>
            {errors.password && <span className="error-message">{errors.password}</span>}

            {/* User Type Selector for Testing */}
            <div className="user-type-selector">
              <label className="selector-label">Login as (Testing):</label>
              <div className="user-type-options">
                <label className="user-type-option">
                  <input
                    type="radio"
                    name="userType"
                    value="free"
                    checked={formData.userType === 'free'}
                    onChange={handleChange}
                  />
                  <span className="option-text">Free User</span>
                </label>
                <label className="user-type-option">
                  <input
                    type="radio"
                    name="userType"
                    value="premium"
                    checked={formData.userType === 'premium'}
                    onChange={handleChange}
                  />
                  <span className="option-text">Premium User</span>
                </label>
                <label className="user-type-option">
                  <input
                    type="radio"
                    name="userType"
                    value="agent"
                    checked={formData.userType === 'agent'}
                    onChange={handleChange}
                  />
                  <span className="option-text">Agent User</span>
                </label>
                <label className="user-type-option">
                  <input
                    type="radio"
                    name="userType"
                    value="sysadmin"
                    checked={formData.userType === 'sysadmin'}
                    onChange={handleChange}
                  />
                  <span className="option-text">System Admin</span>
                </label>
              </div>
            </div>

            <div className="form-options">
              <label className="checkbox-container">
                <input type="checkbox" />
                <span className="checkmark"></span>
                Remember me
              </label>
              <a href="/forgot-password" className="forgot-link">Forgot Password?</a>
            </div>

            <button type="submit" className="login-btn">
              Sign In
            </button>
          </form>

          <div className="login-footer">
            <p>
              Don't have an account?{' '}
              <a href="/signup" className="signup-link">Sign up</a>
            </p>
          </div>

          <div className="back-to-home">
            <a href="/" className="back-link">
              ‚Üê Back to Home
            </a>
          </div>
        </div>

        {/* Visual Panel */}
        <div className="visual-panel">
          <div className="cityscape-photo"></div>
        </div>
      </div>
    </div>
  );
};

export default Login;
