import React, { useState } from 'react';
import Header from '../sharedpages/header';
import Navbar from '../sharedpages/navbar';
import Footer from '../sharedpages/footer';
import './EditHeroSection.css';

const EditHeroSection = () => {
  const [activeTab, setActiveTab] = useState('content');
  const [formData, setFormData] = useState({
    headline: '',
    subheading: '',
    backgroundImage: '/hero-background.jpg',
    buttonText: '',
    buttonUrl: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setFormData(prev => ({
          ...prev,
          backgroundImage: e.target.result
        }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSaveChanges = () => {
    console.log('Saving hero section changes:', formData);
    // Add save logic here
    alert('Hero section updated successfully!');
  };

  const handleCancel = () => {
    console.log('Canceling changes');
    // Navigate back to content management
    window.location.href = '/dashboard/content-management';
  };

  const handleBack = () => {
    // Navigate back to content management
    window.location.href = '/dashboard/content-management';
  };

  return (
    <div className="edit-hero-page">
      <Header />
      <div className="dashboard-container">
        <Navbar activeTab={activeTab} setActiveTab={setActiveTab} />
        <main className="main-content">
          {/* Breadcrumb */}
          <div className="breadcrumb">
            Content Management / Edit Hero Section
          </div>

          {/* Page Title and Back Button */}
          <div className="page-header">
            <h1 className="page-title">Edit Hero Section</h1>
            <button 
              className="back-btn"
              onClick={handleBack}
            >
              Back
            </button>
          </div>

          {/* Form */}
          <div className="edit-form">
            {/* Headline */}
            <div className="form-group">
              <label htmlFor="headline" className="form-label">Headline</label>
              <input
                type="text"
                id="headline"
                name="headline"
                value={formData.headline}
                onChange={handleInputChange}
                className="form-input"
                placeholder="Enter headline text"
              />
            </div>

            {/* Subheading */}
            <div className="form-group">
              <label htmlFor="subheading" className="form-label">Subheading</label>
              <textarea
                id="subheading"
                name="subheading"
                value={formData.subheading}
                onChange={handleInputChange}
                className="form-textarea"
                placeholder="Enter subheading text"
                rows="4"
              />
            </div>

            {/* Background */}
            <div className="form-group">
              <label className="form-label">Background</label>
              <div className="image-preview-container">
                <img
                  src={formData.backgroundImage}
                  alt="Hero background"
                  className="background-preview"
                />
                <div className="image-upload-section">
                  <input
                    type="file"
                    id="background-upload"
                    accept="image/*"
                    onChange={handleImageUpload}
                    className="file-input"
                  />
                  <label htmlFor="background-upload" className="upload-btn">
                    Upload New Image
                  </label>
                </div>
              </div>
            </div>

            {/* Button Text */}
            <div className="form-group">
              <label htmlFor="buttonText" className="form-label">Button Text</label>
              <input
                type="text"
                id="buttonText"
                name="buttonText"
                value={formData.buttonText}
                onChange={handleInputChange}
                className="form-input"
                placeholder="Enter button text"
              />
            </div>

            {/* Button URL */}
            <div className="form-group">
              <label htmlFor="buttonUrl" className="form-label">Button URL</label>
              <input
                type="url"
                id="buttonUrl"
                name="buttonUrl"
                value={formData.buttonUrl}
                onChange={handleInputChange}
                className="form-input"
                placeholder="Enter button URL"
              />
            </div>

            {/* Action Buttons */}
            <div className="action-buttons">
              <button
                type="button"
                className="cancel-btn"
                onClick={handleCancel}
              >
                Cancel
              </button>
              <button
                type="button"
                className="save-btn"
                onClick={handleSaveChanges}
              >
                Save Changes
              </button>
            </div>
          </div>
        </main>
      </div>
      <Footer />
    </div>
  );
};

export default EditHeroSection;
