import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useApi } from '../../context/ApiContext';
import { authAPI } from '../../services/api';
import Header from './header';
import Navbar from './navbar';
import Footer from './footer';
import './profile.css';

const Profile = () => {
  const navigate = useNavigate();
  const { getUserName, logout } = useApi();
  const [activeTab, setActiveTab] = useState('profile');

  // Profile form data
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phoneNumber: ''
  });

  // Password change data
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });

  // UI states
  const [isLoading, setIsLoading] = useState(false);
  const [isLoadingProfile, setIsLoadingProfile] = useState(true);
  const [showSubscriptionModal, setShowSubscriptionModal] = useState(false);
  const [apiError, setApiError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  // User data from database
  const [userProfile, setUserProfile] = useState({
    currentPlan: 'Free',
    subscriptionPeriod: 'monthly',
    subscriptionStartDate: null,
    accountCreatedDate: null,
    lastLogin: null,
    subscriptionStatus: 'active',
    isActive: true
  });

  // Load user profile data from database when component mounts
  useEffect(() => {
    const loadUserProfile = async () => {
      try {
        setIsLoadingProfile(true);
        setApiError('');

        // Get current user email from localStorage
        const currentUser = localStorage.getItem('user');
        if (!currentUser) {
          setApiError('No user session found. Please login again.');
          return;
        }

        const userData = JSON.parse(currentUser);
        const email = userData.email;

        // Fetch profile from backend
        const profileResponse = await authAPI.getProfile(email);

        // Update form data with database values
        setFormData({
          fullName: profileResponse.full_name || '',
          email: profileResponse.email || '',
          phoneNumber: profileResponse.phone_number || ''
        });

        // Update user profile data
        setUserProfile({
          currentPlan: profileResponse.user_type === 'premium' ? 'Premium' : 'Free',
          subscriptionPeriod: 'monthly', // Default, can be enhanced later
          subscriptionStartDate: profileResponse.subscription_start_date ? new Date(profileResponse.subscription_start_date) : null,
          accountCreatedDate: profileResponse.account_created_date ? new Date(profileResponse.account_created_date) : null,
          lastLogin: profileResponse.last_login ? new Date(profileResponse.last_login) : null,
          subscriptionStatus: profileResponse.subscription_status || 'active',
          isActive: profileResponse.is_active
        });

      } catch (error) {
        console.error('Error loading user profile:', error);
        setApiError(error.message || 'Failed to load profile data');
      } finally {
        setIsLoadingProfile(false);
      }
    };

    loadUserProfile();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    setApiError('');
    setSuccessMessage('');
  };

  const handlePasswordChange = (e) => {
    const { name, value } = e.target;
    setPasswordData(prev => ({
      ...prev,
      [name]: value
    }));
    setApiError('');
    setSuccessMessage('');
  };

  const handleChangePassword = async () => {
    // Validation
    if (!passwordData.currentPassword.trim()) {
      setApiError('Please enter your current password!');
      return;
    }
    if (!passwordData.newPassword.trim()) {
      setApiError('Please enter a new password!');
      return;
    }
    if (passwordData.newPassword !== passwordData.confirmPassword) {
      setApiError('New passwords do not match!');
      return;
    }
    if (passwordData.currentPassword === passwordData.newPassword) {
      setApiError('New password must be different from your current password!');
      return;
    }
    if (passwordData.newPassword.length < 6) {
      setApiError('New password must be at least 6 characters long!');
      return;
    }

    try {
      setIsLoading(true);
      setApiError('');
      setSuccessMessage('');

      // Get current user email
      const currentUser = localStorage.getItem('user');
      const userData = JSON.parse(currentUser);
      const email = userData.email;

      // Call backend API
      await authAPI.changePassword(email, passwordData.currentPassword, passwordData.newPassword);

      setSuccessMessage('Password changed successfully!');

      // Reset password form
      setPasswordData({
        currentPassword: '',
        newPassword: '',
        confirmPassword: ''
      });

    } catch (error) {
      console.error('Error changing password:', error);
      setApiError(error.message || 'Failed to change password');
    } finally {
      setIsLoading(false);
    }
  };

  const handleSaveChanges = async () => {
    try {
      setIsLoading(true);
      setApiError('');
      setSuccessMessage('');

      // Get current user email
      const currentUser = localStorage.getItem('user');
      const userData = JSON.parse(currentUser);
      const email = userData.email;

      // Prepare profile data for update
      const profileData = {
        email: email,
        full_name: formData.fullName,
        phone_number: formData.phoneNumber
      };

      // Call backend API
      const response = await authAPI.updateProfile(profileData);

      setSuccessMessage('Profile updated successfully! Your changes have been saved.');

      // Update localStorage with new user data
      const updatedUser = {
        ...userData,
        full_name: response.user.full_name,
        phone_number: response.user.phone_number
      };
      localStorage.setItem('user', JSON.stringify(updatedUser));

    } catch (error) {
      console.error('Error saving profile data:', error);
      setApiError(error.message || 'Failed to save profile data');
    } finally {
      setIsLoading(false);
    }
  };

  const handleCancel = () => {
    // Reload profile data from database to reset form
    window.location.reload();
  };

  const handleManageSubscription = () => {
    setShowSubscriptionModal(true);
  };

  const handleCloseSubscriptionModal = () => {
    setShowSubscriptionModal(false);
  };

  const handleUpgradePlan = () => {
    // Here you would typically handle the upgrade logic
    console.log('Upgrading plan...');
    alert('Redirecting to upgrade page...');
    setShowSubscriptionModal(false);
  };

  const handleDowngradePlan = () => {
    const confirmed = window.confirm(
      'Are you sure you want to cancel your Premium subscription? You will lose access to premium features and be moved to the Free plan.'
    );

    if (confirmed) {
      // Here you would typically handle the subscription cancellation logic
      console.log('Cancelling Premium subscription...');
      alert('Premium subscription cancelled successfully! You will be moved to the Free plan.');
      setUserProfile(prev => ({ ...prev, currentPlan: 'Free' }));
      setShowSubscriptionModal(false);
    }
  };

  const handleDeleteAccount = async () => {
    const confirmed = window.confirm(
      '⚠️ WARNING: This action will PERMANENTLY DELETE your account and all associated data from our database. This action CANNOT be undone. You will lose access to all features immediately and will need to create a new account to use the service again.\n\nAre you absolutely sure you want to proceed?'
    );

    if (confirmed) {
      try {
        setIsLoading(true);
        setApiError('');

        // Get current user email
        const currentUser = localStorage.getItem('user');
        const userData = JSON.parse(currentUser);
        const email = userData.email;

        // Call backend API to delete account
        await authAPI.deleteAccount(email);

        // Show deletion confirmation
        alert('Your account has been permanently deleted. You will be logged out and redirected to the home page.');

        // Logout and clear user data
        logout();

        // Redirect to home page
        navigate('/');

      } catch (error) {
        console.error('Error deactivating account:', error);
        setApiError(error.message || 'Failed to deactivate account');
        setIsLoading(false);
      }
    }
  };

  // Format date for display
  const formatDate = (dateString) => {
    if (!dateString) return 'Not available';
    try {
      const date = new Date(dateString);
      return date.toLocaleDateString('en-US', {
        year: 'numeric',
        month: 'long',
        day: 'numeric'
      });
    } catch (error) {
      return 'Invalid date';
    }
  };

  if (isLoadingProfile) {
    return (
      <div className="user-dashboard">
        <Header />
        <div className="user-dashboard-container">
          <Navbar activeTab={activeTab} setActiveTab={setActiveTab} />
          <main className="user-main-content">
            <div className="profile-loading">
              <p>Loading profile data...</p>
            </div>
          </main>
        </div>
      </div>
    );
  }

  return (
    <div className="user-dashboard">
      {/* Top Header */}
      <Header />

      <div className="user-dashboard-container">
        {/* Left Sidebar */}
        <Navbar activeTab={activeTab} setActiveTab={setActiveTab} />

        {/* Main Content */}
        <main className="user-main-content">
          <h1 className="profile-section-title">Profile</h1>

          {/* Error and Success Messages */}
          {apiError && (
            <div className="profile-error-message">
              {apiError}
              <button 
                className="profile-clear-error-btn"
                onClick={() => setApiError('')}
              >
                ×
              </button>
            </div>
          )}

          {successMessage && (
            <div className="profile-success-message">
              {successMessage}
              <button 
                className="profile-clear-success-btn"
                onClick={() => setSuccessMessage('')}
              >
                ×
              </button>
            </div>
          )}

          {/* Personal Information */}
          <section className="profile-section">
            <h2 className="profile-subsection-title">Personal Information</h2>
            <div className="profile-form-group">
              <label htmlFor="fullName">Full Name</label>
              <input
                type="text"
                id="fullName"
                name="fullName"
                value={formData.fullName}
                onChange={handleInputChange}
                placeholder="Enter your full name"
                disabled={isLoading}
              />
            </div>
            <div className="profile-form-group">
              <label htmlFor="email">Email Address</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                disabled={true}
                className="profile-disabled-input"
                title="Email cannot be changed"
              />
            </div>
            <div className="profile-form-group">
              <label htmlFor="phoneNumber">Phone Number</label>
              <input
                type="tel"
                id="phoneNumber"
                name="phoneNumber"
                value={formData.phoneNumber}
                onChange={handleInputChange}
                placeholder="Enter your phone number"
                disabled={isLoading}
              />
            </div>
          </section>

          {/* Change Password */}
          <section className="profile-section">
            <h2 className="profile-subsection-title">Change Password</h2>
            <div className="profile-form-group">
              <label htmlFor="currentPassword">Current Password</label>
              <input
                type="password"
                id="currentPassword"
                name="currentPassword"
                value={passwordData.currentPassword}
                onChange={handlePasswordChange}
                placeholder="Enter current password"
                disabled={isLoading}
              />
            </div>
            <div className="profile-form-group">
              <label htmlFor="newPassword">New Password</label>
              <input
                type="password"
                id="newPassword"
                name="newPassword"
                value={passwordData.newPassword}
                onChange={handlePasswordChange}
                placeholder="Enter new password"
                disabled={isLoading}
              />
            </div>
            <div className="profile-form-group">
              <label htmlFor="confirmPassword">Confirm New Password</label>
              <input
                type="password"
                id="confirmPassword"
                name="confirmPassword"
                value={passwordData.confirmPassword}
                onChange={handlePasswordChange}
                placeholder="Confirm new password"
                disabled={isLoading}
              />
            </div>
            <button 
              className="profile-change-password-btn"
              onClick={handleChangePassword}
              disabled={isLoading}
            >
              {isLoading ? 'Changing Password...' : 'Change Password'}
            </button>
          </section>

          {/* Account Information */}
          <section className="profile-section">
            <h2 className="profile-subsection-title">Account Information</h2>
            <div className="profile-account-info">
              <div className="profile-info-item">
                <span className="profile-info-label">Account Created:</span>
                <span className="profile-info-value">{formatDate(userProfile.accountCreatedDate)}</span>
              </div>
              <div className="profile-info-item">
                <span className="profile-info-label">Last Login:</span>
                <span className="profile-info-value">{formatDate(userProfile.lastLogin)}</span>
              </div>
              <div className="profile-info-item">
                <span className="profile-info-label">Account Status:</span>
                <span className={`profile-info-value ${userProfile.isActive ? 'active' : 'inactive'}`}>
                  {userProfile.isActive ? 'Active' : 'Inactive'}
                </span>
              </div>
            </div>
          </section>

          {/* Subscription */}
          <section className="profile-section">
            <h2 className="profile-subsection-title">Subscription</h2>
            <div className="profile-subscription-info">
              <div className="profile-info-item">
                <span className="profile-info-label">Current Plan:</span>
                <span className={`profile-info-value ${userProfile.currentPlan.toLowerCase()}`}>
                  {userProfile.currentPlan}
                </span>
              </div>
              <div className="profile-info-item">
                <span className="profile-info-label">Subscription Status:</span>
                <span className={`profile-info-value ${userProfile.subscriptionStatus}`}>
                  {userProfile.subscriptionStatus}
                </span>
              </div>
              {userProfile.subscriptionStartDate && (
                <div className="profile-info-item">
                  <span className="profile-info-label">Subscription Start:</span>
                  <span className="profile-info-value">
                    {formatDate(userProfile.subscriptionStartDate)}
                  </span>
                </div>
              )}
              <p className="profile-subscription-description">
                {userProfile.currentPlan === 'Premium' && 'Enjoy exclusive access to advanced search filters, detailed property reports, and personalized alerts with your Premium plan.'}
                {userProfile.currentPlan === 'Free' && 'Basic access to property listings. Upgrade to unlock advanced features and detailed reports.'}
              </p>
              <button 
                className="profile-manage-subscription-btn"
                onClick={handleManageSubscription}
              >
                Manage Subscription
              </button>
            </div>
          </section>

          {/* Account Settings */}
          <section className="profile-section">
            <h2 className="profile-subsection-title">Danger Zone</h2>
            <button 
              className="profile-delete-account-btn"
              onClick={handleDeleteAccount}
              disabled={isLoading}
            >
              {isLoading ? 'Deleting...' : 'Delete My Account'}
            </button>
          </section>

          {/* Action Buttons */}
          <div className="profile-action-buttons">
            <button 
              className="profile-cancel-btn"
              onClick={handleCancel}
              disabled={isLoading}
            >
              Cancel
            </button>
            <button 
              className="profile-save-btn"
              onClick={handleSaveChanges}
              disabled={isLoading}
            >
              {isLoading ? 'Saving...' : 'Save Changes'}
            </button>
          </div>
        </main>
      </div>

      {/* Subscription Management Modal */}
      {showSubscriptionModal && (
        <div className="profile-modal-overlay">
          <div className="profile-modal">
            <div className="profile-modal-header">
              <h3>Manage Subscription</h3>
              <button 
                className="profile-modal-close-btn"
                onClick={handleCloseSubscriptionModal}
              >
                ×
              </button>
            </div>
            <div className="profile-modal-content">
              <div className="profile-modal-section">
                <h4>Current Plan: {userProfile.currentPlan}</h4>
                <p>
                  {userProfile.currentPlan === 'Premium' 
                    ? 'You currently have a Premium subscription with access to advanced features.'
                    : 'You currently have a Free plan with basic access to property listings.'
                  }
                </p>
              </div>

              {userProfile.currentPlan === 'Premium' ? (
                <div className="profile-modal-section">
                  <h4>Cancel Premium Subscription</h4>
                  <p>Cancel your Premium subscription to return to the Free plan.</p>
                  <button 
                    className="profile-modal-downgrade-btn"
                    onClick={handleDowngradePlan}
                  >
                    Cancel Premium
                  </button>
                </div>
              ) : (
                <div className="profile-modal-section">
                  <h4>Upgrade to Premium</h4>
                  <p>Get access to advanced search filters, detailed reports, and personalized alerts.</p>
                  <button 
                    className="profile-modal-upgrade-btn"
                    onClick={handleUpgradePlan}
                  >
                    Upgrade to Premium
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>
      )}

      <Footer />
    </div>
  );
};

export default Profile;
