            <h3 className="plan-title">Free</h3>
            <p className="plan-subtitle">Starter</p>
            <p className="plan-description">Automation plus enterprise-grade features.</p>
          </div>

          <div className="plan-features">
            <div className="feature-item">
              <span className="checkmark"></span>
              <span>Multi-step</span>
            </div>
            <div className="feature-item">
              <span className="checkmark"></span>
              <span>Unlimited Premium Apps</span>
            </div>
            <div className="feature-item">
              <span className="checkmark"></span>
              <span>Unlimited Users Team</span>
            </div>
            <div className="feature-item">
              <span className="checkmark"></span>
              <span>Advanced Admin</span>
            </div>
            <div className="feature-item">
              <span className="checkmark"></span>
              <span>Custom Data Retention</span>
            </div>
          </div>

          <button className="choose-plan-btn">Choose plan</button>
        </div>

        <div 
          className={`plan-card ${formData.userType === 'premium' ? 'selected' : ''}`}
          onClick={() => setFormData(prev => ({ ...prev, userType: 'premium' }))}
