import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './feedback.css';

const Feedback = () => {
  const [activeTab, setActiveTab] = useState('feedback');
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);
  const [rating, setRating] = useState(0);
  const [feedbackText, setFeedbackText] = useState('');
  const navigate = useNavigate();

  const navigationItems = [
    { id: 'dashboard', label: 'Dashboard', icon: 'üè†' },
    { id: 'sales-prediction', label: 'Sales Prediction', icon: 'üìà' },
    { id: 'bookmarks', label: 'Bookmarks', icon: 'üîñ' },
    { id: 'support', label: 'Support', icon: '‚ùì' },
    { id: 'feedback', label: 'Feedback', icon: 'üí¨' },
    { id: 'profile', label: 'Profile', icon: 'üë§' }
  ];

  // Sample recent reviews data
  const recentReviews = [
    {
      id: 1,
      name: 'Sophia Bennett',
      time: '2 weeks ago',
      rating: 5,
      text: 'The platform is incredibly useful for finding commercial properties. The search filters are very effective, and I\'ve found several promising leads.',
      likes: 12,
      dislikes: 2,
      image: 'https://images.unsplash.com/photo-1494790108755-2616b612b786?w=40&h=40&fit=crop&crop=face'
    },
    {
      id: 2,
      name: 'Ethan Chen',
      time: '1 month ago',
      rating: 4,
      text: 'Overall, a good experience. The property listings are comprehensive, but the interface could be a bit more intuitive.',
      likes: 8,
      dislikes: 1,
      image: 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=40&h=40&fit=crop&crop=face'
    },
    {
      id: 3,
      name: 'Olivia Zhang',
      time: '2 months ago',
      rating: 3,
      text: 'The platform is okay, but I\'ve encountered some outdated listings. It would be great if the information was more consistently updated.',
      likes: 5,
      dislikes: 3,
      image: 'https://images.unsplash.com/photo-1438761681033-6461ffad8d80?w=40&h=40&fit=crop&crop=face'
    }
  ];

  // Sample your reviews data
  const yourReviews = [
    {
      id: 1,
      time: '3 months ago',
      rating: 4,
      text: 'I appreciate the detailed property reports available on the platform. They provide valuable insights that help in making informed decisions.',
      likes: 7,
      dislikes: 0,
      hasAdminReply: true,
      adminReply: {
        text: 'Thank you for your feedback! We\'re glad you found the property reports helpful. We are continuously working to improve the depth and accuracy of our data.',
        time: '3 months ago',
        image: 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=40&h=40&fit=crop&crop=face'
      }
    },
    {
      id: 2,
      time: '6 months ago',
      rating: 5,
      text: 'The customer support team was very responsive and helpful when I had questions about a specific property. Great service!',
      likes: 10,
      dislikes: 1,
      hasAdminReply: true,
      adminReply: {
        text: 'We\'re thrilled to hear about your positive experience with our customer support. We strive to provide prompt and effective assistance to all our users.',
        time: '6 months ago',
        image: 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=40&h=40&fit=crop&crop=face'
      }
    }
  ];

  const handleProfileClick = () => {
    setShowProfileDropdown(!showProfileDropdown);
  };

  const handleProfilePage = () => {
    setShowProfileDropdown(false);
    navigate('/profile');
  };

  const handleLogout = () => {
    setShowProfileDropdown(false);
    localStorage.removeItem('isLoggedIn');
    localStorage.removeItem('userType');
    localStorage.removeItem('userPreferences');
    navigate('/login');
  };

  const handleClickOutside = (e) => {
    if (!e.target.closest('.user-profile')) {
      setShowProfileDropdown(false);
    }
  };

  const handleNavigation = (itemId) => {
    setActiveTab(itemId);
    
    switch (itemId) {
      case 'dashboard':
        navigate('/dashboard/free');
        break;
      case 'sales-prediction':
        navigate('/dashboard/priceprediction');
        break;
      case 'bookmarks':
        navigate('/bookmarks');
        break;
      case 'support':
        navigate('/support');
        break;
      case 'feedback':
        navigate('/feedback');
        break;
      case 'profile':
        navigate('/profile');
        break;
      default:
        break;
    }
  };

  const handleRatingClick = (selectedRating) => {
    setRating(selectedRating);
  };

  const handleSubmitFeedback = () => {
    if (rating === 0) {
      alert('Please select a rating before submitting feedback.');
      return;
    }
    if (feedbackText.trim() === '') {
      alert('Please enter your feedback before submitting.');
      return;
    }
    
    // Here you would typically send the feedback to the backend
    console.log('Submitting feedback:', { rating, feedbackText });
    alert('Thank you for your feedback!');
    
    // Reset form
    setRating(0);
    setFeedbackText('');
  };

  const renderStars = (rating, interactive = false) => {
    return (
      <div className="stars">
        {[1, 2, 3, 4, 5].map((star) => (
          <button
            key={star}
            className={`star-btn ${star <= rating ? 'filled' : ''} ${interactive ? 'interactive' : ''}`}
            onClick={interactive ? () => handleRatingClick(star) : undefined}
            disabled={!interactive}
          >
            ‚òÖ
          </button>
        ))}
      </div>
    );
  };

  // Add click listener to close dropdown when clicking outside
  useEffect(() => {
    document.addEventListener('click', handleClickOutside);
    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, []);

  return (
    <div className="feedback-dashboard">
      {/* Top Header */}
      <header className="dashboard-header">
        <div className="header-center">
          <div className="logo">
            <span className="logo-icon">üè†</span>
            <span className="logo-text">Valuez</span>
          </div>
        </div>
        <div className="header-right">
          <div className="notification-icon">üîî</div>
          <div className="user-profile">
            <img 
              src="https://images.unsplash.com/photo-1494790108755-2616b612b786?w=40&h=40&fit=crop&crop=face" 
              alt="User Profile" 
              className="profile-image"
              onClick={handleProfileClick}
            />
            {showProfileDropdown && (
              <div className="profile-dropdown">
                <div className="dropdown-item" onClick={handleProfilePage}>
                  <span className="dropdown-icon">üë§</span>
                  <span>Profile</span>
                </div>
                <div className="dropdown-divider"></div>
                <div className="dropdown-item" onClick={handleLogout}>
                  <span className="dropdown-icon">üö™</span>
                  <span>Logout</span>
                </div>
              </div>
            )}
          </div>
        </div>
      </header>

      <div className="dashboard-container">
        {/* Left Sidebar */}
        <aside className="sidebar">
          <nav className="sidebar-nav">
            {navigationItems.map((item) => (
              <button
                key={item.id}
                className={`nav-item ${activeTab === item.id ? 'active' : ''}`}
                onClick={() => handleNavigation(item.id)}
              >
                <span className="nav-icon">{item.icon}</span>
                <span className="nav-label">{item.label}</span>
              </button>
            ))}
            <div className="nav-divider"></div>
            <button className="nav-item logout-item" onClick={handleLogout}>
              <span className="nav-icon">üö™</span>
              <span className="nav-label">Logout</span>
            </button>
          </nav>
        </aside>

        {/* Main Content */}
        <main className="main-content">
          {/* Feedback Section */}
          <section className="feedback-section">
            <h1 className="section-title">Feedback</h1>
            <h2 className="subtitle">Rate Your Experience</h2>
            
            <div className="rating-section">
              {renderStars(rating, true)}
            </div>
            
            <div className="feedback-form">
              <textarea
                className="feedback-textarea"
                placeholder="Share your thoughts, suggestions, or issues here... (Max. 500 characters)"
                value={feedbackText}
                onChange={(e) => setFeedbackText(e.target.value)}
                maxLength={500}
                rows={4}
              />
              <div className="char-count">{feedbackText.length}/500</div>
              
              <button 
                className="submit-btn"
                onClick={handleSubmitFeedback}
              >
                Submit Feedback
              </button>
            </div>
          </section>

          {/* Recent Reviews Section */}
          <section className="reviews-section">
            <h2 className="section-title">Recent Reviews</h2>
            <div className="reviews-list">
              {recentReviews.map((review) => (
                <div key={review.id} className="review-card">
                  <div className="review-header">
                    <img src={review.image} alt={review.name} className="reviewer-image" />
                    <div className="reviewer-info">
                      <h3 className="reviewer-name">{review.name}</h3>
                      <span className="review-time">{review.time}</span>
                    </div>
                  </div>
                  <div className="review-rating">
                    {renderStars(review.rating)}
                  </div>
                  <p className="review-text">{review.text}</p>
                  <div className="review-engagement">
                    <button className="engagement-btn">
                      <span className="engagement-icon">üëç</span>
                      <span className="engagement-count">{review.likes}</span>
                    </button>
                    <button className="engagement-btn">
                      <span className="engagement-icon">üëé</span>
                      <span className="engagement-count">{review.dislikes}</span>
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </section>

          {/* Your Reviews Section */}
          <section className="your-reviews-section">
            <h2 className="section-title">Your Reviews</h2>
            <div className="reviews-list">
              {yourReviews.map((review) => (
                <div key={review.id} className="review-card your-review">
                  <div className="review-header">
                    <div className="reviewer-image you">You</div>
                    <div className="reviewer-info">
                      <h3 className="reviewer-name">You</h3>
                      <span className="review-time">{review.time}</span>
                    </div>
                  </div>
                  <div className="review-rating">
                    {renderStars(review.rating)}
                  </div>
                  <p className="review-text">{review.text}</p>
                  <div className="review-engagement">
                    <button className="engagement-btn">
                      <span className="engagement-icon">üëç</span>
                      <span className="engagement-count">{review.likes}</span>
                    </button>
                    <button className="engagement-btn">
                      <span className="engagement-icon">üëé</span>
                      <span className="engagement-count">{review.dislikes}</span>
                    </button>
                  </div>
                  
                  {review.hasAdminReply && (
                    <div className="admin-reply">
                      <div className="review-header">
                        <img src={review.adminReply.image} alt="Admin" className="reviewer-image" />
                        <div className="reviewer-info">
                          <h3 className="reviewer-name">Admin</h3>
                          <span className="review-time">{review.adminReply.time}</span>
                        </div>
                      </div>
                      <div className="review-rating">
                        {renderStars(0)} {/* Admin replies don't have ratings */}
                      </div>
                      <p className="review-text">{review.adminReply.text}</p>
                      <div className="review-engagement">
                        <button className="engagement-btn">
                          <span className="engagement-icon">üëç</span>
                        </button>
                        <button className="engagement-btn">
                          <span className="engagement-icon">üëé</span>
                        </button>
                      </div>
                    </div>
                  )}
                </div>
              ))}
            </div>
          </section>
        </main>
      </div>

      {/* Footer */}
      <footer className="dashboard-footer">
        <div className="footer-left">
          <a href="/terms" className="footer-link">Terms</a>
          <a href="/privacy" className="footer-link">Privacy</a>
          <a href="/contact" className="footer-link">Contact</a>
          <a href="/disclaimer" className="footer-link">Disclaimer</a>
        </div>
        <div className="footer-center">
          <span className="copyright">¬© 2024 Valuez. All rights reserved.</span>
        </div>
      </footer>
    </div>
  );
};

export default Feedback;
