import React, { useState } from 'react';
import './SubscriptionPlans.css';

// Centralized plans data that can be used across the app
export const subscriptionPlansData = [
  {
    id: 'free',
    name: "Free",
    price: "$0",
    icon: "https://cdn-icons-png.flaticon.com/512/3135/3135712.png",
    description: "Perfect for getting started",
    features: [
      "Basic property valuations",
      "Up to 5 properties", 
      "Standard reports",
      "Email support",
      "Mobile app access"
    ],
    badge: "Most Popular",
    buttonText: "Get Started Free"
  },
  {
    id: 'premium',
    name: "Premium",
    price: "$29",
    icon: "https://cdn-icons-png.flaticon.com/512/3135/3135713.png",
    description: "For growing businesses",
    features: [
      "Everything in Free",
      "Unlimited properties",
      "Advanced analytics", 
      "Custom reports",
      "Priority support",
      "API access",
      "Team collaboration"
    ],
    badge: "Best Value",
    featured: true,
    buttonText: "Choose Premium"
  },
  {
    id: 'agent',
    name: "Agent",
    price: "$99",
    icon: "https://cdn-icons-png.flaticon.com/512/3135/3135715.png",
    description: "For real estate professionals",
    features: [
      "Everything in Premium",
      "Client management",
      "Lead generation tools",
      "Commission tracking",
      "Marketing automation",
      "Dedicated account manager",
      "White-label solutions"
    ],
    badge: "Professional",
    buttonText: "Choose Agent"
  }
];

const SubscriptionPlans = ({ 
  variant = 'landing', // 'landing' or 'signup'
  selectedPlan = null,
  onPlanSelect = null,
  showPricingToggle = true,
  showComparison = true,
  customTitle = null,
  customSubtitle = null,
  customButtonText = null,
  onButtonClick = null
}) => {
  const [yearlyBilling, setYearlyBilling] = useState(false);

  // Calculate prices based on billing cycle
  const getPlanPrice = (plan) => {
    if (plan.name === "Free") return "$0";
    const basePrice = parseInt(plan.price.replace('$', ''));
    const yearlyPrice = Math.round(basePrice * 0.8); // 20% discount
    return yearlyBilling ? `$${yearlyPrice}` : plan.price;
  };

  const getOriginalPrice = (plan) => {
    if (plan.name === "Free" || !yearlyBilling) return null;
    return plan.price;
  };

  const getYearlySavings = (plan) => {
    if (plan.name === "Free" || !yearlyBilling) return null;
    const basePrice = parseInt(plan.price.replace('$', ''));
    const savings = Math.round(basePrice * 0.2 * 12);
    return `Save $${savings}/year`;
  };

  const handlePlanClick = (plan) => {
    if (onPlanSelect) {
      onPlanSelect(plan);
    } else if (variant === 'landing') {
      // Default landing page behavior - redirect to signup
      window.location.href = '/signup';
    }
  };

  const handleButtonClick = (plan) => {
    if (onButtonClick) {
      onButtonClick(plan);
    } else if (variant === 'landing') {
      // Default landing page behavior - redirect to signup
      window.location.href = '/signup';
    }
  };

  const title = customTitle || "Choose Your Perfect Plan";
  const subtitle = customSubtitle || "Select the plan that best fits your property management needs";

  return (
    <section className="subscription-plans" id="plans">
      <div className="plans-container">
        <div className="plans-header">
          <h2 className="plans-title">{title}</h2>
          <p className="plans-subtitle">{subtitle}</p>
        </div>

        {showPricingToggle && (
          <div className="pricing-toggle">
            <button 
              className={`toggle-btn ${!yearlyBilling ? 'active' : ''}`}
              onClick={() => setYearlyBilling(false)}
            >
              Monthly
            </button>
            <button 
              className={`toggle-btn ${yearlyBilling ? 'active' : ''}`}
              onClick={() => setYearlyBilling(true)}
            >
              Yearly
              <span className="save-badge">Save 20%</span>
            </button>
          </div>
        )}

        <div className="plans-grid">
          {subscriptionPlansData.map((plan, index) => (
            <div
              key={plan.id}
              className={`plan-card ${plan.featured ? 'highlighted' : ''} ${selectedPlan === plan.id ? 'selected' : ''}`}
              onClick={() => handlePlanClick(plan)}
            >
              {plan.badge && (
                <div className="plan-badge">{plan.badge}</div>
              )}

              <div className="plan-header">
                <h3 className="plan-name">{plan.name}</h3>
                <p className="plan-description">{plan.description}</p>
                <div className="plan-price-container">
                  <div className="plan-price">
                    <span className="price-amount">{getPlanPrice(plan)}</span>
                    <span className="price-period">/month</span>
                  </div>
                  {getOriginalPrice(plan) && (
                    <div className="original-price">
                      <span>was {getOriginalPrice(plan)}/month</span>
                    </div>
                  )}
                  {getYearlySavings(plan) && (
                    <div className="yearly-savings">
                      {getYearlySavings(plan)}
                    </div>
                  )}
                </div>
                <img src={plan.icon} alt={plan.name} className="plan-icon" />
              </div>

              <div className="plan-features">
                {plan.features.map((feature, idx) => (
                  <div key={idx} className="feature-item">
                    <span className="checkmark">✓</span>
                    <span>{feature}</span>
                  </div>
                ))}
              </div>

              <button 
                className="plan-button"
                onClick={(e) => {
                  e.stopPropagation();
                  handleButtonClick(plan);
                }}
              >
                {customButtonText || plan.buttonText}
              </button>
            </div>
          ))}
        </div>

        {showComparison && (
          <div className="plan-comparison">
            <h4>All plans include:</h4>
            <div className="comparison-features">
              <span>✓ 30-day money-back guarantee</span>
              <span>✓ Cancel anytime</span>
              <span>✓ 99.9% uptime SLA</span>
              <span>✓ GDPR compliant</span>
            </div>
          </div>
        )}
      </div>
    </section>
  );
};

export default SubscriptionPlans;
