import React, { useState } from 'react';
import Header from '../sharedpages/header';
import Navbar from '../sharedpages/navbar';
import Footer from '../sharedpages/footer';
import './EditHeroSection.css';

const EditHeroSection = () => {
  const [activeTab, setActiveTab] = useState('content');
  const [formData, setFormData] = useState({
    headline: 'Find Your Perfect Commercial Property',
    subheading: 'Discover premium office spaces, retail locations, and industrial facilities. Connect with property owners and find the ideal space for your business growth.',
    backgroundImage: '/hero-background.jpg',
    buttonText: 'Start Your Search',
    buttonUrl: '/properties'
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setFormData(prev => ({
          ...prev,
          backgroundImage: e.target.result
        }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSaveChanges = () => {
    console.log('Saving hero section changes:', formData);
    // Add save logic here
    alert('Hero section updated successfully!');
  };

  const handleCancel = () => {
    console.log('Canceling changes');
    // Navigate back to content management
    window.location.href = '/dashboard/content-management';
  };

  const handleBack = () => {
    // Navigate back to content management
    window.location.href = '/dashboard/content-management';
  };

  return (
    <div className="sys-edit-hero-page">
      <Header />
      <div className="sys-dashboard-container">
        <Navbar activeTab={activeTab} setActiveTab={setActiveTab} />
        <main className="sys-main-content">
          {/* Breadcrumb */}
          <div className="sys-breadcrumb">
            Content Management / Edit Hero Section
          </div>

          {/* Page Title and Back Button */}
          <div className="sys-page-header">
            <h1 className="sys-page-title">Edit Hero Section</h1>
            <button 
              className="sys-back-btn"
              onClick={handleBack}
            >
              Back
            </button>
          </div>

          {/* Form */}
          <div className="sys-edit-form">
            {/* Headline */}
            <div className="sys-form-group">
              <label htmlFor="headline" className="sys-form-label">Headline</label>
              <input
                type="text"
                id="headline"
                name="headline"
                value={formData.headline}
                onChange={handleInputChange}
                className="sys-form-input"
                placeholder="Enter headline text"
              />
            </div>

            {/* Subheading */}
            <div className="sys-form-group">
              <label htmlFor="subheading" className="sys-form-label">Subheading</label>
              <textarea
                id="subheading"
                name="subheading"
                value={formData.subheading}
                onChange={handleInputChange}
                className="sys-form-textarea"
                placeholder="Enter subheading text"
                rows="4"
              />
            </div>

            {/* Background */}
            <div className="sys-form-group">
              <label className="sys-form-label">Background</label>
              <div className="sys-image-upload">
                <img
                  src={formData.backgroundImage}
                  alt="Hero background"
                  className="sys-image-preview has-image"
                />
                <div className="sys-image-upload">
                  <input
                    type="file"
                    id="background-upload"
                    accept="image/*"
                    onChange={handleImageUpload}
                    className="sys-file-input"
                  />
                  <button
                    type="button"
                    className="sys-upload-btn"
                    onClick={() => document.getElementById('background-upload').click()}
                  >
                    Upload New Image
                  </button>
                </div>
              </div>
            </div>

            {/* Button Text */}
            <div className="sys-form-group">
              <label htmlFor="buttonText" className="sys-form-label">Button Text</label>
              <input
                type="text"
                id="buttonText"
                name="buttonText"
                value={formData.buttonText}
                onChange={handleInputChange}
                className="sys-form-input"
                placeholder="Enter button text"
              />
            </div>

            {/* Button URL */}
            <div className="sys-form-group">
              <label htmlFor="buttonUrl" className="sys-form-label">Button URL</label>
              <input
                type="url"
                id="buttonUrl"
                name="buttonUrl"
                value={formData.buttonUrl}
                onChange={handleInputChange}
                className="sys-form-input"
                placeholder="Enter button URL"
              />
            </div>

            {/* Action Buttons */}
            <div className="sys-action-buttons">
              <button
                type="button"
                className="sys-cancel-btn"
                onClick={handleCancel}
              >
                Cancel
              </button>
              <button
                type="button"
                className="sys-save-btn"
                onClick={handleSaveChanges}
              >
                Save Changes
              </button>
            </div>
          </div>
        </main>
      </div>
      <Footer />
    </div>
  );
};

export default EditHeroSection;
