import React, { useState, useEffect } from 'react';
import Header from '../sharedpages/header';
import Navbar from '../sharedpages/navbar';
import Footer from '../sharedpages/footer';
import { agentAPI } from '../../services/api';
import './agent-common.css';
import './AgentRegions.css';

const AgentRegions = () => {
  const [activeTab, setActiveTab] = useState('regions');
  const [selectedRegions, setSelectedRegions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Singapore postal districts data
  const regionsData = [
    { id: 1, district: '01', sector: '01-06', location: 'Central Business District' },
    { id: 2, district: '02', sector: '07-12', location: 'Chinatown' },
    { id: 3, district: '03', sector: '13-18', location: 'Queenstown' },
    { id: 4, district: '04', sector: '19-24', location: 'Sentosa' },
    { id: 5, district: '05', sector: '25-30', location: 'Buona Vista' },
    { id: 6, district: '06', sector: '31-36', location: 'City Hall' },
    { id: 7, district: '07', sector: '37-42', location: 'Bugis' },
    { id: 8, district: '08', sector: '43-48', location: 'Little India' },
    { id: 9, district: '09', sector: '49-54', location: 'Orchard Road' },
    { id: 10, district: '10', sector: '55-60', location: 'Tanglin' },
    { id: 11, district: '11', sector: '61-66', location: 'Newton' },
    { id: 12, district: '12', sector: '67-72', location: 'Balestier' },
    { id: 13, district: '13', sector: '73-78', location: 'MacPherson' },
    { id: 14, district: '14', sector: '79-84', location: 'Geylang' },
    { id: 15, district: '15', sector: '85-90', location: 'Katong' },
    { id: 16, district: '16', sector: '91-96', location: 'Bedok' },
    { id: 17, district: '17', sector: '97-102', location: 'Loyang' },
    { id: 18, district: '18', sector: '103-108', location: 'Tampines' },
    { id: 19, district: '19', sector: '109-114', location: 'Serangoon' },
    { id: 20, district: '20', sector: '115-120', location: 'Bishan' },
    { id: 21, district: '21', sector: '121-126', location: 'Toa Payoh' },
    { id: 22, district: '22', sector: '127-132', location: 'Ang Mo Kio' },
    { id: 23, district: '23', sector: '133-138', location: 'Yishun' },
    { id: 24, district: '24', sector: '139-144', location: 'Sembawang' },
    { id: 25, district: '25', sector: '145-150', location: 'Woodlands' },
    { id: 26, district: '26', sector: '151-156', location: 'Upper Thomson' },
    { id: 27, district: '27', sector: '157-162', location: 'Yio Chu Kang' },
    { id: 28, district: '28', sector: '163-168', location: 'Seletar' }
  ];

  useEffect(() => {
    const loadAgentRegions = async () => {
      try {
        setLoading(true);
        const response = await agentAPI.getAssignedRegions();
        console.log('Loaded agent regions:', response);

        // Map backend region data to frontend region IDs
        const regionValueToId = {
          'CBD': 1, 'Chinatown': 2, 'Queenstown': 3, 'Sentosa': 4, 'Buona Vista': 5,
          'City Hall': 6, 'Bugis': 7, 'Little India': 8, 'Orchard Road': 9, 'Tanglin': 10,
          'Newton': 11, 'Balestier': 12, 'MacPherson': 13, 'Geylang': 14, 'Katong': 15,
          'Bedok': 16, 'Loyang': 17, 'Tampines': 18, 'Serangoon': 19, 'Bishan': 20,
          'Toa Payoh': 21, 'Ang Mo Kio': 22, 'Yishun': 23, 'Sembawang': 24, 'Woodlands': 25,
          'Upper Thomson': 26, 'Yio Chu Kang': 27, 'Seletar': 28
        };

        const assignedRegionIds = response.map(region => regionValueToId[region.region_value]).filter(id => id);
        setSelectedRegions(assignedRegionIds);
      } catch (error) {
        console.error('Error loading agent regions:', error);
        setError('Failed to load assigned regions');
      } finally {
        setLoading(false);
      }
    };

    loadAgentRegions();
  }, []);

  const handleRegionToggle = (regionId) => {
    setSelectedRegions(prev => {
      if (prev.includes(regionId)) {
        return prev.filter(id => id !== regionId);
      } else {
        // Check if we're at the maximum limit
        if (prev.length >= 3) {
          alert('You can select a maximum of 3 regions.');
          return prev;
        }
        return [...prev, regionId];
      }
    });
  };

  const handleSaveChanges = () => {
    if (selectedRegions.length === 0) {
      alert('Please select at least one region.');
      return;
    }

    const agentRegionsData = {
      regions: selectedRegions,
      completed: true,
      savedAt: new Date().toISOString()
    };

    localStorage.setItem('agentRegions', JSON.stringify(agentRegionsData));
    alert('Regions saved successfully!');
  };

  const getCurrentRegionsText = () => {
    const currentRegions = regionsData.filter(region => selectedRegions.includes(region.id));
    if (currentRegions.length === 0) return 'No regions assigned';

    return currentRegions.map(region => 
      `District ${region.district} (${region.location})`
    ).join(', ');
  };

  return (
    <div className="user-dashboard">
      <Header />
      <div className="user-dashboard-container">
        <Navbar activeTab={activeTab} setActiveTab={setActiveTab} />
        <main className="user-main-content">
          <div className="user-content-header">
            <h1 className="user-main-title">Manage Assigned Regions</h1>
          </div>

          <div className="agent-regions-content">
            {/* Current Regions Section */}
            <section className="agent-regions-current-regions-section">
              <h2 className="agent-regions-section-title">Your Current Regions:</h2>
              <p className="agent-regions-regions-text">
                Assigned Regions: {getCurrentRegionsText()}
              </p>
            </section>

            {/* Region Selection Table */}
            <section className="agent-regions-selection-section">
              <h2 className="agent-regions-section-title">Select Your Regions (Max 3)</h2>
              <div className="agent-regions-table-container">
                <table className="agent-regions-table">
                  <thead>
                    <tr>
                      <th>Postal District</th>
                      <th>Postal Sector</th>
                      <th>General Location</th>
                      <th>Assigned</th>
                    </tr>
                  </thead>
                  <tbody>
                    {regionsData.map((region) => (
                      <tr key={region.id} className={selectedRegions.includes(region.id) ? 'agent-regions-selected-row' : ''}>
                        <td>{region.district}</td>
                        <td>{region.sector}</td>
                        <td>{region.location}</td>
                        <td>
                          <input
                            type="checkbox"
                            checked={selectedRegions.includes(region.id)}
                            onChange={() => handleRegionToggle(region.id)}
                            className="agent-regions-checkbox"
                          />
                          <span className="agent-regions-assignment-status">
                            {selectedRegions.includes(region.id) ? 'Assigned' : 'Not Assigned'}
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </section>

            {/* Save Button */}
            <section className="agent-regions-save-section">
              <button 
                className="agent-regions-save-btn"
                onClick={handleSaveChanges}
                disabled={selectedRegions.length === 0}
              >
                Save Changes
              </button>
            </section>
          </div>
        </main>
      </div>
      <Footer />
    </div>
  );
};

export default AgentRegions;
