const API_BASE_URL = 'http://localhost:5000/api';

// Helper function for API calls
const apiCall = async (endpoint, options = {}) => {
  const url = `${API_BASE_URL}${endpoint}`;

  const defaultOptions = {
    headers: {
      'Content-Type': 'application/json',
    },
    ...options,
  };

  try {
    const response = await fetch(url, defaultOptions);
    const data = await response.json();

    if (!response.ok) {
      throw new Error(data.error || `HTTP error! status: ${response.status}`);
    }

    return data;
  } catch (error) {
    console.error('API call failed:', error);
    throw error;
  }
};

// Auth API calls
export const authAPI = {
  // User registration
  register: async (userData) => {
    return apiCall('/auth/register', {
      method: 'POST',
      body: JSON.stringify(userData),
    });
  },

  // User login
  login: async (credentials) => {
    return apiCall('/auth/login', {
      method: 'POST',
      body: JSON.stringify(credentials),
    });
  },

  // Send OTP for email verification
  sendOTP: async (email) => {
    return apiCall('/auth/send-otp', {
      method: 'POST',
      body: JSON.stringify({ email }),
    });
  },

  // Verify OTP
  verifyOTP: async (email, otp) => {
    return apiCall('/auth/verify-otp', {
      method: 'POST',
      body: JSON.stringify({ email, otp }),
    });
  },

  // Forgot password - send recovery OTP
  forgotPassword: async (email) => {
    return apiCall('/auth/forgot-password', {
      method: 'POST',
      body: JSON.stringify({ email }),
    });
  },

  // Reset password with OTP
  resetPassword: async (email, otp, newPassword) => {
    return apiCall('/auth/reset-password', {
      method: 'POST',
      body: JSON.stringify({ email, otp, new_password: newPassword }),
    });
  },
};

// Properties API calls
export const propertiesAPI = {
  // Get all properties
  getAll: async () => {
    return apiCall('/properties');
  },

  // Get property by ID
  getById: async (id) => {
    return apiCall(`/properties/${id}`);
  },

  // Search properties (you can add filters later)
  search: async (filters = {}) => {
    const queryParams = new URLSearchParams(filters).toString();
    return apiCall(`/properties/search?${queryParams}`);
  },
};

// FAQ API calls
export const faqAPI = {
  // Get all FAQ entries
  getAll: async () => {
    return apiCall('/faq');
  },
};

// Content API calls
export const contentAPI = {
  // Get content sections
  getSections: async () => {
    return apiCall('/content/sections');
  },
};

// Price Prediction API calls
export const predictionAPI = {
  // Create price prediction
  create: async (predictionData) => {
    return apiCall('/predictions', {
      method: 'POST',
      body: JSON.stringify(predictionData),
    });
  },

  // Get user predictions
  getUserPredictions: async (userId) => {
    return apiCall(`/predictions/user/${userId}`);
  },
};

// Export the main API object
export default {
  auth: authAPI,
  properties: propertiesAPI,
  faq: faqAPI,
  content: contentAPI,
  prediction: predictionAPI,
};
