import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Header from '../sharedpages/header';
import Navbar from '../sharedpages/navbar';
import Footer from '../sharedpages/footer';
import './EditStep.css';

const EditStep = () => {
  const { stepId } = useParams();
  const [activeTab, setActiveTab] = useState('content');
  const [formData, setFormData] = useState({
    stepNumber: '',
    title: '',
    description: '',
    image: null,
    imagePreview: null
  });

  // Mock data for steps - in a real app, this would come from an API
  const mockSteps = [
    {
      id: 1,
      title: 'Find Your Space',
      description: 'Description for step 1',
      image: '/step1-image.jpg',
      stepNumber: 1
    },
    {
      id: 2,
      title: 'Connect with Brokers',
      description: 'Description for step 2',
      image: '/step2-image.jpg',
      stepNumber: 2
    },
    {
      id: 3,
      title: 'Secure Your Lease',
      description: 'Description for step 3',
      image: '/step3-image.jpg',
      stepNumber: 3
    }
  ];

  useEffect(() => {
    // Find the step data based on stepId
    const step = mockSteps.find(s => s.id === parseInt(stepId));
    if (step) {
      setFormData({
        stepNumber: step.stepNumber.toString(),
        title: step.title,
        description: step.description,
        image: null,
        imagePreview: step.image
      });
    }
  }, [stepId]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData(prev => ({
        ...prev,
        image: file
      }));

      const reader = new FileReader();
      reader.onload = (e) => {
        setFormData(prev => ({
          ...prev,
          imagePreview: e.target.result
        }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSaveStep = () => {
    console.log('Saving step changes:', formData);
    // Add save logic here
    alert('Step updated successfully!');
    // Navigate back to edit how it works
    window.location.href = '/dashboard/edit-how-it-works';
  };

  const handleCancel = () => {
    console.log('Canceling step changes');
    // Navigate back to edit how it works
    window.location.href = '/dashboard/edit-how-it-works';
  };

  const handleBack = () => {
    // Navigate back to edit how it works
    window.location.href = '/dashboard/edit-how-it-works';
  };

  return (
    <div className="edit-step-page">
      <Header />
      <div className="dashboard-container">
        <Navbar activeTab={activeTab} setActiveTab={setActiveTab} />
        <main className="main-content">
          {/* Breadcrumb */}
          <div className="breadcrumb">
            Content Management / How It Works Section / Edit Step {stepId}
          </div>

          {/* Page Title and Back Button */}
          <div className="page-header">
            <h1 className="page-title">Edit Step {stepId}</h1>
            <button 
              className="back-btn"
              onClick={handleBack}
            >
              Back
            </button>
          </div>

          {/* Form */}
          <div className="edit-form">
            <div className="form-group">
              <label htmlFor="stepNumber" className="form-label">Step Number</label>
              <input
                type="number"
                id="stepNumber"
                name="stepNumber"
                value={formData.stepNumber}
                onChange={handleInputChange}
                className="form-input"
                placeholder="Enter step number"
                min="1"
              />
            </div>

            <div className="form-group">
              <label htmlFor="title" className="form-label">Step Title</label>
              <input
                type="text"
                id="title"
                name="title"
                value={formData.title}
                onChange={handleInputChange}
                className="form-input"
                placeholder="Enter step title"
              />
            </div>

            <div className="form-group">
              <label htmlFor="description" className="form-label">Step Description</label>
              <textarea
                id="description"
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                className="form-textarea"
                placeholder="Enter step description"
                rows="4"
              />
            </div>

            <div className="form-group">
              <label htmlFor="image" className="form-label">Step Image</label>
              <div className="image-upload-section">
                {formData.imagePreview && (
                  <div className="image-preview">
                    <img
                      src={formData.imagePreview}
                      alt="Step preview"
                      className="preview-image"
                    />
                  </div>
                )}
                <input
                  type="file"
                  id="image"
                  name="image"
                  onChange={handleImageUpload}
                  className="file-input"
                  accept="image/*"
                />
                <label htmlFor="image" className="upload-btn">
                  {formData.imagePreview ? 'Change Image' : 'Upload Image'}
                </label>
              </div>
            </div>

            {/* Action Buttons */}
            <div className="action-buttons">
              <button
                type="button"
                className="cancel-btn"
                onClick={handleCancel}
              >
                Cancel
              </button>
              <button
                type="button"
                className="save-btn"
                onClick={handleSaveStep}
              >
                Save Changes
              </button>
            </div>
          </div>
        </main>
      </div>
      <Footer />
    </div>
  );
};

export default EditStep;
