import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { GOOGLE_MAPS_API_KEY } from '../../config/maps';
import Header from '../sharedpages/header';
import Navbar from '../sharedpages/navbar';
import Footer from '../sharedpages/footer';
import './compareprediction.css';

const GOOGLE_MAPS_LIBRARIES = ['places', 'geometry'];

function loadGoogleMaps(apiKey) {
  if (!apiKey) {
    return Promise.reject(new Error('Missing Google Maps API key (set in src/config/maps.js or .env)'));
  }
  if (window.google && window.google.maps) {
    return Promise.resolve(window.google);
  }
  if (window._googleMapsPromise) {
    return window._googleMapsPromise;
  }
  const script = document.createElement('script');
  const libs = GOOGLE_MAPS_LIBRARIES.join(',');
  script.src = `https://maps.googleapis.com/maps/api/js?key=${apiKey}&libraries=${libs}`;
  script.async = true;
  script.defer = true;
  window._googleMapsPromise = new Promise((resolve, reject) => {
    script.onload = () => resolve(window.google);
    script.onerror = reject;
  });
  document.head.appendChild(script);
  return window._googleMapsPromise;
}

const DEFAULT_CENTER = { lat: 1.2868108, lng: 103.8545349 }; // SG center-ish

const ComparePrediction = () => {
  const [activeTab, setActiveTab] = useState('compare-predictions');
  const [mapInstance, setMapInstance] = useState(null);
  const [markers, setMarkers] = useState([]);
  const [addedProperties, setAddedProperties] = useState([]);
  
  // Form state - matching priceprediction.js structure
  const [formData, setFormData] = useState({
    propertyType: '',
    address: '',
    floorArea: '',
    level: '',
    unit: ''
  });
  const [searchAddress, setSearchAddress] = useState('');
  const [clickedAddress, setClickedAddress] = useState('');
  const [isSearching, setIsSearching] = useState(false);
  const [searchError, setSearchError] = useState('');
  const [autocompleteSuggestions, setAutocompleteSuggestions] = useState([]);
  const [showAutocomplete, setShowAutocomplete] = useState(false);
  const [selectedSuggestionIndex, setSelectedSuggestionIndex] = useState(-1);
  
  const navigate = useNavigate();
  const mapRef = useRef(null);
  const markerRef = useRef(null);
  const mapInstanceRef = useRef(null);
  const geocoderRef = useRef(null);
  const inputRef = useRef(null);
  const lastLocationRef = useRef(null);

  // Get user type from localStorage
  const userType = localStorage.getItem('userType') || 'free';

  // Property types and levels matching priceprediction.js
  const propertyTypes = [
    'Industrial Property',
    'Commercial Space',
    'Warehouse',
    'Office Space',
    'Retail Unit',
    'Factory Building'
  ];

  const levels = [
    'Ground Floor',
    'Level 1',
    'Level 2',
    'Level 3',
    'Level 4',
    'Level 5+'
  ];

  // Initialize Google Maps
  useEffect(() => {
    const initMap = async () => {
      try {
        await loadGoogleMaps(GOOGLE_MAPS_API_KEY);
        
        if (mapRef.current && !mapInstance) {
          const map = new window.google.maps.Map(mapRef.current, {
            center: DEFAULT_CENTER,
            zoom: 11,
            mapTypeId: window.google.maps.MapTypeId.ROADMAP,
            styles: [
              {
                featureType: 'poi',
                elementType: 'labels',
                stylers: [{ visibility: 'off' }]
              }
            ]
          });
          
          setMapInstance(map);
          mapInstanceRef.current = map;
          
          // Add click listener to map
          map.addListener('click', (event) => {
            const latLng = event.latLng;
            placeMarker(latLng);
            reverseGeocode(latLng);
          });
        }
      } catch (error) {
        console.error('Error loading Google Maps:', error);
      }
    };

    initMap();
  }, [mapInstance]);

  // Handle clicking outside autocomplete dropdown
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (showAutocomplete && !event.target.closest('.address-search-container')) {
        setShowAutocomplete(false);
        setSelectedSuggestionIndex(-1);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [showAutocomplete]);

  const placeMarker = (latLng) => {
    if (markerRef.current) {
      markerRef.current.setMap(null);
    }
    
    markerRef.current = new window.google.maps.Marker({
      position: latLng,
      map: mapInstanceRef.current,
      animation: window.google.maps.Animation.DROP
    });
  };

  const reverseGeocode = (latLng) => {
    const geocoder = geocoderRef.current || new window.google.maps.Geocoder();
    geocoderRef.current = geocoder;
    
    geocoder.geocode({ location: latLng }, (results, status) => {
      if (status === 'OK' && results && results.length) {
        const address = results[0].formatted_address;
        setClickedAddress(address);
        setSearchAddress(address);
        setFormData(prev => ({ ...prev, address: address }));
      }
    });
  };

  const handleSearchAddress = () => {
    if (!searchAddress.trim()) return;
    setIsSearching(true);
    try {
      const geocoder = geocoderRef.current || new window.google.maps.Geocoder();
      geocoderRef.current = geocoder;
      
      // Check if input is a postal code (6 digits)
      const isPostalCode = /^\d{6}$/.test(searchAddress.trim());
      const searchQuery = isPostalCode ? `${searchAddress}, Singapore` : `${searchAddress}, Singapore`;
      
      geocoder.geocode({ address: searchQuery }, (results, status) => {
        if (status === 'OK' && results && results.length) {
          const result = results[0];
          const latLng = result.geometry.location;
          
          mapInstanceRef.current.panTo(latLng);
          mapInstanceRef.current.setZoom(18);
          placeMarker(latLng);
          const address = result.formatted_address;
          setClickedAddress(address);
          setSearchAddress(address);
          setFormData(prev => ({ ...prev, address: address }));
          
          // Show success message for postal code
          if (isPostalCode) {
            console.log(`âœ… Found address for postal code ${searchAddress}: ${address}`);
          }
        } else {
          if (isPostalCode) {
            setSearchError('No results found for this postal code. Please check the postal code.');
          } else {
            setSearchError('No results found for this address.');
          }
        }
        setIsSearching(false);
      });
    } catch (err) {
      setSearchError('Error searching address.');
      setIsSearching(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearchAddress();
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleAddressInputChange = (e) => {
    const value = e.target.value;
    setSearchAddress(value);
    setFormData(prev => ({ ...prev, address: value }));
    
    if (value === '') {
      setSearchError('');
      setClickedAddress('');
      setAutocompleteSuggestions([]);
      setShowAutocomplete(false);
      setSelectedSuggestionIndex(-1);
      if (markerRef.current) {
        markerRef.current.setMap(null);
        markerRef.current = null;
      }
      return;
    }

    // Get autocomplete suggestions
    if (window.google && window.google.maps && value.length > 2) {
      const autocompleteService = new window.google.maps.places.AutocompleteService();
      const geocoder = new window.google.maps.Geocoder();
      
      autocompleteService.getPlacePredictions({
        input: value + ', Singapore',
        componentRestrictions: { country: 'sg' },
        types: ['address', 'establishment']
      }, (predictions, status) => {
        if (status === window.google.maps.places.PlacesServiceStatus.OK && predictions) {
          setAutocompleteSuggestions(predictions);
          setShowAutocomplete(true);
          setSelectedSuggestionIndex(-1);
        } else {
          setAutocompleteSuggestions([]);
          setShowAutocomplete(false);
        }
      });
    } else {
      setAutocompleteSuggestions([]);
      setShowAutocomplete(false);
    }
  };

  const handleSuggestionClick = (suggestion) => {
    setSearchAddress(suggestion.description);
    setFormData(prev => ({ ...prev, address: suggestion.description }));
    setShowAutocomplete(false);
    setAutocompleteSuggestions([]);
    setSelectedSuggestionIndex(-1);
    
    // Geocode the selected suggestion
    const geocoder = new window.google.maps.Geocoder();
    geocoder.geocode({ placeId: suggestion.place_id }, (results, status) => {
      if (status === 'OK' && results && results.length) {
        const latLng = results[0].geometry.location;
        mapInstanceRef.current.panTo(latLng);
        mapInstanceRef.current.setZoom(18);
        placeMarker(latLng);
        setClickedAddress(suggestion.description);
        setSearchError('');
      }
    });
  };

  const handleKeyDown = (e) => {
    if (!showAutocomplete) return;
    
    if (e.key === 'ArrowDown') {
      e.preventDefault();
      setSelectedSuggestionIndex(prev => 
        prev < autocompleteSuggestions.length - 1 ? prev + 1 : prev
      );
    } else if (e.key === 'ArrowUp') {
      e.preventDefault();
      setSelectedSuggestionIndex(prev => prev > 0 ? prev - 1 : -1);
    } else if (e.key === 'Enter') {
      e.preventDefault();
      if (selectedSuggestionIndex >= 0 && autocompleteSuggestions[selectedSuggestionIndex]) {
        handleSuggestionClick(autocompleteSuggestions[selectedSuggestionIndex]);
      } else {
        handleSearchAddress();
      }
    } else if (e.key === 'Escape') {
      setShowAutocomplete(false);
      setSelectedSuggestionIndex(-1);
    }
  };

  const handleAddProperty = () => {
    if (!formData.propertyType || !formData.address || !formData.floorArea || !formData.level) {
      alert('Please fill in all required fields');
      return;
    }

    if (addedProperties.length >= 2) {
      alert('You can only compare up to 2 properties');
      return;
    }

    const newProperty = {
      id: Date.now(),
      propertyType: formData.propertyType,
      address: formData.address,
      floorArea: formData.floorArea,
      level: formData.level,
      unit: formData.unit || 'N/A'
    };

    setAddedProperties(prev => [...prev, newProperty]);
    
    // Clear form
    setFormData({
      propertyType: '',
      address: '',
      floorArea: '',
      level: '',
      unit: ''
    });
    setSearchAddress('');
    setClickedAddress('');
    setSearchError('');
    
    // Clear marker
    if (markerRef.current) {
      markerRef.current.setMap(null);
      markerRef.current = null;
    }
  };

  const handleRemoveProperty = (propertyId) => {
    setAddedProperties(prev => prev.filter(prop => prop.id !== propertyId));
  };

  const handleCompare = () => {
    if (addedProperties.length !== 2) {
      alert('Please add exactly 2 properties to compare');
      return;
    }
    
    // Navigate to comparison results page
    navigate('/dashboard/compare-results', { 
      state: { properties: addedProperties } 
    });
  };

  return (
    <div className="compare-prediction-dashboard">
      <Header />
      <div className="dashboard-content">
        <Navbar activeTab={activeTab} setActiveTab={setActiveTab} />
        <div className="main-content">
          <div className="compare-prediction-container">
            <h1 className="page-title">Compare Predictions</h1>
            
            <div className="select-properties-section">
              <h2 className="section-title">Select Properties (Max 2)</h2>
              
              <form className="search-form">
                <div className="form-group">
                  <label htmlFor="propertyType">Property Type</label>
                  <select
                    id="propertyType"
                    name="propertyType"
                    value={formData.propertyType}
                    onChange={handleInputChange}
                    required
                  >
                    <option value="">Select property type</option>
                    {propertyTypes.map((type, index) => (
                      <option key={index} value={type}>{type}</option>
                    ))}
                  </select>
                </div>

                <div className="form-group">
                  <label htmlFor="address">Address or Postal Code</label>
                  <div className="address-search-container">
                    <input
                      ref={inputRef}
                      type="text"
                      id="address"
                      name="address"
                      value={searchAddress}
                      onChange={handleAddressInputChange}
                      onKeyDown={handleKeyDown}
                      placeholder="Search for address or enter 6-digit postal code"
                      disabled={isSearching}
                      required
                    />
                    <button 
                      type="button"
                      className="address-search-btn" 
                      onClick={handleSearchAddress}
                      disabled={isSearching || !searchAddress.trim()}
                    >
                      {isSearching ? 'Searching...' : 'Search'}
                    </button>
                  </div>
                  {showAutocomplete && autocompleteSuggestions.length > 0 && (
                    <div className="autocomplete-dropdown">
                      {autocompleteSuggestions.map((suggestion, index) => (
                        <div
                          key={suggestion.place_id}
                          className={`autocomplete-item ${index === selectedSuggestionIndex ? 'selected' : ''}`}
                          onClick={() => handleSuggestionClick(suggestion)}
                        >
                          <span className="suggestion-text">{suggestion.description}</span>
                        </div>
                      ))}
                    </div>
                  )}
                  {searchError && (
                    <div className="search-error">{searchError}</div>
                  )}
                  <div className="search-hint">
                    <small>ðŸ’¡ You can search by address or enter a 6-digit Singapore postal code</small>
                  </div>
                </div>

                <div className="map-snippet">
                  <div ref={mapRef} className="map-container"></div>
                  <div className="map-instructions">
                    <p>ðŸ§­ <strong>Location Selection</strong></p>
                    <p>Click the map or use search to pick a location.</p>
                    <p><small>You can search by address or postal code. The selected address will be automatically filled in the form above.</small></p>
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-group">
                    <label htmlFor="floorArea">Floor Area (sq ft)</label>
                    <input
                      type="number"
                      id="floorArea"
                      name="floorArea"
                      value={formData.floorArea}
                      onChange={handleInputChange}
                      placeholder="Enter floor area"
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label htmlFor="level">Level</label>
                    <select
                      id="level"
                      name="level"
                      value={formData.level}
                      onChange={handleInputChange}
                      required
                    >
                      <option value="">Select level</option>
                      {levels.map((level, index) => (
                        <option key={index} value={level}>{level}</option>
                      ))}
                    </select>
                  </div>

                  <div className="form-group">
                    <label htmlFor="unit">Unit</label>
                    <input
                      type="text"
                      id="unit"
                      name="unit"
                      value={formData.unit}
                      onChange={handleInputChange}
                      placeholder="Enter unit number"
                      required
                    />
                  </div>
                </div>

                <button 
                  type="button"
                  className="add-property-btn"
                  onClick={handleAddProperty}
                  disabled={addedProperties.length >= 2}
                >
                  Add Property ({addedProperties.length}/2)
                </button>
              </form>
              
              {addedProperties.length > 0 && (
                <div className="added-properties">
                  <h3>Added Properties:</h3>
                  {addedProperties.map((property, index) => (
                    <div key={property.id} className="property-item">
                      <span className="property-number">Property {index + 1}:</span>
                      <span className="property-details">
                        {property.propertyType} - {property.address} ({property.floorArea} sq ft, {property.level})
                      </span>
                      <button 
                        className="remove-property-btn"
                        onClick={() => handleRemoveProperty(property.id)}
                      >
                        Remove
                      </button>
                    </div>
                  ))}
                </div>
              )}
            </div>

            <div className="compare-section">
              <button 
                className="compare-btn"
                onClick={handleCompare}
                disabled={addedProperties.length !== 2}
              >
                Compare Properties
              </button>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default ComparePrediction;
