import React from 'react';
import './SubscriptionPlans.css';

const plans = [
  {
    name: "Basic",
    price: "FREE",
    icon: "https://cdn-icons-png.flaticon.com/512/3135/3135712.png", // user/agent-like icon for basic
    description: "Basic listing submission, active for 30 days",
    features: ["All Operating Supported", "10 Property Listings", "30 Days Availability", "Feature Properties", "24/7 Full support"]
  },
  {
    name: "Premium",
    price: "$49.00",
    icon: "https://cdn-icons-png.flaticon.com/512/3135/3135713.png", // briefcase/business icon for premium
    description: "Business listing submission, active for 90 days",
    features: ["All Operating Supported", "30 Property Listings", "90 Days Availability", "Feature Properties", "24/7 Full support"],
    featured: true // Only the Business plan is featured
  },
  {
    name: "Agent",
    price: "$99.00",
    icon: "https://cdn-icons-png.flaticon.com/512/3135/3135715.png", // professional agent icon
    description: "Professional listing submission, active for 60 days",
    features: ["All Operating Supported", "20 Property Listings", "60 Days Availability", "Feature Properties", "24/7 Full support"]
  }
];


const SubscriptionPlans = () => {
  return (
    <div className="landing-page">
      <section className="subscription-plans" id="plans">
        <div className="plans-container">
          <h2 className="plans-title">Find the Right Plan for You</h2>
          <p className="plans-subtitle">
            From free basic valuations to premium reports with in-depth analytics, we have you covered.
          </p>

          <div className="plans-grid">
            {plans.map((plan, index) => (
              <div
                key={index}
                className={`plan-card ${plan.featured ? 'highlighted' : ''}`}
              >
                <div className="plan-header">
                  <h3 className="plan-name">{plan.name}</h3>
                  <p className="plan-price">{plan.price}</p>
                  <img src={plan.icon} alt={plan.name} className="plan-icon" />
                  <p className="plan-description">{plan.description}</p>
                </div>
                <ul className="plan-features">
                  {plan.features.map((feature, idx) => (
                    <li key={idx}><span className="checkmark">âœ“</span>{feature}</li>
                  ))}
                </ul>
                <button className="plan-button">Add to cart</button>
              </div>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default SubscriptionPlans;
