// Centralized property type configuration
// Used across FirstTimer, PropertyListings, and other components

export const PROPERTY_TYPES = [
  { id: 'Office', name: 'Office', icon: 'ðŸ¢', category: 'Commercial' },
  { id: 'Retail', name: 'Retail Space', icon: 'ðŸª', category: 'Commercial' },
  { id: 'Shop House', name: 'Shop House', icon: 'ðŸ˜ï¸', category: 'Commercial' },
  { id: 'Business Parks', name: 'Business Parks', icon: 'ðŸ¢', category: 'Industrial Property' },
  { id: 'Single-user Factory', name: 'Single-user Factory', icon: 'ðŸ­', category: 'Industrial Property' },
  { id: 'Multiple-user Factory', name: 'Multiple-user Factory', icon: 'ðŸ­', category: 'Industrial Property' },
  { id: 'Warehouse', name: 'Warehouse', icon: 'ðŸ­', category: 'Industrial Property' }
];

export const PROPERTY_CATEGORIES = [
  {
    label: 'Commercial',
    types: ['Office', 'Retail', 'Shop House']
  },
  {
    label: 'Industrial Property',
    types: ['Business Parks', 'Single-user Factory', 'Multiple-user Factory', 'Warehouse']
  }
];

// Get property type by ID
export const getPropertyTypeById = (id) => {
  return PROPERTY_TYPES.find(type => type.id === id);
};

// Get property types by category
export const getPropertyTypesByCategory = (category) => {
  return PROPERTY_TYPES.filter(type => type.category === category);
};

// Get all property type IDs
export const getPropertyTypeIds = () => {
  return PROPERTY_TYPES.map(type => type.id);
};

// Get property type names
export const getPropertyTypeNames = () => {
  return PROPERTY_TYPES.map(type => type.name);
};
