import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import User from './user/user';
import AgentUser from './agent/AgentUser';
import SysAdmin from './system/SysAdmin';

const Dashboard = () => {
  const navigate = useNavigate();

  useEffect(() => {
    // Check if user is logged in
    const isLoggedIn = localStorage.getItem('isLoggedIn');
    if (!isLoggedIn) {
      navigate('/login');
      return;
    }

    // Get user type
    const userType = localStorage.getItem('userType') || 'free';

    // Only check first-time status for Free and Premium users
    if (userType === 'free' || userType === 'premium') {
      // Check if user has completed first-time setup
      const userPreferences = localStorage.getItem('userPreferences');
      const isFirstTimeUser = !userPreferences || !JSON.parse(userPreferences).completed;

      if (isFirstTimeUser) {
        navigate('/first-time');
        return;
      }
    }
    // SysAdmin and Agent users proceed directly to their dashboards
  }, [navigate]);

  // Get user type from localStorage
  const userType = localStorage.getItem('userType') || 'free';

  // Render appropriate dashboard based on user type
  switch (userType) {
    case 'agent':
      return <AgentUser />;
    case 'sysadmin':
      return <SysAdmin />;
    case 'free':
    case 'premium':
    default:
      return <User />;
  }
};

export default Dashboard;
