import React, { useState } from 'react';
import './SignUp.css';

const SignUp = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    password: '',
    confirmPassword: '',
    userType: 'free',
    termsAccepted: false,
    // Agent specific fields
    ceaNumber: '',
    company: '',
    workNumber: '',
    companyEmail: '',
    ceaLicense: null
  });
  const [errors, setErrors] = useState({});
  const [otp, setOtp] = useState(['', '', '', '', '']);
  const [isEmailVerified, setIsEmailVerified] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleOtpChange = (index, value) => {
    if (value.length <= 1 && /^\d*$/.test(value)) {
      const newOtp = [...otp];
      newOtp[index] = value;
      setOtp(newOtp);

      // Auto-focus next input
      if (value && index < 4) {
        const nextInput = document.getElementById(`otp-${index + 1}`);
        if (nextInput) nextInput.focus();
      }
    }
  };

  const handleKeyDown = (index, e) => {
    if (e.key === 'Backspace' && !otp[index] && index > 0) {
      const prevInput = document.getElementById(`otp-${index - 1}`);
      if (prevInput) prevInput.focus();
    }
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData(prev => ({
        ...prev,
        ceaLicense: file
      }));
    }
  };

  const validateStep = (step) => {
    const newErrors = {};

    switch (step) {
      case 1: // Name and Email
        if (!formData.fullName) {
          newErrors.fullName = 'Full name is required';
        }
        if (!formData.email) {
          newErrors.email = 'Email is required';
        } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
          newErrors.email = 'Email is invalid';
        }
        break;

      case 2: // Email Verification
        const otpString = otp.join('');
        if (otpString.length !== 5) {
          newErrors.otp = 'Please enter the complete OTP';
        }
        break;

      case 3: // Password
        if (!formData.password) {
          newErrors.password = 'Password is required';
        } else if (formData.password.length < 8) {
          newErrors.password = 'Password must be at least 8 characters';
        }
        if (!formData.confirmPassword) {
          newErrors.confirmPassword = 'Please confirm your password';
        } else if (formData.password !== formData.confirmPassword) {
          newErrors.confirmPassword = 'Passwords do not match';
        }
        break;

      case 4: // Terms
        if (!formData.termsAccepted) {
          newErrors.terms = 'You must accept the terms and conditions';
        }
        break;

      case 5: // Subscription
        if (!formData.userType) {
          newErrors.userType = 'Please select a subscription type';
        }
        break;

      case 6: // Agent Details (only if agent selected)
        if (formData.userType === 'agent') {
          if (!formData.ceaNumber) {
            newErrors.ceaNumber = 'CEA number is required';
          }
          if (!formData.company) {
            newErrors.company = 'Company name is required';
          }
          if (!formData.workNumber) {
            newErrors.workNumber = 'Work number is required';
          }
          if (!formData.companyEmail) {
            newErrors.companyEmail = 'Company email is required';
          } else if (!/\S+@\S+\.\S+/.test(formData.companyEmail)) {
            newErrors.companyEmail = 'Company email is invalid';
          }
          if (!formData.ceaLicense) {
            newErrors.ceaLicense = 'CEA license upload is required';
          }
        }
        break;
    }

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return false;
    }
    return true;
  };

  const nextStep = () => {
    if (validateStep(currentStep)) {
      if (currentStep === 2) {
        setIsEmailVerified(true);
      }

      // Handle different subscription flows
      if (currentStep === 5) {
        // Subscription selection step
        if (formData.userType === 'free') {
          // Free users: complete signup and redirect to login
          console.log('Free user signup complete:', formData);
          // Redirect to login page
          window.location.href = '/login';
          return;
        } else if (formData.userType === 'premium') {
          // Premium users: go to payment step
          setCurrentStep(7);
          setErrors({});
          return;
        } else if (formData.userType === 'agent') {
          // Agent users: go to agent details step
          setCurrentStep(6);
          setErrors({});
          return;
        }
      }

      // For agent users, after agent details, go to payment
      if (currentStep === 6 && formData.userType === 'agent') {
        setCurrentStep(7);
        setErrors({});
        return;
      }

      // For premium users, after payment, redirect to login
      if (currentStep === 7 && formData.userType === 'premium') {
        console.log('Premium user signup complete:', formData);
        // Redirect to login page
        window.location.href = '/login';
        return;
      }

      // For agent users, after payment, redirect to login
      if (currentStep === 7 && formData.userType === 'agent') {
        console.log('Agent user signup complete:', formData);
        // Redirect to login page
        window.location.href = '/login';
        return;
      }

      // Default: move to next step
      setCurrentStep(prev => prev + 1);
      setErrors({});
    }
  };

  const prevStep = () => {
    // Handle special cases for different user types
    if (currentStep === 7 && formData.userType === 'premium') {
      // Premium users: go from payment back to subscription selection
      setCurrentStep(5);
    } else if (currentStep === 7 && formData.userType === 'agent') {
      // Agent users: go from payment back to agent details
      setCurrentStep(6);
    } else if (currentStep === 6 && formData.userType === 'agent') {
      // Agent users: go from agent details back to subscription selection
      setCurrentStep(5);
    } else {
      // Default: go back one step
      setCurrentStep(prev => prev - 1);
    }
    setErrors({});
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateStep(currentStep)) {
      // Handle final submission
      console.log('Final signup data:', formData);

      // If agent or premium, redirect to payment
      if (formData.userType === 'agent' || formData.userType === 'premium') {
        // Redirect to payment page
        console.log('Redirecting to payment...');
      } else {
        // Free user - complete signup
        console.log('Free user signup complete');
      }
    }
  };

  const renderStep1 = () => (
    <div className="step-content">
      <p className="section-subtitle">Get started with your account</p>

      <div className="form-group">
        <label htmlFor="fullName">Full Name</label>
        <div className="input-container">
          <span className="input-icon"></span>
          <input
            type="text"
            id="fullName"
            name="fullName"
            value={formData.fullName}
            onChange={handleChange}
            className={errors.fullName ? 'error' : ''}
            placeholder="Enter full name"
          />
        </div>
        {errors.fullName && <span className="error-message">{errors.fullName}</span>}
      </div>

      <div className="form-group">
        <label htmlFor="email">Email</label>
        <div className="input-container">
          <span className="input-icon"></span>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className={errors.email ? 'error' : ''}
            placeholder="Enter email address"
          />
        </div>
        {errors.email && <span className="error-message">{errors.email}</span>}
      </div>

      <button type="button" className="next-btn" onClick={nextStep}>
        Next
      </button>
    </div>
  );

  const renderStep2 = () => (
    <div className="step-content">
      <p className="section-subtitle">Enter OTP sent to {formData.email}</p>

      <div className="otp-container">
        {otp.map((digit, index) => (
          <input
            key={index}
            id={`otp-${index}`}
            type="text"
            maxLength="1"
            value={digit}
            onChange={(e) => handleOtpChange(index, e.target.value)}
            onKeyDown={(e) => handleKeyDown(index, e)}
            className="otp-input"
            placeholder=""
          />
        ))}
      </div>

      {errors.otp && <span className="error-message">{errors.otp}</span>}

      <div className="otp-instructions">
        <span className="instruction-text">Enter code sent to your email</span>
        <button type="button" className="resend-link">Resend code</button>
      </div>

      <div className="step-buttons">
        <button type="button" className="btn-secondary" onClick={prevStep}>
          Back
        </button>
        <button type="button" className="next-btn" onClick={nextStep}>
          Verify & Continue
        </button>
      </div>
    </div>
  );

  const renderStep3 = () => (
    <div className="step-content">
      <p className="section-subtitle">Secure your account with a strong password</p>

      <div className="form-group">
        <label htmlFor="password">Password</label>
        <input
          type="password"
          id="password"
          name="password"
          value={formData.password}
          onChange={handleChange}
          className={errors.password ? 'error' : ''}
          placeholder="Create a password (min 8 characters)"
        />
        {errors.password && <span className="error-message">{errors.password}</span>}
      </div>

      <div className="form-group">
        <label htmlFor="confirmPassword">Confirm Password</label>
        <input
          type="password"
          id="confirmPassword"
          name="confirmPassword"
          value={formData.confirmPassword}
          onChange={handleChange}
          className={errors.confirmPassword ? 'error' : ''}
          placeholder="Confirm your password"
        />
        {errors.confirmPassword && <span className="error-message">{errors.confirmPassword}</span>}
      </div>

      <div className="step-buttons">
        <button type="button" className="btn-secondary" onClick={prevStep}>
          Back
        </button>
        <button type="button" className="next-btn" onClick={nextStep}>
          Continue
        </button>
      </div>
    </div>
  );

  const renderStep4 = () => (
    <div className="step-content">
      <p className="section-subtitle">Please read and accept our terms</p>

      <div className="terms-content">
        <p>By accepting these terms, you agree to our service conditions and privacy policy.</p>
      </div>

      <div className="form-group">
        <label className="checkbox-label">
          <input
            type="checkbox"
            name="terms"
            checked={formData.termsAccepted}
            onChange={(e) => setFormData(prev => ({ ...prev, termsAccepted: e.target.checked }))}
          />
          <span className="checkmark"></span>
          I have read and agree to the Terms of Service and Privacy Policy
        </label>
        {errors.terms && <span className="error-message">{errors.terms}</span>}
      </div>

      <div className="step-buttons">
        <button type="button" className="btn-secondary" onClick={prevStep}>
          Back
        </button>
        <button type="button" className="next-btn" onClick={nextStep}>
          Accept & Continue
        </button>
      </div>
    </div>
  );

  const renderStep5 = () => (
    <div className="step-content">
      <p className="section-subtitle">Select the plan that best fits your needs</p>

      <div className="subscription-options">
        <div 
          className={`subscription-option ${formData.userType === 'free' ? 'selected' : ''}`}
          onClick={() => setFormData(prev => ({ ...prev, userType: 'free' }))}
        >
          <input
            type="radio"
            id="free"
            name="userType"
            value="free"
            checked={formData.userType === 'free'}
            onChange={handleChange}
          />
          <label htmlFor="free">
            <h3>Free</h3>
            <p>Basic property valuations</p>
            <span className="price">$0/month</span>
          </label>
        </div>

        <div 
          className={`subscription-option ${formData.userType === 'premium' ? 'selected' : ''}`}
          onClick={() => setFormData(prev => ({ ...prev, userType: 'premium' }))}
        >
          <input
            type="radio"
            id="premium"
            name="userType"
            value="premium"
            checked={formData.userType === 'premium'}
            onChange={handleChange}
          />
          <label htmlFor="premium">
            <h3>Premium</h3>
            <p>Advanced analytics & reports</p>
            <span className="price">$29/month</span>
          </label>
        </div>

        <div 
          className={`subscription-option ${formData.userType === 'agent' ? 'selected' : ''}`}
          onClick={() => setFormData(prev => ({ ...prev, userType: 'agent' }))}
        >
          <input
            type="radio"
            id="agent"
            name="userType"
            value="agent"
            checked={formData.userType === 'agent'}
            onChange={handleChange}
          />
          <label htmlFor="agent">
            <h3>Agent</h3>
            <p>Professional tools & client management</p>
            <span className="price">$99/month</span>
          </label>
        </div>
      </div>

      {errors.userType && <span className="error-message">{errors.userType}</span>}

      <div className="step-buttons">
        <button type="button" className="btn-secondary" onClick={prevStep}>
          Back
        </button>
        <button type="button" className="next-btn" onClick={nextStep}>
          Continue
        </button>
      </div>
    </div>
  );

  const renderStep6 = () => (
    <div className="step-content">
      <p className="section-subtitle">Please provide your professional information</p>

      <div className="form-group">
        <label htmlFor="ceaNumber">CEA Number *</label>
        <input
          type="text"
          id="ceaNumber"
          name="ceaNumber"
          value={formData.ceaNumber}
          onChange={handleChange}
          className={errors.ceaNumber ? 'error' : ''}
          placeholder="Enter your CEA license number"
        />
        {errors.ceaNumber && <span className="error-message">{errors.ceaNumber}</span>}
      </div>

      <div className="form-group">
        <label htmlFor="company">Company Name *</label>
        <input
          type="text"
          id="company"
          name="company"
          value={formData.company}
          onChange={handleChange}
          className={errors.company ? 'error' : ''}
          placeholder="Enter your company name"
        />
        {errors.company && <span className="error-message">{errors.company}</span>}
      </div>

      <div className="form-group">
        <label htmlFor="workNumber">Work Phone Number *</label>
        <input
          type="tel"
          id="workNumber"
          name="workNumber"
          value={formData.workNumber}
          onChange={handleChange}
          className={errors.workNumber ? 'error' : ''}
          placeholder="Enter your work phone number"
        />
        {errors.workNumber && <span className="error-message">{errors.workNumber}</span>}
      </div>

      <div className="form-group">
        <label htmlFor="companyEmail">Company Email *</label>
        <input
          type="email"
          id="companyEmail"
          name="companyEmail"
          value={formData.companyEmail}
          onChange={handleChange}
          className={errors.companyEmail ? 'error' : ''}
          placeholder="Enter your company email"
        />
        {errors.companyEmail && <span className="error-message">{errors.companyEmail}</span>}
      </div>

      <div className="form-group">
        <label htmlFor="ceaLicense">CEA License Upload *</label>
        <div className="file-upload">
          <input
            type="file"
            id="ceaLicense"
            name="ceaLicense"
            accept=".pdf,.jpg,.jpeg,.png"
            onChange={handleFileUpload}
            className="file-input"
          />
          <label htmlFor="ceaLicense" className="file-label">
            {formData.ceaLicense ? formData.ceaLicense.name : 'Choose file (PDF, JPG, PNG)'}
          </label>
        </div>
        {errors.ceaLicense && <span className="error-message">{errors.ceaLicense}</span>}
      </div>

      <div className="step-buttons">
        <button type="button" className="btn-secondary" onClick={prevStep}>
          Back
        </button>
        <button type="button" className="next-btn" onClick={nextStep}>
          Continue
        </button>
      </div>
    </div>
  );

  const renderStep7 = () => (
    <div className="step-content">
      <p className="section-subtitle">Complete your subscription</p>

      <div className="payment-summary">
        <h3>Order Summary</h3>
        <div className="summary-item">
          <span>Plan:</span>
          <span>{formData.userType === 'premium' ? 'Premium' : 'Agent'} Subscription</span>
        </div>
        <div className="summary-item">
          <span>Price:</span>
          <span>${formData.userType === 'premium' ? '29' : '99'}/month</span>
        </div>
        <div className="summary-item total">
          <span>Total:</span>
          <span>${formData.userType === 'premium' ? '29' : '99'}/month</span>
        </div>
      </div>

      <div className="payment-form">
        <div className="form-group">
          <label htmlFor="cardNumber">Card Number</label>
          <input
            type="text"
            id="cardNumber"
            placeholder="1234 5678 9012 3456"
            className="card-input"
          />
        </div>

        <div className="card-row">
          <div className="form-group">
            <label htmlFor="expiry">Expiry Date</label>
            <input
              type="text"
              id="expiry"
              placeholder="MM/YY"
              className="card-input"
            />
          </div>
          <div className="form-group">
            <label htmlFor="cvv">CVV</label>
            <input
              type="text"
              id="cvv"
              placeholder="123"
              className="card-input"
            />
          </div>
        </div>

        <div className="form-group">
          <label htmlFor="cardName">Cardholder Name</label>
          <input
            type="text"
            id="cardName"
            placeholder="Enter cardholder name"
            className="card-input"
          />
        </div>
      </div>

      <div className="step-buttons">
        <button type="button" className="btn-secondary" onClick={prevStep}>
          Back
        </button>
        <button type="submit" className="next-btn">
          Complete Payment
        </button>
      </div>
    </div>
  );

  const renderCurrentStep = () => {
    switch (currentStep) {
      case 1: return renderStep1();
      case 2: return renderStep2();
      case 3: return renderStep3();
      case 4: return renderStep4();
      case 5: return renderStep5();
      case 6: return renderStep6();
      case 7: return renderStep7();
      default: return renderStep1();
    }
  };

  const getStepTitle = () => {
    switch (currentStep) {
      case 1: return 'Sign up';
      case 2: return 'Email Verification';
      case 3: return 'Create Password';
      case 4: return 'Terms & Conditions';
      case 5: return 'Choose Plan';
      case 6: return 'Agent Details';
      case 7: return 'Payment';
      default: return 'Sign up';
    }
  };

  const shouldShowPaymentStep = () => {
    return (formData.userType === 'agent' || formData.userType === 'premium') && currentStep === 7;
  };

  return (
    <div className="signup-page">
      <div className="signup-container">
        {/* Left Panel - Form */}
        <div className="form-panel">
          <div className="form-header">
            <h1 className="page-title">{getStepTitle()}</h1>
            <div className="login-link-top">
              Already have an account? <a href="/login">Log in</a>
            </div>
          </div>

          {/* Progress Bar */}
          <div className="progress-bar">
            <div className={`progress-step ${currentStep >= 1 ? 'active' : ''}`}>1</div>
            <div className={`progress-line ${currentStep >= 2 ? 'active' : ''}`}></div>
            <div className={`progress-step ${currentStep >= 2 ? 'active' : ''}`}>2</div>
            <div className={`progress-line ${currentStep >= 3 ? 'active' : ''}`}></div>
            <div className={`progress-step ${currentStep >= 3 ? 'active' : ''}`}>3</div>
            <div className={`progress-line ${currentStep >= 4 ? 'active' : ''}`}></div>
            <div className={`progress-step ${currentStep >= 4 ? 'active' : ''}`}>4</div>
            <div className={`progress-line ${currentStep >= 5 ? 'active' : ''}`}></div>
            <div className={`progress-step ${currentStep >= 5 ? 'active' : ''}`}>5</div>

            {/* Show step 6 only for agent users */}
            {formData.userType === 'agent' && (
              <>
                <div className={`progress-line ${currentStep >= 6 ? 'active' : ''}`}></div>
                <div className={`progress-step ${currentStep >= 6 ? 'active' : ''}`}>6</div>
              </>
            )}

            {/* Show step 7 for both premium and agent users */}
            {(formData.userType === 'premium' || formData.userType === 'agent') && (
              <>
                <div className={`progress-line ${currentStep >= 7 ? 'active' : ''}`}></div>
                <div className={`progress-step ${currentStep >= 7 ? 'active' : ''}`}>7</div>
              </>
            )}
          </div>

          <form className="signup-form" onSubmit={handleSubmit}>
            {renderCurrentStep()}
          </form>
        </div>

        {/* Right Panel - Cityscape Image */}
        <div className="visual-panel">
          <div className="cityscape-image">
            <div className="image-placeholder">
              <div className="cityscape-content">
                <h3>Singapore</h3>
                <p>Marina Bay Sands  Marina Bay</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SignUp;
